				RequiredAnyTextLines = { "PersephoneMeeting05_A", "PersephoneMeeting05_B" },
				RequiredFalseTextLinesLastRun = { "ZeusAboutPersephone01" },
				RequiredFalseTextLines = { "OlympianReunionQuestComplete" },
