				RequiredTrait = "ForceZeusBoonTrait",
				{ Cue = "/VO/Zeus_0205",
					StartSound = "/Leftovers/World Sounds/MapZoomInShort", UseEventEndSound = true,
					Text = "Ah, must be my gleaming Thunder Signet that I transferred to you! Why, I could feel its call from all the way up here!" },
			},

			ZeusLowHealth01 =
			{
