			ZeusRejection13 =
			{
				{ Cue = "/VO/Zeus_0140",
					PreLineFunctionName = "BoonInteractPresentation", PreLineWait = 1.0,
					StartSound = "/Leftovers/World Sounds/MapZoomInShort", UseEventEndSound = true,
