				{ Cue = "/VO/Athena_0056",
					StartSound = "/Leftovers/World Sounds/MapZoomInShort", UseEventEndSound = true,
					Text = "I'm satisfied we understand each other better, now, don't we. What do you say we carry on, and get you from this place?" },
