				{ Cue = "/VO/Athena_0058",
					StartSound = "/Leftovers/World Sounds/MapZoomInShort", UseEventEndSound = true,
					Text = "I'd say I've bickered with you quite enough for now, Cousin. Let us continue down a more productive path." },
