				HasTraitNameInRoom = "StationaryRiftTrait",
				RequiredFalseTextLines = { "OlympianReunionQuestComplete" },
				{ Cue = "/VO/Demeter_0143",
					StartSound = "/Leftovers/World Sounds/MapZoomInShort",
