					-- My pleasure.
					{ Cue = "/VO/ZagreusHome_2409" },
				},
				{ Cue = "/VO/Hades_0860",
					Text = "I know what you did. Stay out of Nyx's affairs next time, boy. She is needed here! Not wallowing in the primordial dark." },
				{ Cue = "/VO/ZagreusHome_2408", Portrait = "Portrait_Zag_Defiant_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Text = "You're just angry because some of us still want to have anything to do with their parents." },
				{ Cue = "/VO/Hades_0861",
					PreLineThreadedFunctionName = "PlayFistSlamAnimFromSource",
					Text = "Parents, {#DialogueItalicFormat}pah{#PreviousFormat}! You know nothing of what parents are capable of. Conspire all you like against my wishes, I care not. Get out." },
