			-- Hydra Ending (retired, Early Access)
			HadesRunCleared01 =
			{
				Skip = true,
				Priority = true,
				PlayOnce = true,
				RequiredTextLines = { "HadesFirstMeeting" },
				RequiredFalseTextLines = { "TheseusFirstAppearance_NotMetMinotaur", "TheseusFirstAppearance_MetMinotaur", "TheseusFirstAppearance_MetBeatMinotaur" },
				RequiredKillsThisRun = { "HydraHeadImmortal" },
				RequiredMaxCompletedRuns = 0,
				{ Cue = "/VO/Hades_0220",
					Text = "So you were able to dispatch my Hydra, boy. Right down to the last hateful head. It's an accomplishment I felt was certainly beyond your talent set. Though, here you are again, nevertheless." },
				{ Cue = "/VO/ZagreusHome_0401", Portrait = "Portrait_Zag_Defiant_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					Text = "You cheated me. There wasn't anywhere that I could go! I could have gone much farther. All the way to Mother!" },
				{ Cue = "/VO/Hades_0221",
					PreLineAnim = "ZagreusTalkDenialReturnToIdle", PreLineAnimTarget = "Hero",
					Text = "You shall do no such thing. Besides, if you were any help to me, you'd know vast portions of my realm are presently still under renovation. You won't be getting farther than you have." },
				{ Cue = "/VO/ZagreusHome_0402", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					Text = "I can wait. You can't just keep this place on lockdown for eternity. The mortals up there wouldn't stand for it." },
				{ Cue = "/VO/Hades_0222",
					PreLineAnim = "ZagreusTalkEmpathy_Return", PreLineAnimTarget = "Hero",
					Text = "They have no patience, yes. The same as you. Then wait, with all the rest of them. It matters not to me. For I have other work to do." },
			},
			HadesRunCleared01FollowUp =
			{
				Priority = true,
				PlayOnce = true,
				UseableOffSource = true,
				BlockStatusAnimations = true,
				RequiredTextLinesThisRun = "HadesRunCleared01",
				-- That's not a bad idea.
				EndCue = "/VO/ZagreusHome_0384",
				EndWait = 0.5,
				{ Cue = "/VO/Hades_0223",
					Text = "Now you begone from here, get yourself killed some more for all I care." },
			},
			-- Theseus & Minotaur Ending (retired, Early Access)
			HadesRunCleared02 =
			{
				Skip = true,
				Priority = true,
				PlayOnce = true,
				RequiredTextLines = { "HadesFirstMeeting" },
				RequiredFalseTextLines = { "LordHadesEncounter01", "LordHadesEncounter01B" },
				RequiredRoomThisRun = "C_Boss01",
				RequiredFalseSeenRoomThisRun = "D_Boss01",
				RequiredMaxCompletedRuns = 0,
				-- And then, we'll both find out.
				EndCue = "/VO/ZagreusHome_0864",
				EndWait = 0.55,
				{ Cue = "/VO/Hades_0396",
					Text = "Well, boy. Word has reached me that you were able to dethrone the Champion of Elysium as well as his bull-headed companion. Tell me, how did you accomplish such a thing?" },
				{ Cue = "/VO/ZagreusHome_0862", Portrait = "Portrait_Zag_Defiant_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Text = "First, you tell me when I can get out of there. The path guarded by Theseus was sealed! Is there even a surface? Is the outside world all a lie?!" },
				{ Cue = "/VO/Hades_0397",
					Text = "Ah, now you are asking the big questions! Comforting to see such growth of character. Well if there is an outside world, you shall not be seeing it for quite some time. You know why, don't you?" },
				{ Cue = "/VO/ZagreusHome_0863", Portrait = "Portrait_Zag_Defiant_01", Speaker = "CharProtag",
					Text = "Ongoing Underworld renovations?" },
				{ Cue = "/VO/Hades_0398",
					Text = "Ongoing Underworld renovations. Now go and let King Theseus drive that sharp spear of his into yourself some more, until such time. The mortals soon shall force me to reopen, and then what?" },
			},
			HadesRunCleared02FollowUp =
			{
				Priority = true,
				PlayOnce = true,
				UseableOffSource = true,
				BlockStatusAnimations = true,
				RequiredTextLinesThisRun = "HadesRunCleared02",
				-- That's not a bad idea.
				EndCue = "/VO/ZagreusHome_0384",
				EndWait = 0.5,
				{ Cue = "/VO/Hades_0223",
					Text = "Now you begone from here, get yourself killed some more for all I care." },
			},
			-- Lord Hades Ending (retired, Early Access)
			HadesRunCleared03 =
			{
				Skip = true,
				Priority = true,
				PlayOnce = true,
				RequiredKillsThisRun = { "Hades" },
				RequiredFalseTextLines = { "LordHadesDefeated01", "PersephoneFirstMeeting" },
				RequiresRunCleared = true,
				UseableOffSource = true,
				-- Guess I'll keep fighting you just for the sake of it then.
				EndCue = "/VO/ZagreusHome_0915",
				EndWait = 0.65,
				{ Cue = "/VO/Hades_0428",
					Text = "You are late, boy! Have you any idea what time it is?" },
				{ Cue = "/VO/ZagreusHome_0913", Portrait = "Portrait_Zag_Defiant_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Text = "No. Wait, what? I just vanquished you in single combat, and that's all you have to say to me?" },
				{ Cue = "/VO/Hades_0429",
					PreLineThreadedFunctionName = "PlayFistSlamAnimFromSource",
					Text = "Vanquished {#DialogueItalicFormat}me{#PreviousFormat}? {#DialogueItalicFormat}Hah, hahaha{#PreviousFormat}, nonsense. Don't you think I would recall such an event?" },
				{ Cue = "/VO/ZagreusHome_0914", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "You can't be serious. You're going to pretend as though it never happened?" },
				{ Cue = "/VO/Hades_0430",
					Text = "Everything is as it always was. And so shall it be, until the Fates determine otherwise. Or until our ongoing Underworld renovations are finally complete. Whichever comes first." },
			},
			-- (Early Access, retired)
			HadesRunCleared03_C =
			{
				Skip = true,
				Priority = true,
				PlayOnce = true,
				UseableOffSource = true,
				RequiredKillsThisRun = { "Hades" },
				RequiredTextLines = { "HadesRunCleared03_B" },
				RequiredFalseTextLines = { "LordHadesFirstDefeat", "LordHadesDefeated01", "PersephoneFirstMeeting" },
				RequiresRunCleared = true,
				EndVoiceLines =
				{
					PreLineWait = 0.65,
					UsePlayerSource = true,
					RequiredMinElapsedTime = 3,
					-- Excellent, can't wait!
					{ Cue = "/VO/ZagreusHome_0947" },
				},
				{ Cue = "/VO/ZagreusHome_0946", Portrait = "Portrait_Zag_Defiant_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Text = "So you're really not going to answer my question about Mother from the other time, until the Underworld renovations are complete?" },
				{ Cue = "/VO/Hades_0458",
					PreLineThreadedFunctionName = "PlayFistSlamAnimFromSource",
					Text = "I know not what you're going on about, there, boy. Although, my focus is entirely upon our renovations, yes. Perhaps someday or night when this place is in proper working order, I shall have more time for your insipid questions." },
			},
			-- ending
