			HadesAfterPersephoneMeeting01_B =
			{
				Priority = true,
				PlayOnce = true,
				UseableOffSource = true,
				RequiredTextLines = { "HadesAfterPersephoneMeeting01" },
				RequiredFalseTextLines = { "Ending01" },
				RequiresRunCleared = true,
				EndVoiceLines =
				{
					PreLineWait = 0.35,
					ObjectType = "NPC_Hades_01",
					-- No! I'll hear no demands from you.
					{ Cue = "/VO/Hades_0948", PreLineThreadedFunctionName = "PlayFistSlamAnimFromSource" },
				},
				{ Cue = "/VO/Hades_0946",
					Text = "I told you, boy. I told you! There's no escape from here. Learned that the hard way, then?" },
				{ Cue = "/VO/ZagreusHome_3417", Portrait = "Portrait_Zag_Defiant_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Text = "You could have told me more important things. Mother left because she never even knew I was alive. Thanks to you! She's got a lot of questions, herself, as you can probably imagine. I'm headed back to her. You going to cooperate?" },
				{ Cue = "/VO/Hades_0947",
					Emote = "PortraitEmoteFiredUp",
					PreLineAnim = "Hades_HouseWryLaughToGlaring",
					Text = "{#DialogueItalicFormat}Hahaha{#PreviousFormat}, nonsense! Listen to me: If you have any sense remaining in that head of yours, I caution you not to discuss this here and now." },
				{ Cue = "/VO/ZagreusHome_3418", Portrait = "Portrait_Zag_Defiant_01", Speaker = "CharProtag",
					Emote = "PortraitEmoteFiredUp",
					Text = "Let me go. Let me find her. She demands it!" },
			},

			HadesAfterPersephoneMeeting02 =
			{
				SuperPriority = true,
				PlayOnce = true,
				UseableOffSource = true,
				RequiredTextLines = { "HadesAfterPersephoneMeeting01", "PersephoneMeeting02" },
				RequiredFalseTextLines = { "Ending01" },
				RequiresRunCleared = true,
				EndVoiceLines =
				{
					UsePlayerSource = true,
					RequiredMinElapsedTime = 3,
