					-- Count on that.
					{ Cue = "/VO/ZagreusHome_3211" },
				},
				{ Cue = "/VO/ZagreusHome_3210", Portrait = "Portrait_Zag_Defiant_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Text = "Quite a mess you've created, I must say, Father... after being of no help whatsoever when it comes to disentangling it all. Though, this isn't the place to talk, right?" },
				{ Cue = "/VO/Hades_0949",
					Emote = "PortraitEmoteFiredUp",
					PreLineThreadedFunctionName = "PlayFistSlamAnimFromSource",
					Text = "No! It most certainly is not. Though, I take it it shall not be long before another of our private meetings, then." },
			},

			HadesRunCleared03_B =
			{
				PlayOnce = true,
				UseableOffSource = true,
				RequiredMinNPCInteractions = { NPC_Hades_01 = 1 },
				RequiredFalseTextLines = { "Ending01" },
				RequiredMinRunsCleared = 4,
				RequiresRunCleared = true,
				EndVoiceLines =
				{
					UsePlayerSource = true,
					RequiredMinElapsedTime = 3,
					PreLineWait = 0.5,
					-- Until such place and time, then.
					{ Cue = "/VO/ZagreusHome_0945" },
				},
				{ Cue = "/VO/ZagreusHome_0944", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Text = "Something the matter, there, Father? You seem a little quieter than usual. Dare I say even a little somber and remorseful, for some reason?" },
				{ Cue = "/VO/Hades_0457",
					PreLineThreadedFunctionName = "PlayFistSlamAnimFromSource",
