				{ Cue = "/VO/ZagreusHome_1496", Portrait = "Portrait_Zag_Defiant_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "Be sure to add those to the list of words you'll eat someday." },
			},

			HadesAboutShrine02 =
			{
				PlayOnce = true,
				UseableOffSource = true,
				RequiredMinNPCInteractions = { NPC_Hades_01 = 1 },
				RequiredTextLines = { "Ending01" },
				RequiredFalseTextLines = { "HadesAboutShrine01", "HadesShrineProgress01", "HadesShrineProgress02" },
				RequiredScreenViewed = "ShrineUpgrade",
				RequiredFalseFlags = { "HardMode" },
				EndVoiceLines =
				{
					PreLineWait = 0.65,
					UsePlayerSource = true,
					RequiredMinElapsedTime = 3,
					-- Tsch.
					{ Cue = "/VO/ZagreusHome_0976" },
				},
				{ Cue = "/VO/ZagreusHome_0973", Portrait = "Portrait_Zag_Defiant_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Text = "Saw your new Pact of Punishment out in the courtyard, Father. Impressed that you could find a parchment large enough to fit all the unpleasant stipulations. So you're taking even harsher measures to contain me?" },
				{ Cue = "/VO/Hades_0478",
					Text = "Whether you consent to the terms of the Pact is entirely up to you, boy. But, in all your comings and goings, it is important that we have full knowledge of which property is being carried from the premises." },
			},

			HadesShrineProgress01 =
			{
				PlayOnce = true,
				UseableOffSource = true,
				RequiredMinNPCInteractions = { NPC_Hades_01 = 1 },
				RequiredFalseTextLines = { "HadesShrineProgress02" },
				RequiredActiveShrinePointsMin = 12,
				EndVoiceLines =
				{
					PreLineWait = 0.55,
					UsePlayerSource = true,
					RequiredMinElapsedTime = 3,
					-- Plenty of room for improvement...
					{ Cue = "/VO/ZagreusHome_0740" },
				},
				{ Cue = "/VO/Hades_0349",
					Text = "You've taken more advantage of the Pact of Punishment than I would have expected, boy. I almost wish to commend you for doing such a thing." },
				{ Cue = "/VO/ZagreusHome_0738", Portrait = "Portrait_Zag_Defiant_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Text = "Almost? I've been rampaging through the worst your realm has to offer. Causing you untold inconvenience and likely a fortune in repair costs. I thought you'd be upset." },
				{ Cue = "/VO/Hades_0350",
					Text = "Upset? Heavens, no. Through your repeated bumbling, you have revealed a number of shortcomings in our operations, which my underlings are busy working to address." },
				{ Cue = "/VO/ZagreusHome_0739", Portrait = "Portrait_Zag_Defiant_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "You know you sound especially despicable when you say {#DialogueItalicFormat}underlings{#PreviousFormat}, don't you?" },
				{ Cue = "/VO/Hades_0351",
					Text = "Don't let me keep you any longer, boy, go forth! Try to escape again. You're helping make the Underworld a better place." },
			},
			HadesShrineProgress02 =
			{
				PlayOnce = true,
				UseableOffSource = true,
				RequiredMinNPCInteractions = { NPC_Hades_01 = 1 },
				ReachedShrineSoftCapWithAnyWeaponName = true,
				RequiresRunCleared = true,
				RequiredActiveShrinePointsMin = 5,
				EndVoiceLines =
				{
					PreLineWait = 0.55,
					UsePlayerSource = true,
					RequiredMinElapsedTime = 3,
					-- Sure. Sorry about your Pact.
					{ Cue = "/VO/ZagreusHome_1243" },
				},
				{ Cue = "/VO/Hades_0545",
					Text = "Explain to me something: How did you do it? I mean the Pact. For all of the conditions it imposed... you still surpassed me. How?" },
				{ Cue = "/VO/ZagreusHome_1241", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Text = "Plenty of practice, regular exercise, a positive attitude, the blessings of assorted family members you don't like, and your discarded Titan-slaying weapons I suppose. Nothing special. Why do you ask?" },
				{ Cue = "/VO/Hades_0546",
					Portrait = "Portrait_Hades_Averted_01",
					Text = "To think that you would squander such a talent on frivolous pursuit. You do not even realize what you have achieved, do you." },
				{ Cue = "/VO/ZagreusHome_1242", Portrait = "Portrait_Zag_Defiant_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "Wait, wait wait wait wait, care to repeat that first part that you said back there? The {#DialogueItalicFormat}talent {#PreviousFormat}part?" },
				{ Cue = "/VO/Hades_0547",
					Text = "Would that you had a talent for listening. Begone for now." },
			},

			HadesAboutExtremeMeasures01 =
			{
				PlayOnce = true,
				Priority = true,
				UseableOffSource = true,
				RequiredTextLines = { "Ending01", "LordHadesExtremeMeasures01" },
				RequiredMinNPCInteractions = { NPC_Hades_01 = 1 },
				RequiredCosmetics = { "HadesEMFight" },
				RequiredPlayed = { "/VO/HadesField_0584" },
				EndVoiceLines =
				{
					PreLineWait = 0.45,
					UsePlayerSource = true,
					RequiredMinElapsedTime = 3,
					-- Bet I can take you.
					{ Cue = "/VO/ZagreusHome_3469" },
				},
				{ Cue = "/VO/Hades_1084",
					Text = "Back to your senses, then, boy? The Pact of Punishment is not something one ought to trifle with unknowingly, in case you've yet to notice." },
				{ Cue = "/VO/ZagreusHome_3467", Portrait = "Portrait_Zag_Empathetic_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Text = "Father... the Pact... you were so blasted strong. I had no idea! All those other times we fought... I can't believe that you were holding back. You were desperate to stop me, you could have!" },
				{ Cue = "/VO/Hades_1085",
					Text = "We are bound by the Fates to this realm. It is ours, and we belong to it, in turn. And... I did not hold back against you. Rather... I had forgotten my old strength. Some of it since returned to me." },
				{ Cue = "/VO/ZagreusHome_3468", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusInteractionThoughtful", PreLineAnimTarget = "Hero",
					Text = "Must have been all our vicious battles to the death? Or maybe it's from Mother being back in your life?" },
				{ Cue = "/VO/Hades_1086",
					PreLineThreadedFunctionName = "PlayFistSlamAnimFromSource",
					Text = "Pointless to even ask. What motivates us is a complicated thing. But if you long to face me with the power I once held during my own youth... you have available to you the means." },
			},
			HadesAboutExtremeMeasures02 =
			{
				PlayOnce = true,
				Priority = true,
				UseableOffSource = true,
				RequiredTextLines = { "Ending01", "LordHadesExtremeMeasures01" },
				RequiredMinNPCInteractions = { NPC_Hades_01 = 1 },
				RequiredMinActiveMetaUpgradeLevel = { Name = "BossDifficultyShrineUpgrade", Count = 4 },
				RequiresRunCleared = true,
				EndVoiceLines =
				{
					PreLineWait = 0.45,
					UsePlayerSource = true,
					RequiredMinElapsedTime = 2,
					-- I'm thinking there shall.
					{ Cue = "/VO/ZagreusHome_3521" },
				},
				{ Cue = "/VO/Hades_1132",
					Text = "{#DialogueItalicFormat}Urgh{#PreviousFormat}... come, get your gloating well out of the way, so I can get back to this parchmentwork. It piled up, in my prolonged absence!" },
				{ Cue = "/VO/ZagreusHome_3520", Portrait = "Portrait_Zag_Empathetic_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Text = "Not here to gloat, Father. Well fought back there. I can't believe I got you." },
				{ Cue = "/VO/Hades_1133", PreLineWait = 0.5,
					Portrait = "Portrait_Hades_Default_01",
					Text = "...Nor I. Next time, the Fates may not decide to favor you like that. If there shall be a next time." },
			},			

			HadesAboutZeus01 =
			{
				Priority = true,
				PlayOnce = true,
				UseableOffSource = true,
				RequiredTextLines = { "ZeusAboutHades01" },
				RequiredMinNPCInteractions = { NPC_Hades_01 = 1 },
				RequiredFalseTextLines = { "HadesAboutOlympianReunionQuest01A", "OlympianReunionQuestComplete" },
				IgnoreResumeGhostProcessionOnUseableOff = true,
				EndVoiceLines =
				{
					PreLineWait = 0.65,
					UsePlayerSource = true,
					RequiredMinElapsedTime = 2,
					-- Loud and clear.
					{ Cue = "/VO/ZagreusHome_0620" },
				},
				{ Cue = "/VO/ZagreusHome_0618", Portrait = "Portrait_Zag_Empathetic_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Text = "Father... look, Lord Uncle Zeus, I've been in contact with him, and he wishes to make peace. He asked for me to tell you. To tell you he forgives you for... whatever happened." },
				{ Cue = "/VO/Hades_0287",
					Emote = "PortraitEmoteAnger",
					PreLineThreadedFunctionName = "PlayFistSlamAnimFromSource",
					Text = "My brother Zeus... forgives me? Zeus forgives {#DialogueItalicFormat}me{#PreviousFormat}?! How dare that blasted little brat! His beard may be luxurious but he's as much a child as are you. Forgives me, bah!" },
				{ Cue = "/VO/ZagreusHome_0619", Portrait = "Portrait_Zag_Empathetic_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "What did you do...? What happened between you?" },
				{ Cue = "/VO/Hades_0288",
					Emote = "PortraitEmoteFiredUp",
					PreLineThreadedFunctionName = "PlayFistSlamAnimFromSource",
					Text = "None of your business! Go on now and frolic with that Lord Uncle of yours, the both of them! And don't you ever speak of him to me again, you hear me?" },
			},

			HadesAboutPersephone01 =
			{
				PlayOnce = true,
				Priority = true,
				UseableOffSource = true,
				RequiredTextLines = { "DemeterAboutPersephone03" },
				RequiredMinNPCInteractions = { NPC_Hades_01 = 1 },
				RequiredFalseTextLines = { "Ending01" },
				IgnoreResumeGhostProcessionOnUseableOff = true,
				EndVoiceLines =
				{
					PreLineWait = 0.65,
					UsePlayerSource = true,
					RequiredMinElapsedTime = 2,
					-- ...You can't hide the truth forever.
					{ Cue = "/VO/ZagreusHome_1608" },
				},
				{ Cue = "/VO/ZagreusHome_1606", Portrait = "Portrait_Zag_Defiant_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Text = "Question for you, Father. The name {#DialogueItalicFormat}Kore {#PreviousFormat}happen to mean anything to you, by any chance?" },
				{ Cue = "/VO/Hades_0632",
					Emote = "PortraitEmoteSurprise",
					Text = "{#DialogueItalicFormat}What{#PreviousFormat}, where did you...?! {#DialogueItalicFormat}Ahh{#PreviousFormat}, the cold witch brought it up, did she? Never repeat that name again, not here, nor anywhere, you hear me, boy? Know only this: The one called by that name despised it even more than I." },
				{ Cue = "/VO/ZagreusHome_1607", Portrait = "Portrait_Zag_Defiant_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusInteractionThoughtful", PreLineAnimTarget = "Hero",
					Text = "So let's see. Demeter's daughter went missing. She wound up down here. I... happened. Mother left. Demeter, and everyone else on Olympus apparently, has no idea. That about right? Tell me what happened." },
				{ Cue = "/VO/Hades_0633",
					Emote = "PortraitEmoteAnger",
					PreLineThreadedFunctionName = "PlayFistSlamAnimFromSource",
					Text = "I'll tell you nothing! Keep meddling in matters that you cannot understand, and you'll incur the wrath of Olympus on all that you hold dear. And, know that it is not for {#DialogueItalicFormat}my {#PreviousFormat}sake that I'm warning you." },
			},
			HadesAboutPersephone02 =
			{
				PlayOnce = true,
				Priority = true,
				UseableOffSource = true,
				RequiredTextLines = { "HadesAboutPersephone01" },
				RequiredFalseTextLines = { "Ending01" },
				IgnoreResumeGhostProcessionOnUseableOff = true,
				EndVoiceLines =
				{
					PreLineWait = 0.45,
					UsePlayerSource = true,
					RequiredMinElapsedTime = 2,
					-- ...I'll have to try that, thanks.
					{ Cue = "/VO/ZagreusHome_1611" },
				},
				{ Cue = "/VO/Hades_0634",
					Text = "What is it now? I see that sneering look upon your countenance, which must mean you have yet another one of your insipid questions, haven't you." },
				{ Cue = "/VO/ZagreusHome_1609", Portrait = "Portrait_Zag_Defiant_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Text = "Oh, this one's not insipid in the slightest. Let me get this straight. My mother's Persephone. Her mother's Demeter, but her father... evidently was some farm-boy? My red blood comes from him? And my propensity to die I guess." },
				{ Cue = "/VO/Hades_0635",
					Text = "You blame your ancestors for your own weakness? You should be thankful you were born with immortality! Though, your meddlesome curiosity and constant need for affirmation must certainly be from your mortal side." },
				{ Cue = "/VO/ZagreusHome_1610", Portrait = "Portrait_Zag_Defiant_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "Then, Mother, did... she grow older with age? Is she just going to expire as the mortals do? With demigods I hear it can go either way." },
				{ Cue = "/VO/Hades_0636",
					Emote = "PortraitEmoteAnger",
					PreLineThreadedFunctionName = "PlayFistSlamAnimFromSource",
					Text = "I said never to speak of her, blast you! You said, yourself, she is the daughter of the goddess of seasons! Use your fool head, for once." },
			},

			HadesAboutDemeter01 =
			{
				PlayOnce = true,
				Priority = true,
				UseableOffSource = true,
				RequiredMinNPCInteractions = { NPC_Hades_01 = 1 },
				RequiredTextLines = { "DemeterAboutPersephone03", "HadesAboutPersephone02" },
				RequiredLootThisRun = "DemeterUpgrade",
				RequiredFalseTextLines = { "OlympianReunionQuestComplete", "HadesGift05" },
				IgnoreResumeGhostProcessionOnUseableOff = true,
				EndVoiceLines =
				{
					PreLineWait = 0.65,
					UsePlayerSource = true,
					RequiredMinElapsedTime = 2,
					-- ...Hard to imagine.
					{ Cue = "/VO/ZagreusHome_1605" },
				},
				{ Cue = "/VO/ZagreusHome_1604", Portrait = "Portrait_Zag_Defiant_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Text = "Greetings, Father. Ran into Lady Demeter out there, on my last outing. Or should I say, my grandmother? What's she to you, then, ex-mother-in-law? Source of abject terror? Hard keeping track. Our family's rather complicated." },
				{ Cue = "/VO/Hades_0631",
					Emote = "PortraitEmoteAnger",
					PreLineThreadedFunctionName = "PlayFistSlamAnimFromSource",
					Text = "Do not invoke her name inside my House, you hear me, boy? You do not know at all what she is like. Though I can see why you two hit it off. She hated her father. Though understandably, as he was worse even than mine. Even than yours." },
			},

			-- ending
			HadesPostEnding01 =
			{
				SuperPriority = true,
				PlayOnce = true,
				UseableOffSource = true,
				RequiredTextLines = { "Ending01" },
				RequiredFalseTextLines = { "OlympianReunionQuestComplete" },
				IgnoreResumeGhostProcessionOnUseableOff = true,
				EndVoiceLines =
				{
					PreLineWait = 0.4,
					ObjectType = "NPC_Hades_01",
					-- I do. Your mother the Queen is working on that.
					{ Cue = "/VO/Hades_0942" },
				},
				{ Cue = "/VO/ZagreusHome_2713", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero", PreLineWait = 0.35,
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Text = "Greetings, Father. So! I can just... head on out there again whenever I want? Don't need to stick to any rigid schedule?" },
				{ Cue = "/VO/Hades_0941",
					Text = "Your unpredictability is one of your assets. Our defenses must always be on guard. So, yes: Go about your business as you best see fit!" },
				{ Cue = "/VO/ZagreusHome_2714", Portrait = "Portrait_Zag_Defiant_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusInteractionThoughtful", PreLineAnimTarget = "Hero",
					Text = "And the Olympians are going to keep helping me, thinking I'm trying to get away from you, to {#DialogueItalicFormat}them{#PreviousFormat}. We can't keep this up with them forever, you know." },
				OnQueuedFunctionName = "CheckDistanceTriggerThread",
				OnQueuedFunctionArgs = PresetEventArgs.HadesPostEnding,
			},
			HadesPostEnding02 =
			{
				Priority = true,
				PlayOnce = true,
				UseableOffSource = true,
				RequiredTextLines = { "Ending01", "PersephoneHomeMeeting01" },
				RequiredAnyTextLines = { "HadesGift01", "HadesGift01_B" },
				MinRunsSinceAnyTextLines = { TextLines = { "Ending01", "PersephoneHomeMeeting01" }, Count = 2 },
				IgnoreResumeGhostProcessionOnUseableOff = true,
				EndVoiceLines =
				{
					PreLineWait = 0.5,
					UsePlayerSource = true,
					-- You're welcome.
					{ Cue = "/VO/ZagreusHome_3242" },
				},
				{ Cue = "/VO/Hades_1052",
					Text = "Tell me something, boy... Zagreus... why did you do all this? Return the Queen. Go to such lengths. At a certain point... you must have learned the way I felt about her. Yet still you persisted. If I were you... I'd not have done me any favors." },
				{ Cue = "/VO/ZagreusHome_3241", Portrait = "Portrait_Zag_Serious_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero", PreLineWait = 0.35,
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Text = "I didn't often stop to give it much thought. I just knew I needed to find her... and then as soon as I did, I knew I needed her in my life. In other words, doing you any favors was never a priority, Father. Just ended up that way, I guess." },
				{ Cue = "/VO/Hades_1053", PreLineWait = 0.4,
					Portrait = "Portrait_Hades_Averted_01",
					Text = "...I see. I appreciate your candor. Nevertheless... I thank you. I do not know how you accomplished what you did, but know that I am grateful for the outcome. Even if I fail to act like it, much of the time." },
			},
			HadesPostEnding03 =
			{
				PlayOnce = true,
				UseableOffSource = true,
				RequiredTextLines = { "Ending01", "HadesGift03" },
				IgnoreResumeGhostProcessionOnUseableOff = true,
				EndVoiceLines =
				{
					PreLineWait = 0.45,
					UsePlayerSource = true,
					RequiredMinElapsedTime = 3,
					-- Can't wait!
					{ Cue = "/VO/ZagreusHome_3475" },
				},
				{ Cue = "/VO/ZagreusHome_3473", Portrait = "Portrait_Zag_Defiant_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero", PreLineWait = 0.35,
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Text = "Say, Father... back when you drew lots with your brothers, and wound up with the Underworld, rather than the heavens or the seas... which would you have claimed, if the choice was yours?" },
				{ Cue = "/VO/Hades_1088",
					Text = "I do not deal in hypotheticals, boy. Each realm has its host of unenviable problems. You have never experienced the cold and sopping sea, or the blinding heights of Olympus. I have. And I assure you, you're not missing much." },
				{ Cue = "/VO/ZagreusHome_3474", Portrait = "Portrait_Zag_Defiant_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "Didn't you try to leave the Underworld, yourself, at some point? You once gave me that impression. I always thought it was because you hated it here." },
				{ Cue = "/VO/Hades_1089",
					PreLineThreadedFunctionName = "PlayFistSlamAnimFromSource",
					Text = "I hate not having any choice. Enjoy the freedoms that you have. As you grow long in years, you gain more burdens and responsibilities, until they bind you." },
			},
			HadesPostEnding04 =
			{
				PlayOnce = true,
				Priority = true,
				UseableOffSource = true,
				RequiredTextLines = { "Ending01" },
				RequiredMinNPCInteractions = { NPC_Hades_01 = 1 },
				RequiredFalseTextLines = { "OlympianReunionQuestComplete" },
				EndVoiceLines =
				{
					PreLineWait = 0.45,
					UsePlayerSource = true,
					RequiredMinElapsedTime = 3,
					-- Sounds like you've got it all under control.
					{ Cue = "/VO/ZagreusHome_3667" },
				},
				{ Cue = "/VO/ZagreusHome_3666", Portrait = "Portrait_Zag_Defiant_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Text = "Father, all these shades... aren't they going to let on that Mother's back? What if word spreads... and the Olympians find out?" },
				{ Cue = "/VO/Hades_1176",
					Text = "You are so well-accustomed to defying me that you forget there are no others, either living or dead, who would so dare. {#DialogueItalicFormat}None {#PreviousFormat}in this House shall speak of this until we are prepared to make any announcements, on our own terms." },
			},

			HadesRevealsBadgeSeller01 =
			{
				Priority = true,
				PlayOnce = true,
				UseableOffSource = true,
				RequiredTextLines = { "Ending01" },
				RequiredMinNPCInteractions = { NPC_Hades_01 = 1 },
				MinRunsSinceAnyTextLines = { TextLines = { "Ending01" }, Count = 3 },
				RequiredRoomLastRun = "D_Hub",
				EndVoiceLines =
				{
					PreLineWait = 0.4,
					UsePlayerSource = true,
					RequiredMinElapsedTime = 3,
					-- Sounds great!
					{ Cue = "/VO/ZagreusHome_3482" },
				},	
				{ Cue = "/VO/Hades_1094",
					Text = "Regarding your responsibilities: Our Resources Director is available to you. In my private quarters. Normally, it would be I, alone, transferring excess resources back into our realm." },
				{ Cue = "/VO/ZagreusHome_3480", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Text = "The fabled Resources Director! So, now I get to give away the hard-earned resources I've painstakingly gathered over time?" },
				{ Cue = "/VO/Hades_1095",
					Text = "You get to do precisely that. It is not necessary that you do. But if you wish to rise above your current station, you shall do it." },
				{ Cue = "/VO/ZagreusHome_3481", Portrait = "Portrait_Zag_Defiant_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "What's the point in rising above my current station? I thought our work was to be done in secrecy." },
				{ Cue = "/VO/Hades_1096",
					PostLineThreadedFunctionName = "MaxedRelationshipPresentation",
					PostLineFunctionArgs = { Delay = 1.5, Title = "AssistTraitsPermitted_Title", Text = "BadgeSellerUnlocked_Subtitle", TextRevealSound = "/Leftovers/Menu Sounds/TextReveal3", AnimationName = "LocationTextBGGeneric", AnimationOutName = "LocationTextBGGenericOut" },
					Text = "It is. There is no point but pride. And pride is dangerous. Do what you will. I care not, as long as everything gets adequately done." },
			},
