			HadesAboutBadgeSellerProgress01 =
			{
				PlayOnce = true,
				UseableOffSource = true,
				RequiredTextLines = { "HadesRevealsBadgeSeller01" },
				RequiredFalseTextLines = { "HadesAboutBadgeSellerProgress02", "HadesAboutBadgeSellerProgress03" },
				RequiredMinValues = { BadgeRank = 6 },
				EndVoiceLines =
				{
					PreLineWait = 0.4,
					UsePlayerSource = true,
					RequiredMinElapsedTime = 3,
					-- Thank you.
					{ Cue = "/VO/ZagreusHome_3483" },
				},
				{ Cue = "/VO/Hades_1097",
					Text = "Briefly, now, boy: Our Resources Director informs me you've advanced in rank. Commendable. That's all." },
			},
			HadesAboutBadgeSellerProgress02 =
			{
				PlayOnce = true,
				UseableOffSource = true,
				RequiredTextLines = { "HadesRevealsBadgeSeller01" },
				RequiredFalseTextLines = { "HadesAboutBadgeSellerProgress03" },
				RequiredMinValues = { BadgeRank = 21 },
				EndVoiceLines =
				{
					PreLineWait = 0.4,
					UsePlayerSource = true,
					RequiredMinElapsedTime = 3,
					-- OK!
					{ Cue = "/VO/ZagreusHome_0385" },
				},
				{ Cue = "/VO/Hades_1098",
					Text = "You are advancing through the ranks of your position, and returning to our realm the vast resources you have claimed. As well you ought. Carry on." },
			},
			HadesAboutBadgeSellerProgress03 =
			{
				PlayOnce = true,
				UseableOffSource = true,
				RequiredTextLines = { "HadesRevealsBadgeSeller01" },
				RequiredMinValues = { BadgeRank = 50 },
				IgnoreResumeGhostProcessionOnUseableOff = true,
				{ Cue = "/VO/Hades_1099",
					Text = "We cannot speak of this, except... {#DialogueItalicFormat}Unseen One{#PreviousFormat}... the highest ranking that we can bestow. I never once imagined that you could achieve that rank, yourself. Or any rank of note." },
				{ Cue = "/VO/ZagreusHome_3484", Portrait = "Portrait_Zag_Serious_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "You always thought too little of me, Father. Though, thank you for taking note of that. I figured I ought to give back to the realm that made me what I am." },
				{ Cue = "/VO/Hades_1100", PreLineWait = 0.35,
					Portrait = "Portrait_Hades_Averted_01",
					Text = "Yes. For this is the only world we know." },
			},

			HadesAboutOlympianReunionQuest01A =
			{
				SuperPriority = true,
				PlayOnce = true,
				UseableOffSource = true,
				GiftableOffSource = true,
				RequiredTextLines = { "Ending01" },
				MinRunsSinceAnyTextLines = { TextLines = { "Ending01" }, Count = 3 },
				RequiredFalseQueuedTextLines = GameData.GardenTextLines,
				IgnoreResumeGhostProcessionOnUseableOff = true,
				EndVoiceLines =
				{
					PreLineWait = 0.4,
					UsePlayerSource = true,
					RequiredMinElapsedTime = 3,
					-- I will... take care, Father.
					{ Cue = "/VO/ZagreusHome_3718" },
				},
				{ Cue = "/VO/ZagreusHome_3714", Portrait = "Portrait_Zag_Defiant_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero", PreLineWait = 0.35,
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Text = "Father... all this stuff with the Olympians... will you just tell me what's going on, for once? How did Mother come to join you here? What's your problem with Uncle Zeus?" },
				{ Cue = "/VO/Hades_1197",
					PreLineThreadedFunctionName = "PlayFistSlamAnimFromSource",
					PostLineThreadedFunctionName = "SetupHadesInGarden",
					PortraitExitAnimation = "Portrait_Hades_Default_01_Exit",
					EndSound = "/SFX/Menu Sounds/HadesTextDisappearFadeLOCATION",
					Emote = "PortraitEmoteFiredUp",
					Text = "I thought she already told you...! {#DialogueItalicFormat}Urgh{#PreviousFormat}. Come with me. Now." },
				{ Cue = "/VO/Hades_1198",
					FadeOutTime = 0.5, FullFadeTime = 1.8, FadeInTime = 2.0, 
					InitialExitSound = "/VO/CerberusGrowl",
					FadeInSound = "/Leftovers/World Sounds/MapZoomInShortHigh",
					-- TeleportToId = 556885,
					TeleportOffsetX = -180,
					TeleportOffsetY = -90,
					TeleportHeroToId = 556886,
					AngleHeroTowardTargetId = 556885,
					PreLineWait = 0.35,
					PreLineFunctionName = "HadesArmsCross",
					Text = "...Swear to me that you shall never repeat what you are about to hear. Swear it!" },
				{ Cue = "/VO/ZagreusHome_3715", Portrait = "Portrait_Zag_Defiant_01", Speaker = "CharProtag",
					PreLineFunctionName = "HadesArmsUnCross",
					PortraitExitAnimation = "Portrait_Zag_Defiant_01_Exit",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "All right, all right, I swear. I swear, in your own name, I'll keep what you're about to say between us.... So?" },
				{ Cue = "/VO/Hades_1199",
					Portrait = "Portrait_Hades_Averted_01",
					PreLineWait = 1.0,
					PreLineFunctionName = "HadesArmsCross",
					Text = "So... you had it mostly right, before. After I inherited the Underworld... your Uncle Zeus felt sympathy for me, he says. Your mother, he knew she was unhappy on Olympus. And... he knew I was drawn to her. So what did he do? He brought her here. Gave her to me as a {#DialogueItalicFormat}gift{#PreviousFormat}!" },
				{ Cue = "/VO/ZagreusHome_3716", Portrait = "Portrait_Zag_Defiant_01", Speaker = "CharProtag",
					PreLineFunctionName = "HadesArmsUnCross",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Text = "You weren't expecting a gift like that. But she didn't want to return to Olympus, and you liked her, so you didn't send her away. And you're angry with Zeus for putting you in that position? Cut ties with him?" },
				{ Cue = "/VO/Hades_1200",
					PreLineFunctionName = "HadesArmsCross",
					Emote = "PortraitEmoteAnger",
					Text = "{#DialogueItalicFormat}Yes{#PreviousFormat}!! The blasted fool, with all his flights of fancy! He does whatever he wants! He could have achieved a similar outcome in a limitless number of reasonable ways... rather than resort to what was tantamount to kidnapping! Even {#DialogueItalicFormat}if {#PreviousFormat}she wanted to leave!" },
				{ Cue = "/VO/ZagreusHome_3717", Portrait = "Portrait_Zag_Defiant_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusInteractionThoughtful", PreLineAnimTarget = "Hero",
					Text = "And now if Demeter and the others found all this out, you and Mother... even Zeus would be in a bad spot, wouldn't he? He must have a temper on him to match yours." },
				{ Cue = "/VO/Hades_1201",
					PreLineFunctionName = "HadesArmsUnCross",
					Text = "You know nothing of tempers if mine is your frame of reference, Zagreus. Now, enough of this! You know the rest. Remember your oath." },
			},
			HadesAboutOlympianReunionQuest01 =
			{
				PlayOnce = true,
				UseableOffSource = true,
				RequiredMinNPCInteractions = { NPC_Hades_01 = 1 },
				RequiredTextLines = { "Ending01" },
				RequiredFalseTextLines = { "OlympianReunionQuestComplete" },
				IgnoreResumeGhostProcessionOnUseableOff = true,
				EndVoiceLines =
				{
					PreLineWait = 0.35,
					ObjectType = "NPC_Hades_01",
					-- Nrgh...
					{ Cue = "/VO/Hades_0881" },
				},
				{ Cue = "/VO/Hades_1054",
					Text = "One thing, before you depart again, boy. Until the Queen's intentions with Olympus come to pass... not a word to anyone about her presence, here. Do you understand?" },
				{ Cue = "/VO/ZagreusHome_3243", Portrait = "Portrait_Zag_Serious_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusInteractionThoughtful", PreLineAnimTarget = "Hero",
					Text = "I do. But she won't have to hide forever." },
			},

			HadesPostEpilogue01 =
			{
				Priority = true,
				PlayOnce = true,
				UseableOffSource = true,
				RequiredMinNPCInteractions = { NPC_Hades_01 = 1 },
				RequiredTextLines = { "OlympianReunionQuestComplete" },
				MaxRunsSinceAnyTextLines = { TextLines = { "OlympianReunionQuestComplete" }, Count = 20 },
				IgnoreResumeGhostProcessionOnUseableOff  = true,
				EndVoiceLines =
				{
					PreLineWait = 0.45,
					ObjectType = "NPC_Hades_01",
					-- The work nevertheless has to be done!
					{ Cue = "/VO/Hades_1178", PreLineAnim = "Hades_HouseFistSlam" },
				},	
				{ Cue = "/VO/ZagreusHome_3668", Portrait = "Portrait_Zag_Defiant_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Text = "So, now that the festivities are over with, it's back to work like this, the same as ever?" },
				{ Cue = "/VO/Hades_1177",
					Text = "Do you have any idea what happens when you announce the Underworld has a Queen, boy? {#DialogueItalicFormat}Urgh, damn it{#PreviousFormat}! {#DialogueItalicFormat}Zagreus{#PreviousFormat}, I mean. You become inundated with well-wishes from every blasted shade that wants something." },
				{ Cue = "/VO/ZagreusHome_3669", Portrait = "Portrait_Zag_Empathetic_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "You're responding to well-wishes? {#DialogueItalicFormat}Aw. {#PreviousFormat}Don't you think some of your subjects are sincerely happy for you?" },
			},

			HadesPostEpilogue02 =
			{
				PlayOnce = true,
				UseableOffSource = true,
				RequiredTextLines = { "OlympianReunionQuestComplete" },
				RequiredAnyTextLines = { "HadesGift02" },
				EndVoiceLines =
				{
					PreLineWait = 0.45,
					ObjectType = "NPC_Hades_01",
					-- They are very easy to maintain!
					{ Cue = "/VO/Hades_1058" },
				},	
				{ Cue = "/VO/ZagreusHome_3419", Portrait = "Portrait_Zag_Defiant_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Text = "I never stopped to think about why pomegranates grew here in the Underworld. Mother's doing from before, then? And all that stuff she told the Olympians, about how they bind you to this place... it's all nonsense?" },
				{ Cue = "/VO/Hades_1057",
					Text = "You know full well that the pomegranates in my realm hold a certain power beyond the tartness of their taste, boy. The Queen... she has a way with plants, you see. A bit of her own mother's gift, applied more sensibly." },
				{ Cue = "/VO/ZagreusHome_3420", Portrait = "Portrait_Zag_Empathetic_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "You kept them growing all this time... flourishing, even, because of her." },
			},
			-- there's an 03 below
			HadesPostEpilogue03A =
			{
				PlayOnce = true,
				UseableOffSource = true,
				RequiredTextLines = { "Ending01", "HadesGift03" },
				RequiredAnyTextLines = { "EurydiceAboutPersephone01", "EurydiceAboutPersephone01_B", "EurydicePostEpilogue01", "SisyphusPostEpilogue01" },
				EndVoiceLines =
				{
					PreLineWait = 0.4,
					UsePlayerSource = true,
					RequiredMinElapsedTime = 3,
					-- All right, all right.
					{ Cue = "/VO/ZagreusHome_3665" },
				},
				{ Cue = "/VO/ZagreusHome_3664", Portrait = "Portrait_Zag_Defiant_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "So, what happened, when Mother first arrived, if I may ask? Some of your residents seem to remember the Queen from before. Yet the Olympians just never found out?" },
				{ Cue = "/VO/Hades_1175",
					Text = "They had no jurisdiction here. No knowledge of our goings-on. Well, Hermes did, perhaps, but in a very limited capacity. As far as anybody knew beyond this realm... our Queen was Nyx. Ask nothing more of this." },
			},

			HadesAboutPersephoneLeavesToOlympus01 =
			{
				Priority = true,
				PlayOnce = true,
				UseableOffSource = true,
				RequiredMinNPCInteractions = { NPC_Hades_01 = 1 },
				RequiredTrueFlags = { "PersephoneAway" },
				RequiredTextLines = { "PersephoneLeavesToOlympus01" },
				MinRunsSinceAnyTextLines = { TextLines = { "PersephoneLeavesToOlympus01" }, Count = 2 },
				EndVoiceLines =
				{
					PreLineWait = 0.4,
					UsePlayerSource = true,
					RequiredMinElapsedTime = 3,
					-- Glad you're handling it fine.
					{ Cue = "/VO/ZagreusHome_3248" },
				},
				{ Cue = "/VO/ZagreusHome_3246", Portrait = "Portrait_Zag_Defiant_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "Father... how are you doing without Mother around? Will she be all right, on Olympus?" },
				{ Cue = "/VO/Hades_1067",
					Text = "Clearly she is more than capable of handling herself. And we here have been well accustomed to not having her about." },
				{ Cue = "/VO/ZagreusHome_3247", Portrait = "Portrait_Zag_Empathetic_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Text = "So everything's back to normal, then? That sounds lovely." },
				{ Cue = "/VO/Hades_1068",
					Text = "Nothing is normal, anymore. Besides! She shall return. Knowing that she shall return, is that not sufficient for you? Be patient, for once." },
			},
			HadesAboutPersephoneReturnsFromOlympus01 =
			{
				SuperPriority = true,
				PlayOnce = true,
				UseableOffSource = true,
				AreIdsAlive = { 555714 },
				RequiredTextLines = { "PersephoneLeavesToOlympus01" },
				MinRunsSinceAnyTextLines = { TextLines = { "PersephoneLeavesToOlympus01" }, Count = 2 },
				EndVoiceLines =
				{
					{
						PreLineWait = 0.35,
						UsePlayerSource = true,
						-- Zagreus.
						{ Cue = "/VO/ZagreusHome_3253" },
					},
					{
						PreLineWait = 0.4,
						ObjectType = "NPC_Hades_01",
						-- ...Zagreus.
						{ Cue = "/VO/Hades_1073" },
					},
				},	
				{ Cue = "/VO/ZagreusHome_3252", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Text = "It's good to see Mother's back. Not that I was too concerned, but... I was definitely a little concerned." },
				{ Cue = "/VO/Hades_1072",
					Text = "I shared in that particular concern, and yes... it is good the Queen has once again returned. And she shall have to depart again before we know it, so cherish your time with her, boy!" },
			},

			HadesPostEpilogue03 =
			{
				PlayOnce = true,
				UseableOffSource = true,
				RequiredMinNPCInteractions = { NPC_Hades_01 = 1 },
				RequiredTextLines = { "OlympianReunionQuestComplete" },
				RequiredMinAnyTextLines = { TextLines = { "ZeusPostEpilogue01", "PoseidonPostEpilogue01", "AthenaPostEpilogue01", "AphroditePostEpilogue01", "AresPostEpilogue01", "ArtemisPostEpilogue01", "DionysusPostEpilogue01", "HermesPostEpilogue01", "DemeterPostEpilogue01" }, Count = 4 },
				MinRunsSinceAnyTextLines = { TextLines = { "OlympianReunionQuestComplete" }, Count = 3 },
				EndVoiceLines =
				{
					PreLineWait = 0.45,
					UsePlayerSource = true,
					RequiredMinElapsedTime = 3,
					-- They have.
					{ Cue = "/VO/ZagreusHome_3477" },
				},
				{ Cue = "/VO/Hades_1090",
					Text = "Tell me, in brief, boy. Our own upon Olympus... are they continuing to aid you as expected?" },
				{ Cue = "/VO/ZagreusHome_3476", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Text = "Still showing up and lending their support. I think some of them are a little disappointed I'm not planning to come join them, though... I think they understand now that they owe the Underworld a debt. We all have to do our part, looking after the mortals. Whether alive or dead." },
				{ Cue = "/VO/Hades_1091",
					Text = "Indeed. I half-expected they would disappoint. Abandon you to toil on your own. At least thus far they've honored their commitment, then." },
			},
			HadesPostEpilogue04 =
			{
				PlayOnce = true,
				UseableOffSource = true,
				RequiredTextLines = { "OlympianReunionQuestComplete" },
				MinRunsSinceAnyTextLines = { TextLines = { "OlympianReunionQuestComplete" }, Count = 10 },
				EndVoiceLines =
				{
					{
						PreLineWait = 0.45,
						UsePlayerSource = true,
						-- But, they're family...
						{ Cue = "/VO/ZagreusHome_3251" },
					},
					{
						PreLineWait = 0.6,
						ObjectType = "NPC_Hades_01",
						-- Pah.
						{ Cue = "/VO/Hades_1071" },
					},
				},	
				{ Cue = "/VO/ZagreusHome_3249", Portrait = "Portrait_Zag_Empathetic_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "So, are you maintaining better contact now with Uncle Zeus, Uncle Poseidon, and the rest? Oh, and... is it all right for me to speak about them here?" },
				{ Cue = "/VO/Hades_1069",
					Text = "{#DialogueItalicFormat}Urgh{#PreviousFormat}... I'm certain the superficial messages we have exchanged shall taper off, with time. We have always been apart, irrespective of the distance separating us. I know Zeus is content with your mother's version of events. Thus we are at peace, for now." },
				{ Cue = "/VO/ZagreusHome_3250", Portrait = "Portrait_Zag_Defiant_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					PreLineWait = 0.35,
					Text = "Wait, what do you mean, {#DialogueItalicFormat}for now{#PreviousFormat}?" },
				{ Cue = "/VO/Hades_1070",
					Text = "I mean only that our family is prone to causing problems for itself. Never content to leave things well enough alone. But if we have little to do with them... their quarrels, all their nonsense on Olympus... it should be of no consequence." },
			},
			HadesPostEpilogue05 =
			{
				PlayOnce = true,
				UseableOffSource = true,
				RequiredTextLines = { "OlympianReunionQuestComplete", "HadesGift05" },
				MinRunsSinceAnyTextLines = { TextLines = { "OlympianReunionQuestComplete" }, Count = 8 },
				RequiredRoomThisRun = "D_Hub",
				EndVoiceLines =
				{
					PreLineWait = 0.45,
					ObjectType = "NPC_Hades_01",
					-- Precisely.
					{ Cue = "/VO/Hades_0993" },
				},	
				{ Cue = "/VO/Hades_0991",
					Text = "The latest reports indicate that mortals are fully twice as fearful of ending up in Tartarus as in our last evaluation period, thanks to the destruction you have caused. You are an unseen power in the Underworld. Commendable." },
				{ Cue = "/VO/ZagreusHome_3423", Portrait = "Portrait_Zag_Defiant_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Text = "Commendable, me? An {#DialogueItalicFormat}unseen power{#PreviousFormat}, huh. Surprised you're giving me the credit, Father." },
				{ Cue = "/VO/Hades_0992",
					Text = "I give credit where it's due. But the mortals? They know nothing of what transpires here. Only rumblings we permit to escape our realm... rumblings that reinforce their deepest fears about what shall transpire after their demise." },
				{ Cue = "/VO/ZagreusHome_3424", Portrait = "Portrait_Zag_Defiant_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					PreLineWait = 0.35,
					Text = "So the more havoc I wreak in the Underworld, the more mortals hear about all the unpleasantness, and... it makes them fearful of this place? If more of them lead upstanding lives, it'll keep Tartarus from overflowing, I guess." },
			},

