			{
				UseableOffSource = true,
				RequiredFalseTextLines = { "Ending01" },
				EndGlobalVoiceLines = "MiscEndVoiceLines_Hades",
				{ Cue = "/VO/Hades_0124",
					Text = "Whatever you may wish to say, I do not wish to hear it." },
			},
			HadesChat22 =
			{
				UseableOffSource = true,
				RequiredFalseTextLines = { "Ending01" },
				EndGlobalVoiceLines = "MiscEndVoiceLines_Hades",
				{ Cue = "/VO/Hades_0425",
					Text = "Have I mentioned that you're never getting out of here?" },
			},
			HadesChat23 =
			{
				UseableOffSource = true,
				RequiredFalseTextLines = { "Ending01" },
				EndGlobalVoiceLines = "MiscEndVoiceLines_Hades",
				{ Cue = "/VO/Hades_0426",
					Text = "You're here to stay, boy. All of us are." },
			},
