		GiftTextLineSets =
		{
			HadesGift01 =
			{
				PlayOnce = true,
				UseableOffSource = true,
				RequiredFalseTextLines = { "Ending01", "HadesGift01_B" },
				-- You're welcome, Father.
				EndCue = "/VO/ZagreusHome_0314",
				EndWait = 1.0,
				{ Cue = "/VO/ZagreusHome_0113", Portrait = "Portrait_Zag_Empathetic_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "Hey, uh, Father. I thought, maybe, you know, here." },
				{ Cue = "/VO/Hades_0090",
					Emote = "PortraitEmoteSurprise",
					PreLineThreadedFunctionName = "PlayFistSlamAnimFromSource",
					Text = "Where did you get this...? You think you can butter me up with such trifles? Give me that, and then get out of here." },
			},
			HadesGift01_B =
			{
				PlayOnce = true,
				RequiredTextLines = { "Ending01" },
				RequiredFalseTextLines = { "HadesGift01" },
				-- You're welcome, Father.
				EndCue = "/VO/ZagreusHome_0314",
				EndWait = 1.0,
				{ Cue = "/VO/ZagreusHome_0113", Portrait = "Portrait_Zag_Empathetic_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "Hey, uh, Father. I thought, maybe, you know, here." },
				{ Cue = "/VO/Hades_1087",
					Text = "You owe me nothing, certainly not this. And I've nothing to give you in exchange. But fine, I'll honor our custom. Give it here." },
			},
			HadesGift02 =
			{
				PlayOnce = true,
				RequiredAnyTextLines = { "HadesGift01", "HadesGift01_B" },
				{ Cue = "/VO/ZagreusHome_3659", Portrait = "Portrait_Zag_Defiant_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Text = "{#DialogueItalicFormat}Erm{#PreviousFormat}, Father... I know we can't exactly change the past, but we can try to move forward. Look, I know I'm not even supposed to have this stuff, but everyone likes it, won't you try it, at least? Here, take it, either way." },
				{ Cue = "/VO/Hades_1059",
					PreLineWait = 0.4,
					Portrait = "Portrait_Hades_Averted_01",
					Text = "{#DialogueItalicFormat}Hrm{#PreviousFormat}. I... can dispose of this properly. Speaking of disposal... I've no further need for this, here, so... rid me of it, why don't you? On your way out." },
			},
			HadesGift03 =
			{
				PlayOnce = true,
				RequiredTextLines = { "HadesGift02" },
				EndVoiceLines =
				{
					PreLineWait = 0.4,
					UsePlayerSource = true,
					RequiredMinElapsedTime = 3,
					-- Why not!
					{ Cue = "/VO/ZagreusHome_3708" },
				},
				{ Cue = "/VO/ZagreusHome_3660", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Text = "More Nectar for the old disposal service, Father, if you don't mind. Come on, you must have tried the stuff, at least in the days before you were here! Didn't you ever enjoy yourself, at any point?" },
				{ Cue = "/VO/Hades_1060",
					Text = "I don't require beverages in order to enjoy myself, boy. Unlike so many of your kin upon their mountain. Yet this blasted substance somehow finds its way to my domain. Then, fine! You wish for the customs here to be more akin to Olympus, why not?" },
			},
			HadesGift04 =
			{
				PlayOnce = true,
				RequiredTextLines = { "HadesGift03" },
				EndVoiceLines =
				{
					PreLineWait = 0.4,
					UsePlayerSource = true,
					-- Well, with that attitude...!
					{ Cue = "/VO/ZagreusHome_3661" },
				},
				{ Cue = "/VO/ZagreusHome_3254", Portrait = "Portrait_Zag_Defiant_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Text = "Have another bottle, why don't you, Father? Not like it's going to change anything between us, right? All the Nectar in the Underworld can't change what's already happened. Or change yourself." },
				{ Cue = "/VO/Hades_1074",
					Portrait = "Portrait_Hades_Averted_01",
					Text = "{#DialogueItalicFormat}Hrm{#PreviousFormat}. You're probably correct. You must think it is my age that makes me obstinate. Unable to change my nature. But I have always been inflexible. You think I'm proud of it? You call me {#DialogueItalicFormat}'Father' {#PreviousFormat}like it's an insult. I deserve the title only as a technicality, I know." },
				{ Cue = "/VO/ZagreusHome_3255", Portrait = "Portrait_Zag_Defiant_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "You could start being a better father at any point. There's no one stopping you but you." },
				{ Cue = "/VO/Hades_1075",
					Portrait = "Portrait_Hades_Averted_01",
					Text = "Yes, well... unfortunately, I am a significant impediment. You should be well aware of that. {#DialogueItalicFormat}You {#PreviousFormat}were able to stop me. I have thus far been unable to do so..." }, 
			},
			HadesGift05 =
			{
				PlayOnce = true,
				RequiredTextLines = { "HadesGift04" },
				EndVoiceLines =
				{
					PreLineWait = 0.65,
					UsePlayerSource = true,
					-- All right. Well, it's a start.
					{ Cue = "/VO/ZagreusHome_3258" },
				},
				{ Cue = "/VO/Hades_1076",
					Emote = "PortraitEmoteFiredUp",
					Text = "Enough of this, boy! Even mortals know better. I don't want your Nectar, or Ambrosia, or whichever offerings. You owe me no such thing, and I shall not accept it any longer. In fact, here... you shall take all of it back!" },				
				{ Cue = "/VO/ZagreusHome_3256", Portrait = "Portrait_Zag_Defiant_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Text = "Why? It's just Nectar, Father, lighten up. I get that you're surly about everything, but please at least try toward decency with me! If not for me, then for Mother." },
				{ Cue = "/VO/Hades_1077",
					PreLineThreadedFunctionName = "PlayFistSlamAnimFromSource",
					Emote = "PortraitEmoteAnger",
					Text = "How dare you lecture me? I mean... {#DialogueItalicFormat}urgh{#PreviousFormat}... look. As you told me yourself... there's no changing what already transpired. And our memories are warnings. When you have lived as long as I have... you come to understand your weaknesses." },
				{ Cue = "/VO/ZagreusHome_3257", Portrait = "Portrait_Zag_Defiant_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "What are you getting at? You're just going to give up, stick with what's comfortable for you instead of making any real effort at making peace with your son?" },
				{ Cue = "/VO/Hades_1078",
					Portrait = "Portrait_Hades_Averted_01",
					PostLineThreadedFunctionName = "MaxedRelationshipPresentation",
					PostLineFunctionArgs = { Text = "NPC_Hades_01", Icon = "Keepsake_HadesSticker_Max", DoHadesRefund = true },
					Text = "{#DialogueItalicFormat}Rngh{#PreviousFormat}. Listen to me. My attempts at making peace are going to be rather subtle for your tastes. I cannot change the past. And there is only so much I can do about the future. I tell you this because... I shall not lie to you again. That much, I swear." },
			},
		},

