				-- ...Right.
				EndCue = "/VO/ZagreusHome_2134",
				EndWait = 0.5,
				{ Cue = "/VO/Hades_0825", PauseMusic = true,
					FadeOutIds = { 488651 },
					Text = "Well? What is the latest explanation for your tardiness? Lost track of time admiring the courtyard view? Perhaps you were recuperating from your martial training. Achilles is not prone to holding back." },
				{ Cue = "/VO/ZagreusHome_2133", Portrait = "Portrait_Zag_Defiant_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusInteractionThoughtful", PreLineAnimTarget = "Hero",
					Text = "I... I'm sorry, Father, I just overslept. I must have overslept. How am I even supposed to know exactly when to be here, I mean, every moment every day and night, here, it's all the same!" },
				{ Cue = "/VO/Hades_0826",
					Emote = "PortraitEmoteAnger",
					Text = "It's {#DialogueItalicFormat}not {#PreviousFormat}at all the same! The hue of Ixion's green light takes on an obviously lighter hue when it is time! You're an embarrassment not just to me, here, boy, but to this entire operation. Now get back to work!" },
				-- OnQueuedFunctionName = "CheckDistanceTriggerThread",
				-- OnQueuedFunctionArgs = PresetEventArgs.NyxFlashback,
			},
			Hades_Flashback_DayNightJob_02 =
			{
				Priority = true,
				PlayOnce = true,
				InitialGiftableOffSource = true,
				GiftableOffSource = true,
				UseableOffSource = true,
				RequiredTrueFlags = { "InFlashback", },
				RequiredTextLines = { "Flashback_DayNightJob_Office_Conclusion_01" },
				UseInitialInteractSetup = true,
				IgnoreSourceStartEndAnimations = true,
				{ Cue = "/VO/Hades_0827",
					Text = "Not only were you late, again... but then you also failed to perform even the simplest tasks that I had asked, repeatedly, that you complete. How many chances do you need at this? I am too lenient with you." },
				{ Cue = "/VO/ZagreusHome_2135", Portrait = "Portrait_Zag_Defiant_01", Speaker = "CharProtag",
					Emote = "PortraitEmoteSurprise",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Text = "Too lenient? Instead of lambasting me nonstop, have you considered, maybe, training me some more to do this fascinating work to your satisfaction? You'll forgive me if I didn't memorize every laborious detail the last time you performed your monologue about how all this works." },
				{ Cue = "/VO/Hades_0828",
					Emote = "PortraitEmoteAnger",
					Text = "{#DialogueItalicFormat}Excuse me{#PreviousFormat}?! If I am not mistaken, you're deflecting your incompetence toward {#DialogueItalicFormat}me{#PreviousFormat}? As though the Goddess of Wisdom herself has come to aid you in your time of need! I could replace you here and now with any number of simple shades, hungry to work here!" },
				{ Cue = "/VO/ZagreusHome_2136", Portrait = "Portrait_Zag_Defiant_01", Speaker = "CharProtag",
					Emote = "PortraitEmoteFiredUp",
					PreLineAnim = "ZagreusCosmeticPurchase", PreLineAnimTarget = "Hero",
					Text = "Then why don't you?! I don't want any of this. I'm not the one who drew the short lot with your brothers and got stuck here forever!! Why am I having to do this senseless parchment-pushing for you?" },
				{ Cue = "/VO/Hades_0829", PostLineFunctionName = "ConcludeFlashback",
					PreLineThreadedFunctionName = "PowerWordPresentationFlashback", PreLineThreadedFunctionArgs = { WaitTime = 3.3 },
					Text = "Why are you stuck? You're not. You're {#DialogueItalicFormat}fired{#PreviousFormat}! I was wrong to think you were prepared to take on any responsibility at all. And, don't you ever invoke your uncles here again. Or I'll have you cast into the lowest pits of Tartarus. Now, get out of my administration, and my sight!" },
