					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					Text = "Guess what, boy? Made it all the way to Asphodel last time! I fully understand how come you never liked it there. Now to trek on back, I guess. Take care!" },
				{ Cue = "/VO/Storyteller_0166", PreLineWait = 1.0,
					StartSound = "/VO/CerberusWhineSad",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero", IsNarration = true,
					Text = "{#DialogueItalicFormat}Despite the Underworld Prince's optimism, his old companion Cerberus, the multi-headed hound, maintains a somewhat sulky attitude for now." },
			},
			CerberusRunProgress03 =
			{
				PlayOnce = true,
				UseableOffSource = true,
				RequiredRoomThisRun = "D_Hub",
				RequiredTextLines = { "CerberusMiscMeeting01" },
				UseInitialInteractSetup = true,
				StatusAnimation = false,
				EndVoiceLines =
				{
					PreLineWait = 0.45,
					UsePlayerSource = true,
					RequiredMinElapsedTime = 3,
					-- See you, boy.
					{ Cue = "/VO/ZagreusHome_3049" },
				},
				{ Cue = "/VO/ZagreusHome_0952", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "Hey good to see you back here, boy. I'm glad you're back on guard duty! Even if it means having to find a way past you. I know you're just doing your job." },
				{ Cue = "/VO/Storyteller_0259", PreLineWait = 1.0,
					StartSound = "/VO/CerberusWhineSad",
					IsNarration = true,
					Text = "{#DialogueItalicFormat}The fear-inducing hellhound eyes the Underworld Prince uneasily, his six ferocious eyes each glistening within the dim light of the House." },
			},
			CerberusAboutFastTravel01 =
