				PlayOnce = true,
				UseableOffSource = true,
				UseInitialInteractSetup = true,
				RequiredRoomLastRun = "D_Hub",
				RequiredTextLines = { "CerberusRunProgress03" },
				StatusAnimation = false,
				EndVoiceLines =
				{
					PreLineWait = 0.35,
					UsePlayerSource = true,
					RequiredMinElapsedTime = 3,
					-- Hahaha, just kidding, boy.
					{ Cue = "/VO/ZagreusHome_2845" },
				},
				{ Cue = "/VO/ZagreusHome_2844", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "I hope I haven't been inconveniencing you too much with all the visits to the Temple lately, boy. Say, how do you get back? Charon giving you a lift?" },
				{ Cue = "/VO/Storyteller_0361", PortraitExitWait = 1.0,
					StartSound = "/VO/CerberusCuteWhine_2",
					IsNarration = true,
					Text = "{#DialogueItalicFormat}Thus asks the Prince his question of the guardian of the gates of hell, knowing full well the multi-headed monster keeps a wide berth of the river-boatman, as do all with any sense." },
			},

