				StatusAnimation = false,
				EndVoiceLines =
				{
					PreLineWait = 0.35,
					UsePlayerSource = true,
					RequiredMinElapsedTime = 2,
					-- Cheer up, boy, it'll be all right.
					{ Cue = "/VO/ZagreusHome_0436" },
				},
				{ Cue = "/VO/ZagreusHome_0405", Portrait = "Portrait_Zag_Empathetic_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "You really ripped the lounge to pieces, boy? I hope you didn't get yourself in too much trouble. I'm sorry to have worried you like that." },
				{ Cue = "/VO/Storyteller_0193", PortraitExitWait = 1.0,
					StartSound = "/VO/CerberusWhine_1",
					IsNarration = true,
					Text = "{#DialogueItalicFormat}The guilt-wracked monster Cerberus admits to committing the crime, expressing some remorse for having implicated the incendiary Prince.", },
			},
			CerberusLoungeOpen01 =
			{
				Priority = true,
				PlayOnce = true,
				UseableOffSource = true,
				RequiredCosmetics = { "Cosmetic_ClearScratches", "Cosmetic_ClearFur" },
