					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Text = "Hey, did you see, boy? The old lounge is good as new again, you ought to check it out! Just, don't annihilate it like you did last time." },
				{ Cue = "/VO/Storyteller_0194", PortraitExitWait = 1.0,
					StartSound = "/VO/CerberusWhine_8", IsNarration = true,
					Text = "{#DialogueItalicFormat}Each savage countenance of Cerberus lights up to hear the news, doubtless beginning to assemble stratagems for ravaging the lounging area once more." },
			},

			CerberusAboutUnderworldRenovations01 =
			{
				PlayOnce = true,
				UseableOffSource = true,
				RequiredCosmetics = { "GhostAdminDesk" },
				UseInitialInteractSetup = true,
				RequiredAnyCosmetics = { "Cosmetic_KitchenIsland", "Cosmetic_LoungeChairsA", "Cosmetic_LoungeTablesA", "Cosmetic_Aquarium", "Cosmetic_BatCage", },
