				},
				EndVoiceLines =
				{
					PreLineWait = 0.35,
					UsePlayerSource = true,
					RequiredMinElapsedTime = 2,
					-- Let's enjoy it while it lasts.
					{ Cue = "/VO/ZagreusHome_2843" },
				},
				{ Cue = "/VO/ZagreusHome_2842", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
