					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "Hey, boy. I hope all the construction and renovation around here hasn't been too inconvenient for you, lately. But hey, the lounge is just about ready for you to utterly destroy again!" },
				{ Cue = "/VO/Storyteller_0360", PortraitExitWait = 1.0,
					StartSound = "/VO/CerberusExtraGrowl_2", IsNarration = true,
					Text = "{#DialogueItalicFormat}Prince Zagreus implants in the impressionable, vicious watchdog of the Underworld a new appetite for more destruction, to be sated in another age.", },
			},

			CerberusAboutBeingBestBoy01 =
			{
				PlayOnce = true,
				UseableOffSource = true,
				UseInitialInteractSetup = true,
				RequiredMinValues = { NumCerberusPettings = 20 },
				RequiredTextLines = { "CerberusMiscMeeting01", "CerberusGift06" },
				EndVoiceLines =
				{
					PreLineWait = 0.35,
					UsePlayerSource = true,
					TriggerCooldowns = { "PetCerberusSpeechPlayed" },
					-- Speaking of which...!
					{ Cue = "/VO/ZagreusHome_2847", PostLineFunctionName = "PetCerberusThread", },
				},
				{ Cue = "/VO/ZagreusHome_2846", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "You know, after dying in some wretched manner, giving pets to my good boy sure helps me get back on my feet. Thanks for being here for me." },
				{ Cue = "/VO/Storyteller_0370", PortraitExitWait = 1.0, StartSound = "/VO/CerberusWhineHappy", IsNarration = true,
					Text = "{#DialogueItalicFormat}The burning embers in the eyes of monstrous Cerberus, notorious hound of hell, reveal that he, too, is amenable to the pets of which the Underworld Prince speaks, and in fact is deeply grateful for the quantity of them.", },
			},
			CerberusAboutPersephone01 =
			{
				SuperPriority = true,
				PlayOnce = true,
				UseableOffSource = true,
				UseInitialInteractSetup = true,
				RequiredTextLines = { "PersephoneFirstMeeting" },
				RequiredFalseTextLines = { "Ending01" },
				{ Cue = "/VO/ZagreusHome_3552", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "I found her, boy! My mother! Persephone, you remember her, don't you? She was pleased to hear about you. But I couldn't stay for long, and I have to find her again, so... guess I'll see you at the Temple, then?" },
				{ Cue = "/VO/Storyteller_0352", PortraitExitWait = 1.0,
					StartSound = "/VO/CerberusCuteWhine_3",
					IsNarration = true,
					Text = "{#DialogueItalicFormat}The ghoulish features of the much-reviled hound of hell all soften instantly upon the mention of a certain name; briefly, then, the monster is at peace." },
			},
			CerberusPostEnding01 =
			{
				SuperPriority = true,
				PlayOnce = true,
				UseableOffSource = true,
				UseInitialInteractSetup = true,
				RequiredTextLines = { "Ending01" },
				AreIdsAlive = { 555714 },
				EndVoiceLines =
				{
					PreLineWait = 0.4,
					UsePlayerSource = true,
					RequiredMinElapsedTime = 3,
					-- How dare you, sir.
					{ Cue = "/VO/ZagreusHome_0109" },
				},
				{ Cue = "/VO/ZagreusHome_3553", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "Well, look at you, boy! Never seen you quite so perky, relatively speaking of course, big tough guy like yourself. You must have missed Mother just as much as she missed you. Well, you've got plenty of time for catching up on things, now!" },
				{ Cue = "/VO/Storyteller_0353", PortraitExitWait = 1.0, StartSound = "/VO/CerberusWhine_7",
					IsNarration = true,
					Text = "{#DialogueItalicFormat}The monstrous triple-headed beast of Hades would perhaps be overcome with pure emotion by the joyous circumstances, were he only capable of this." },
			},
			CerberusPostEnding02 =
			{
				PlayOnce = true,
				UseableOffSource = true,
				UseInitialInteractSetup = true,
				RequiredTextLines = { "CerberusPostEnding01" },
				AreIdsAlive = { 555714 },
				EndVoiceLines =
				{
					PreLineWait = 0.4,
					UsePlayerSource = true,
					RequiredMinElapsedTime = 3,
					-- Hey!
					{ Cue = "/VO/ZagreusHome_3697" },
				},
				{ Cue = "/VO/ZagreusHome_3696", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "Bet you're happy to see Mother home again, huh, boy? Only wish you could have told me about her before." },
				{ Cue = "/VO/Storyteller_0389", PortraitExitWait = 1.0, StartSound = "/VO/CerberusWhine_7",
					IsNarration = true,
					Text = "{#DialogueItalicFormat}The hell-born monster Cerberus contentedly ignores the Prince's callous, inconsiderate remark." },
			},

