		GiftTextLineSets =
		{
			-- grants a gift
			CerberusGift01 =
			{
				PlayOnce = true,
				PreEventFunctionName = "PetCerberusThread",
				{ Cue = "/VO/ZagreusHome_0112", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					Text = "Look what I got for you, boy...!" },
				{ Cue = "/VO/Storyteller_0152", PreLineWait = 1.0, StartSound = "/VO/CerberusWhine_4", IsNarration = true,
					Text = "{#DialogueItalicFormat}As one of Cerberus' heads receives the Prince's gift, another muzzles something back into his hand.", },
			},
			CerberusGift02 =
			{
				PlayOnce = true,
				PreEventFunctionName = "PetCerberusThread",
				RequiredTextLines = { "CerberusGift01", },
				{ Cue = "/VO/ZagreusHome_0185", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					Text = "There's a good boy... thought you'd like that!" },
				{ Cue = "/VO/Storyteller_0149", PreLineWait = 1.0, StartSound = "/VO/CerberusCuteWhine_3", IsNarration = true,
					Text = "{#DialogueItalicFormat}Cerberus is overjoyed to have received such a delicious treat.", },
			},
			CerberusGift03 =
			{
				PlayOnce = true,
				PreEventFunctionName = "PetCerberusThread",
				RequiredTextLines = { "CerberusGift02", },
				{ Cue = "/VO/ZagreusHome_0186", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					Text = "Only the very best treats for my best friend, right, boy?" },
				{ Cue = "/VO/Storyteller_0150", PreLineWait = 1.0, StartSound = "/VO/CerberusWhine_7", IsNarration = true,
					Text = "{#DialogueItalicFormat}Cerberus yawlps in merriment upon receiving such a generous offering.", },
			},
			CerberusGift04 =
			{
				PlayOnce = true,
				PreEventFunctionName = "PetCerberusThread",
				RequiredTextLines = { "CerberusGift03", },
				{ Cue = "/VO/ZagreusHome_0515", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					Text = "It is my humble duty to oblige my favorite boy with all the treats as I am able to procure." },
				{ Cue = "/VO/Storyteller_0151", PreLineWait = 1.0, StartSound = "/VO/CerberusWhine_2", IsNarration = true,
					Text = "{#DialogueItalicFormat}Knowing the watch-dog's never-ending appetite for treats, the Prince obliges him.", },
			},
			CerberusGift05 =
			{
				PlayOnce = true,
				PreEventFunctionName = "PetCerberusThread",
				RequiredTextLines = { "CerberusGift04", },
				{ Cue = "/VO/ZagreusHome_2848", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					Text = "You like this stuff, don't you, boy? Well, good, because I like sneaking it over to you!" },
				{ Cue = "/VO/Storyteller_0355", PreLineWait = 1.0, StartSound = "/VO/CerberusWhineHappy", IsNarration = true,
					Text = "{#DialogueItalicFormat}The delectable taste of Nectar is enough to briefly sate even the ferocious appetite of the hound of hell.", },
			},
			CerberusGift06 =
			{
				PlayOnce = true,
				PreEventFunctionName = "PetCerberusThread",
				RequiredTextLines = { "CerberusGift05", },
				EndVoiceLines =
				{
					PreLineWait = 0.35,
					ObjectType = "NPC_Cerberus_01",
					{ Cue = "/VO/CerberusWhineSad" },
				},
				{ Cue = "/VO/ZagreusHome_2849", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					Text = "This Nectar not fancy enough for you, boy, is that it? You've developed quite the delicate palate at your distinguished age!" },
				{ Cue = "/VO/Storyteller_0356", PreLineWait = 1.0, StartSound = "/VO/CerberusWhine_3", IsNarration = true,
					Text = "{#DialogueItalicFormat}Though having little appetite remaining for the lightness of Nectar's flavor, the multi-headed monster of the Underworld nevertheless accepts.", },
			},
			CerberusGift07 =
			{
				PlayOnce = true,
				PreEventFunctionName = "PetCerberusThread",
				RequiredTextLines = { "CerberusGift06", },
				{ Cue = "/VO/ZagreusHome_2850", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					Text = "That's right, boy... Ambrosia, just for you. You're going to love it. Pretty much every creature, living or dead, does!" },
				{ Cue = "/VO/Storyteller_0357", PreLineWait = 1.0, StartSound = "/VO/CerberusWhineSad", IsNarration = true,
					Text = "{#DialogueItalicFormat}The six eyes of the hideous hound of hell go wide at once from having received the exquisite gift of rare Olympian Ambrosia. The creature is rather beside himself.", },
			},
			CerberusGift08 =
			{
				PlayOnce = true,
				PreEventFunctionName = "PetCerberusThread",
				RequiredTextLines = { "CerberusGift07", },
				{ Cue = "/VO/ZagreusHome_2851", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					Text = "Hits the spot, doesn't it, boy? I know you're caught up in the middle of all this and having to weigh your loyalties more than you'd like. I'm sorry we're putting you through this." },
				{ Cue = "/VO/Storyteller_0358", PreLineWait = 1.0, StartSound = "/VO/CerberusWhine_8", IsNarration = true,
					Text = "{#DialogueItalicFormat}The Underworld Prince's next attempt to bribe the monstrous triple-headed hellhound into further liking him proves once again successful, as the kingly gift of heavenly Ambrosia is beyond compare.", },
			},
			CerberusGift09 =
			{
				PlayOnce = true,
				PreEventFunctionName = "PetCerberusThread",
				RequiredTextLines = { "CerberusGift08", },
				{ Cue = "/VO/ZagreusHome_2852", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					Text = "Cerberus, not to get all sentimental on you, but... oh, who am I kidding, look... you've been with me my entire life! I know you're here at home partly because of me, aren't you?" },
				{ Cue = "/VO/Storyteller_0359", PreLineWait = 1.0, StartSound = "/VO/CerberusCuteGrowl_2", IsNarration = true,
					Text = "{#DialogueItalicFormat}The bond between a hound and its master is as hard as adamant. Cerberus, the fearsome monster born of hell, and guardian of its gates, answers firstly to the Prince's father. But it turns out, the Prince may be the next best thing.", },
				{ Cue = "/VO/ZagreusHome_2853", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PostLineThreadedFunctionName = "MaxedRelationshipPresentation",
					PostLineFunctionArgs = { Text = "NPC_Cerberus_01", Icon = "Keepsake_CerberusSticker_Max" },
					Text = "You're the best boy in the whole world. Even if heads Beta and Gamma don't much care for pets, do they? I've got more than enough lined up for you, though. Count on it." },
			},

		},

