				{ Cue = "/VO/ZagreusHome_0852", Portrait = "Portrait_Zag_Empathetic_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "It's just... within Elysium, I happened on a shade. It's your Patroclus. He's all right, but he seems like he's known better times than these. Is there something I should say to him, if I run into him again?" },
				{ Cue = "/VO/Achilles_0108",
					Emote = "PortraitEmoteSurprise",
					Text = "Oh...! Well, then, please send him my regards. And... all my love. I think of him always. But I cannot see him any longer. And you mustn't ask me why, do you understand? Now, leave me be, please, lad. I need some time." },
				OnQueuedFunctionName = "CheckDistanceTriggerThread",
				OnQueuedFunctionArgs = PresetEventArgs.AchillesGreeting,
			},

			-- alt below
			AchillesAboutPatroclus02 =
			{
				SuperPriority = true,
				PlayOnce = true,
				UseableOffSource = true,
				RequiredAnyTextLines = { "AchillesAboutPatroclus01A", "AchillesAboutPatroclus01B", "AchillesAboutPatroclus01C" },
				RequiredFalseTextLines = { "AchillesAboutPatroclus02B" },
				RequiredFalseTextLinesThisRun = { "PersephoneMeeting08" },
				RequiredFalseFlags = { "InFlashback" },
