			AchillesAboutPatroclus03 =
			{
				PlayOnce = true,
				UseableOffSource = true,
				RequiredTextLines = { "PatroclusAboutAchilles02" },
				RequiredAnyTextLines = { "AchillesAboutPatroclus02", "AchillesAboutPatroclus02B" },
				RequiredFalseFlags = { "InFlashback" },
				-- ...I do. I think.
				EndCue = "/VO/ZagreusHome_0859",
				EndWait = 0.65,
				{ Cue = "/VO/ZagreusHome_0857", Portrait = "Portrait_Zag_Empathetic_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "You have to tell me more about this pact you signed, Achilles. You and Patroclus... so, you're doomed to never be together, then?" },
				{ Cue = "/VO/Achilles_0112",
					Text = "You must safeguard the details of it, lad. Although it's not as bad as you make out. I ensured Patroclus is provided for. We wouldn't have been together anyway. Initially he wasn't sent into Elysium, you see." },
				{ Cue = "/VO/ZagreusHome_0858", Portrait = "Portrait_Zag_Serious_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Text = "There has to be a way for me to make this right. Not just for him, for you." },
				{ Cue = "/VO/Achilles_0113",
					Text = "You've other, pressing matters to attend to, lad. And Patroclus and I have ample time to wait this out. I know you mean well, but... do not discuss this matter with your father, now. I fear that it could make things worse. Do you understand?" },
				OnQueuedFunctionName = "CheckDistanceTriggerThread",
				OnQueuedFunctionArgs = PresetEventArgs.AchillesGreeting,
			},

			AchillesAboutPatroclus04 =
			{
				PlayOnce = true,
				UseableOffSource = true,
				RequiredAnyTextLines = { "PatroclusAboutAchilles03A", "PatroclusAboutAchilles03B" },
				RequiredFalseFlags = { "InFlashback" },
				-- He shouldn't, sir. And neither should you.
				EndCue = "/VO/ZagreusHome_0883",
				EndWait = 0.65,
				{ Cue = "/VO/ZagreusHome_0881", Portrait = "Portrait_Zag_Empathetic_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "The... fallen warrior in Elysium we spoke of. He seems like a good man, though... was he always the despondent sort?" },
				{ Cue = "/VO/Achilles_0116",
					Text = "No, lad. I was that sort, I think, but he was not. He was... one of the only men I knew who did not look upon me as a threat. He saw something in me I never knew was there. And in turn, with him, I felt... how to describe it? Calm. Whole." },
				{ Cue = "/VO/ZagreusHome_0882", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Text = "{#DialogueItalicFormat}Hah{#PreviousFormat}! I'm restless all the time. I can only imagine." },
				{ Cue = "/VO/Achilles_0117",
					Text = "He had this wry humor about him. Like he was wise beyond his years. A quality I trust shall see him through his struggles now. Although... I hoped residing in Elysium would mean he wouldn't have to struggle any longer." },
				OnQueuedFunctionName = "CheckDistanceTriggerThread",
				OnQueuedFunctionArgs = PresetEventArgs.AchillesGreeting,
			},

			AchillesAboutPatroclusBackstory01 =
			{
				Priority = true,
				PlayOnce = true,
				UseableOffSource = true,
				RequiredTextLines = { "PatroclusAboutAchilles04" },
				RequiredFalseQueuedTextLines = { "PersephoneWithNyx02" },
				EndVoiceLines =
				{
					PreLineWait = 0.4,
					UsePlayerSource = true,
					-- ...Of course.
					{ Cue = "/VO/ZagreusHome_2556" },
				},
				{ Cue = "/VO/ZagreusHome_2553", Portrait = "Portrait_Zag_Serious_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "I know that it's a painful subject, sir, but can you tell me... what exactly happened, in the war that claimed Patroclus and yourself? He brought it up. Said you refused to fight." },
				{ Cue = "/VO/Achilles_0197",
					PreLineWait = 0.5,
					PreLineAnim = "AchillesIdleGreeting",
					PostLineThreadedFunctionName = "LoungeRevelryPresentation",
					PostLineFunctionArgs = { Sound2 = "/EmptyCue", Sound3 = "/EmptyCue" },
					PortraitExitAnimation = "Portrait_MaleGhost_Default_01_Exit",
					Text = "Until he died. I did refuse to fight, until he died, but at that point... {#DialogueItalicFormat}ungh{#PreviousFormat}, look, lad, if we're going to discuss these matters, best we do so in the lounge, not here." },
				{ Cue = "/VO/Achilles_0198", PreLineWait = 0.35, FadeOutTime = 0.5, FullFadeTime = 7.8, FadeInTime = 2.0, FadeInSound = "/Leftovers/World Sounds/MapZoomInShortHigh", TeleportToId = 556834, AngleTowardTargetId = 556835, TeleportHeroToId = 556835, AngleHeroTowardTargetId = 556834,
					Text = "...The fool of a king I served... I'd never take up arms, for him. But, Patroclus, he always had a different way of looking at things. As I refused to fight, he rose to the occasion. Took up my armor, led my brothers into battle, in my guise. Fought nobly... but then, he... well." },
				{ Cue = "/VO/ZagreusHome_2554", Portrait = "Portrait_Zag_Serious_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusInteractionThoughtful", PreLineAnimTarget = "Hero",
					Text = "And after you found out... you sought vengeance for him. Took vengeance." },
				{ Cue = "/VO/Achilles_0199",
					Text = "That I did. They got me, finally, of course. But, not before I broke them, first. The war soon ended, after that. It is an awful contradiction, that to quickly end war, one need only extinguish many lives at once. I had not realized it at the time." },
				{ Cue = "/VO/ZagreusHome_2555", Portrait = "Portrait_Zag_Empathetic_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "Did you... allow yourself to die? So that you could see Patroclus again, here?" },
				{ Cue = "/VO/Achilles_0200",
					PreLineWait = 0.35,
					PreLineAnim = "AchillesIdleGreeting",
					PortraitExitAnimation = "Portrait_MaleGhost_Default_01_Exit",
					Text = "...I don't know. Maybe. I only know that I was filled with rage. Rage that subsided, soon after I arrived here. I felt... empty. I never saw Patroclus again. But, I learned my brothers honored my last request, as my ashes were laid to rest with his. {#DialogueItalicFormat}<Sigh> {#PreviousFormat}Anyway...! Appreciate the drink." },
				{ Cue = "/VO/Achilles_0201", PreLineWait = 0.55, FadeOutTime = 0.5, FullFadeTime = 0.5, TeleportToId = 391565, AngleTowardTargetId = 421072, TeleportHeroToId = 391570, AngleHeroTowardTargetId = 391565, FadeInTime = 0.5,
					FadeInSound = "/Leftovers/World Sounds/MapZoomInShortHigh",
					Text = "Suppose I'd best be getting back to standing quietly about. Though, that war, lad? Don't ask me about it again. All right?" },
			},

			AchillesAboutPatroclus05 =
			{
				Priority = true,
				PlayOnce = true,
				UseableOffSource = true,
				RequiredTextLines = { "PatroclusAboutAchilles06" },
				MinRunsSinceAnyTextLines = { TextLines = { "PatroclusAboutAchilles06" }, Count = 2 },
				RequiredFalseFlags = { "InFlashback" },
				-- I swear I will.
				EndCue = "/VO/ZagreusHome_2559",
				EndWait = 0.4,
				{ Cue = "/VO/Achilles_0202",
					Text = "How is he, anyway, if you've encountered him of late during your journeys, lad? The fallen warrior you've run into in Elysium, that is." },
				{ Cue = "/VO/ZagreusHome_2557", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "He's doing relatively well, I think. There's a lot you've told me... that he didn't know. About what led up to his present circumstances, and your own." },
				{ Cue = "/VO/Achilles_0203",
					Text = "You told him, then. Without requesting my consent to tell him, first! What, exactly, did you say to him?" },
				{ Cue = "/VO/ZagreusHome_2558", Portrait = "Portrait_Zag_Empathetic_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Text = "Achilles, I... I'm sorry, I... just told him about your ashes being intermingled, that whole thing? And about the pact. You told me once to send him your love and regards! This brought him no joy, only questions! You told me not to speak to Father, not to him!" },
				{ Cue = "/VO/Achilles_0204",
					Text = "{#DialogueItalicFormat}<Sigh>{#PreviousFormat}...You're right, I... should have specified. Or shouldn't have. Because what you said, probably is for the best. I didn't mean to lose my temper with you, lad, it's just... look, thank you for looking out for him. For both of us. Just, please be careful." },
				OnQueuedFunctionName = "CheckDistanceTriggerThread",
				OnQueuedFunctionArgs = PresetEventArgs.AchillesGreeting,
			},
			AchillesAboutMyrmidonReunionQuest01 =
			{
				PlayOnce = true,
				SuperPriority = true,
				UseableOffSource = true,
				RequiredAnyTextLines = { "PatroclusAboutMyrmidonReunionQuest01" },
				RequiredFalseTextLinesThisRun = { "PersephoneMeeting08" },
				RequiredFalseFlags = { "InFlashback" },
				EndWait = 0.4,
				{ Cue = "/VO/ZagreusHome_2560", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "I have a message for you from the fallen warrior over in Elysium, Achilles sir. He says... he simply said to let you know to risk it all." },
				{ Cue = "/VO/Achilles_0205",
					Emote = "PortraitEmoteSurprise",
					Text = "To risk it all. He said to risk it all?" },
				{ Cue = "/VO/ZagreusHome_2561", Portrait = "Portrait_Zag_Empathetic_01", Speaker = "CharProtag",
					Text = "Yes. Is... there something significant about that phrase?" },
				{ Cue = "/VO/Achilles_0206",
					Text = "When last he said to risk it all, to me... I didn't listen to him, then. But should have. I should have." },
				{ Cue = "/VO/ZagreusHome_2562", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					Text = "He's asking you to take another chance." },
				{ Cue = "/VO/Achilles_0207",
					Text = "No... he's giving me another chance, I think. I... leave me lad, for now. Thank you for telling me." },
				OnQueuedFunctionName = "CheckDistanceTriggerThread",
				OnQueuedFunctionArgs = PresetEventArgs.AchillesGreeting,
			},
			AchillesAboutMyrmidonReunionQuest02 =
			{
				Priority = true,
				PlayOnce = true,
				UseableOffSource = true,
				RequiredTextLines = { "AchillesAboutMyrmidonReunionQuest01" },
				RequiredFalseTextLines = { "AchillesAboutMyrmidonReunionQuest03_A", "AchillesAboutMyrmidonReunionQuest03_B", "AchillesAboutMyrmidonReunionQuest03_C" },
				MinRunsSinceAnyTextLines = { TextLines = { "AchillesAboutMyrmidonReunionQuest01" }, Count = 2 },
				RequiredFalseFlags = { "InFlashback" },
				-- Please do.
				EndCue = "/VO/ZagreusHome_2564",
				EndWait = 0.4,
				{ Cue = "/VO/ZagreusHome_2563", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "About risking it all, sir. Did you come to any decision about what that meant? What you should do?" },
				{ Cue = "/VO/Achilles_0208",
					Text = "I'm getting there, lad. In battle, I would operate on instincts, make snap judgments almost instantly. In other matters, though... I need some time. Your patience, please. I'll let you know if I can figure something out." },
				OnQueuedFunctionName = "CheckDistanceTriggerThread",
				OnQueuedFunctionArgs = PresetEventArgs.AchillesGreeting,
			},
			-- alts below
			AchillesAboutMyrmidonReunionQuest03_A =
			{
				Priority = true,
				PlayOnce = true,
				UseableOffSource = true,
				StatusAnimation = "StatusIconWantsToTalkImportant",
				RequiredTextLines = { "AchillesAboutMyrmidonReunionQuest01", "SisyphusLiberationQuestComplete" },
				RequiredAnyTextLines = { "AchillesGift08", "AchillesGift06_A" },
				RequiredFalseTextLines = { "OrpheusAboutSingersReunionQuest01", "AchillesAboutMyrmidonReunionQuest03_B", "AchillesAboutMyrmidonReunionQuest03_C" },
				RequiredCosmetics = { "OfficeDoorUnlockItem" },
				RequiredFalseFlags = { "InFlashback" },
				EndVoiceLines =
				{
					PreLineWait = 0.4,
					ObjectType = "NPC_Achilles_01",
					-- You have my thanks.
					{ Cue = "/VO/Achilles_0214" },
				},
				{ Cue = "/VO/Achilles_0209",
					Text = "I need your help with something, lad. As I'm about to risk it all, I think. Will you hear me out?" },
				{ Cue = "/VO/ZagreusHome_2565", Portrait = "Portrait_Zag_Empathetic_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "Of course, sir, what is it? I'm listening." },
				{ Cue = "/VO/Achilles_0210",
					Text = "The pact I signed with your father. It must be buried somewhere down in the administrative chamber there. I dared not ask that you locate it, out of fear that tampering with it would only make things worse, except..." },
				{ Cue = "/VO/ZagreusHome_2566", Portrait = "Portrait_Zag_Serious_01", Speaker = "CharProtag",
					Text = "...Except that fear is for the weak, Achilles, sir." },
				{ Cue = "/VO/Achilles_0211",
					Text = "Indeed. Besides... your father has been more abiding than I expected with how you were able to nullify the Knave-King's Sentence... so, perhaps my case isn't a lost cause, yet." },
				{ Cue = "/VO/ZagreusHome_2567", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					Text = "I'll speak with Nyx about it the next chance I get. And I'll be careful, sir. I promise you." },
				OnQueuedFunctionName = "CheckDistanceTriggerThread",
				OnQueuedFunctionArgs = PresetEventArgs.AchillesGreeting,
			},
