				},
				{ Cue = "/VO/ZagreusHome_0195", Portrait = "Portrait_Zag_Serious_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					Text = "Nyx, this may be an odd question, but... do you know what the Fates may have in store for me? They are your daughters, right?" },
				{ Cue = "/VO/Nyx_0089",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "The Three Fates are my daughters, yes, my child. However, I am no more privy to their workings than the naive mortals living up above. Not even the Olympians can know the weavings of the Fates." },
			},

			NyxMiscMeeting05 =
			{
				PlayOnce = true,
				RequiredMinNPCInteractions = { NPC_Nyx_01 = 1 },
				RequiredFalseTextLines = { "PersephoneFirstMeeting" },
				UseableOffSource = true,
				RequiredFalseFlags = { "InFlashback", },
				{ Cue = "/VO/Nyx_0002",
					Text = "Darkness guide you, child. You have outgrown this House, of that I am now certain. Should you return again here, I shall keep you safe." },
				{ Cue = "/VO/ZagreusScratch_0014", Portrait = "Portrait_Zag_Serious_01", Speaker = "CharProtag",
					Text = "I won't forget you, Nyx. I swear it." },
			},

			NyxBackstory01 =
			{
				PlayOnce = true,
				UseableOffSource = true,
				RequiredTextLines = { "NyxGift01", },
				RequiredMinNPCInteractions = { NPC_Thanatos_01 = 1, NPC_Hypnos_01 = 1 },
				RequiredAnyOtherTextLines = { "Flashback_Mother_01", "CerberusStyxMeeting01" },
				RequiredFalseFlags = { "InFlashback", },
				EndVoiceLines =
				{
					PreLineWait = 0.35,
					ObjectType = "NPC_Nyx_01",
					-- It is more than all right, my child.
					{ Cue = "/VO/Nyx_0096" },
				},
				{ Cue = "/VO/ZagreusHome_2146", Portrait = "Portrait_Zag_Serious_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusInteractionThoughtful", PreLineAnimTarget = "Hero",
					Text = "Somehow, I always knew. You know... that you were not my birthmother. I mean, look at Hypnos and Thanatos. There's no way that I could have been related to {#DialogueItalicFormat}them{#PreviousFormat}, everybody knew, and yet... when I finally learned for certain, it was still a shock." },
				{ Cue = "/VO/Nyx_0095", Portrait = "Portrait_Nyx_Averted_01",
					Text = "I always knew, in turn, that you would learn the truth of it, eventually. And I was fearful of what would occur after that time arrived. I'm grateful it has come and gone. Though, tell me. I still find I call you child. Is your preference that I cease to use such terms, from this point on?" },
				{ Cue = "/VO/ZagreusHome_2147", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "No, Nyx, please, it's... quite all right. You may not have given birth to me, but you still cared for me. I never doubt that. Likewise, are you sure it's all right that I continue to address you by name?" },
			},
			NyxBackstory02 =
			{
				PlayOnce = true,
				UseableOffSource = true,
				RequiredMinNPCInteractions = { NPC_Nyx_01 = 1 },
				RequiredTextLines = { "ZeusAboutHades01" },
				RequiredFalseTextLines = { "PersephoneMeeting06" },
				RequiredFalseFlags = { "InFlashback", },
				-- Had to ask.
				EndCue = "/VO/ZagreusHome_2198",
				EndWait = 0.4,
				{ Cue = "/VO/ZagreusHome_2197", Portrait = "Portrait_Zag_Serious_01", Speaker = "CharProtag",
					Text = "Nyx, you must know something about why Father broke off ties with the Olympians, don't you?" },
				{ Cue = "/VO/Nyx_0150", Portrait = "Portrait_Nyx_Averted_01",
					Text = "That is a question only your father can answer, child. I cannot tell you more than you already know." },
			},

			NyxAboutHades01 =
			{
				PlayOnce = true,
				UseableOffSource = true,
				RequiredMinNPCInteractions = { NPC_Nyx_01 = 1 },
				RequiredTextLines = { "NyxWithHades01" },
				RequiredFalseFlags = { "InFlashback", },
				EndVoiceLines =
				{
					PreLineWait = 0.4,
					UsePlayerSource = true,
					RequiredMinElapsedTime = 3,
					-- I'm sure we can reconcile our disagreements all the same.
					{ Cue = "/VO/ZagreusHome_2164" },
				},
				{ Cue = "/VO/ZagreusHome_2161", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					Text = "I have to ask you something, Nyx. What is this debt you owe my father, anyway? Forgive my eavesdropping, it's just, I heard the two of you discussing things, and... it came up." },
				{ Cue = "/VO/Nyx_0110", Portrait = "Portrait_Nyx_Averted_01",
					Text = "...I was here long before your father. The responsibility to govern fell to me. Yet, I could not manage on my own. I shape the darkness; ensure the integrity of the domain. I am less prone to census-taking and administering rules. I receded from such responsibilities." },
				{ Cue = "/VO/ZagreusHome_2162", Portrait = "Portrait_Zag_Serious_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusInteractionThoughtful", PreLineAnimTarget = "Hero",
					Text = "It was too much, even for you. So... when father arrived... you welcomed him? He helped?" },
				{ Cue = "/VO/Nyx_0111",
					Text = "I was quite skeptical of him, at first. I thought his presence merely a cruel trick of Fate. But... he worked diligently, from the start. Soon there was order where once there was none. I fashioned this House for him, and I maintain it still. We remain in partnership. In interdependency." },
				{ Cue = "/VO/ZagreusHome_2163", Portrait = "Portrait_Zag_Defiant_01", Speaker = "CharProtag",
					Text = "He has never taken advantage of you? Of your generosity?" },
				{ Cue = "/VO/Nyx_0112",
					Text = "No, child. He has not. He can be short with me, as you have seen. But that is the extent of it. We have found ways to reconcile disagreements. Though, your relationship with him... it is more complicated than my own, I fear..." },
			},

			-- alt below
			NyxAboutHades02 =
			{
				PlayOnce = true,
				UseableOffSource = true,
				RequiredTextLines = { "NyxGrantsRespec", "NyxGift01", "NyxAboutGhostAdmin01" },
				RequiredAnyTextLines = { "Flashback_Mother_01", "CerberusStyxMeeting01" },
