				RequiredFalseTextLines = { "PersephoneFirstMeeting" },
				MinRunsSinceAnyTextLines = { TextLines = { "NyxAboutPersephone01", "NyxAboutPersephone01_B" }, Count = 2 },
				-- Ungh. All right.
