			NyxAboutAres02 =
			{
				PlayOnce = true,
				UseableOffSource = true,
				RequiredAnyTextLines = { "AresAboutNyx02", "AresAboutNyx02_B" },
				RequiredFalseFlags = { "InFlashback", },
