					{ Cue = "/VO/ZagreusHome_2275" },
				},
				{ Cue = "/VO/ZagreusHome_2274", Portrait = "Portrait_Zag_Defiant_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "Hey, Nyx, {#DialogueItalicFormat}erm{#PreviousFormat}, Lord Ares wanted you to know, and I believe I quote, that {#DialogueItalicFormat}'I am often thinking of her here'{#PreviousFormat}. Take that for what it's worth?" },
				{ Cue = "/VO/Nyx_0330",
					Text = "I shall, thank you, my child. If the god of war is thus preoccupied with me, well then, perhaps he shall be slower to send droves of mortals to our doorstep here." },
			},
			NyxAboutAres03 =
			{
				PlayOnce = true,
				UseableOffSource = true,
				RequiredAnyTextLines = { "AresAboutNyx03", "AresAboutNyx03_B" },
				RequiredFalseFlags = { "InFlashback", },
				EndVoiceLines =
				{
					PreLineWait = 0.4,
					UsePlayerSource = true,
