			NyxAboutAres05 =
			{
				PlayOnce = true,
				UseableOffSource = true,
				RequiredAnyTextLines = { "AresAboutNyx05", "AresAboutNyx05_B" },
				RequiredFalseFlags = { "InFlashback", },
				EndVoiceLines =
				{
