			},

			NyxAboutChaos01 =
			{
				Priority = true,
				PlayOnce = true,
				RequiredMinNPCInteractions = { NPC_Nyx_01 = 1 },
				RequiredTextLines = { "ChaosFirstPickUp" },
				RequiredFalseTextLinesThisRun = { "ChaosFirstPickUp" },
 				RequiredFalseFlags = { "InFlashback", },
				RequiredAnyRoomsThisRun = { "RoomSecret01", "RoomSecret02", "RoomSecret03" },
				UseableOffSource = true,
				EndVoiceLines =
				{
					PreLineWait = 0.55,
					UsePlayerSource = true,
					RequiredMinElapsedTime = 3,
					-- I understand. I think.
					{ Cue = "/VO/ZagreusHome_0631" },
				},
				{ Cue = "/VO/ZagreusHome_0089", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					Text = "Nyx, do you know anything about a softly-spoken gentlebeing under the guise of Chaos? They approached me recently, and offered me a Boon, of sorts." },
				{ Cue = "/VO/Nyx_0070",
					PreLineAnim = "ZagreusTalkEmpathy_Return", PreLineAnimTarget = "Hero",
					Text = "Primordial Chaos... indeed, I know them well. They must have taken interest in your quest. I would advise that you beware their offerings, though know that Chaos, whilst a trickster, is not a deceiver." },
			},
			NyxAboutChaos02 =
			{
				Priority = true,
				PlayOnce = true,
				RequiredTextLines = { "NyxAboutChaos01", "NyxGift01", "ChaosGift01" },
				RequiredFalseFlags = { "InFlashback", },
				RequiredAnyRoomsThisRun = { "RoomSecret01", "RoomSecret02", "RoomSecret03" },
				UseableOffSource = true,
				-- Then, where did... never mind.
				EndCue = "/VO/ZagreusHome_0632",
				EndWait = 0.6,
				{ Cue = "/VO/ZagreusHome_0190", Portrait = "Portrait_Zag_Serious_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					Text = "Can you tell me something more of Chaos, Nyx? I... have a lot of questions." },
				{ Cue = "/VO/Nyx_0071",
					PreLineAnim = "ZagreusTalkEmpathy_Return", PreLineAnimTarget = "Hero",
					Text = "My place is not to speak too openly of Chaos, child. It is their own prerogative to tell you of themselves. I can say they are the eldest force within this world. Before them, there was nothing." },
			},
			NyxAboutChaos03 =
			{
				Priority = true,
				PlayOnce = true,
				RequiredTextLines = { "NyxAboutChaos02", "NyxGift02" },
				RequiredAnyTextLines = { "ChaosAboutNyx01", "ChaosAboutNyx01_B", "ChaosAboutNyx01_C", "ChaosDarknessProgress02" },
				RequiredAnyRoomsThisRun = { "RoomSecret01", "RoomSecret02", "RoomSecret03" },
				RequiredFalseFlags = { "InFlashback", },
				UseableOffSource = true,
				-- That's... oh.
				EndCue = "/VO/ZagreusHome_0633",
				EndWait = 0.5,
				{ Cue = "/VO/ZagreusHome_0191", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					Text = "Chaos seems to have taken somewhat of a liking to me, Nyx. They mentioned you." },
				{ Cue = "/VO/Nyx_0072",
					PreLineAnim = "ZagreusTalkEmpathy_Return", PreLineAnimTarget = "Hero",
					Text = "They mentioned me, did they... I thought, perhaps, that they had long forgotten. You should know they are my mother and my father, child. We have not spoken since the dawn of time." },
			},
			NyxAboutChaos04 =
			{
				Priority = true,
				PlayOnce = true,
				RequiredTextLines = { "NyxAboutChaos03", "NyxGift03", "ChaosGift03" },
				RequiredAnyTextLines = { "ChaosAboutNyx01_B", "ChaosAboutNyx01_C" },
				RequiredAnyRoomsThisRun = { "RoomSecret01", "RoomSecret02", "RoomSecret03" },
				-- for backwards compatibility
				RequiredFalseTextLines = { "NyxChaosReunionQuestComplete" },
				RequiredFalseFlags = { "InFlashback", },
				UseableOffSource = true,
				-- I see. I'm sorry to have troubled you.
				EndCue = "/VO/ZagreusHome_0634",
				EndWait = 0.5,
				{ Cue = "/VO/ZagreusHome_0192", Portrait = "Portrait_Zag_Serious_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					Text = "Nyx, I hate to be a bother, but I wanted to ask you about Chaos once again. Won't you at least try to reach out to them?" },
				{ Cue = "/VO/Nyx_0073",
					PreLineAnim = "ZagreusTalkEmpathy_Return", PreLineAnimTarget = "Hero",
					Text = "It is for Chaos to reach out to me if they wish to reacquaint themselves with me. Child, it would please me if we spoke no more of this for now." },
			},
			-- NyxChaosReunionQuest Start
			NyxAboutChaos05 =
			{
				PlayOnce = true,
				Priority = true,
				UseableOffSource = true,
				RequiredTextLines = { "ThanatosGift01", "HypnosGift01", "NyxGift04", "ChaosAboutNyx02" },
				RequiredAnyTextLines = { "ChaosAboutNyx03", "ChaosAboutOlympianReunionQuest01" },
				RequiredLifetimeResourcesSpentMin = { Gems = 500 },
				RequiredFalseTextLinesThisRun = { "ChaosAboutNyx03" },
				RequiredFalseFlags = { "InFlashback", },
				{ Cue = "/VO/Nyx_0182", Portrait = "Portrait_Nyx_Averted_01",
					Text = "Child, I... know not exactly what you did, but... I detected faintly an attempt by Chaos to reach out to me. A message, merely at the edges of my consciousness, yet I felt certain it was there. But I could not discern it, otherwise. What does Chaos want from me?" },
				{ Cue = "/VO/ZagreusHome_2239", Portrait = "Portrait_Zag_Empathetic_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "I... think Chaos wishes to reestablish contact with you, Nyx. The two of you drifted apart from one another, so, I think I can imagine. I lost touch with Thanatos awhile! But that was nothing in comparison to this. Isn't there someway you can reconnect?" },
				{ Cue = "/VO/Nyx_0183",
					Text = "Now that the House is strong, and this realm orderly, I do not know. But I shall think upon it, child. Leave me, please." },
			},
			-- NyxChaosReunionQuest
			NyxAboutChaos06 =
			{
				Priority = true,
				PlayOnce = true,
				UseableOffSource = true,
				StatusAnimation = "StatusIconWantsToTalkImportant",
				RequiredTextLines = { "ChaosAboutNyx04", "NyxAboutChaos05", "NyxGift06", "ChaosGift06", "Inspect_DeathAreaOffice_Teleporter_01" },
				RequiredFalseTextLinesLastRun = { "NyxAboutChaos05" },
				RequiredCosmetics = { "OfficeDoorUnlockItem" },
				RequiredFalseFlags = { "InFlashback", },
				-- I'll do everything I can.
				-- EndCue = "/VO/ZagreusHome_2242",
				-- EndWait = 0.4,
				EndVoiceLines =
				{
					PreLineWait = 0.4,
					ObjectType = "NPC_Nyx_01",
					-- So shall it be.
					{ Cue = "/VO/Nyx_0232" },
				},
				{ Cue = "/VO/Nyx_0184",
					Text = "I know what must be done, regarding Chaos and myself, my child. It is not often that I ask for your assistance, but I need for you to do something for me, if you would hear me out." },
				{ Cue = "/VO/ZagreusHome_2240", Portrait = "Portrait_Zag_Empathetic_01", Speaker = "CharProtag",
					Text = "Of course, Nyx. Anything. What can I do?" },
				{ Cue = "/VO/Nyx_0185",
					Text = "In the administrative chamber, you perhaps recall the Eldest Sigil there. Through it we have access directly to the darkest reaches of the realm. But not to Chaos, anymore. Not currently. Though, this is solvable, I think." },
				{ Cue = "/VO/ZagreusHome_2241", Portrait = "Portrait_Zag_Defiant_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Text = "Wait, the Sigil? But I'm not authorized to use it. It's never worked for me, and I have even less authority around here now." },
				{ Cue = "/VO/Nyx_0186",
					Text = "That you were stripped of your authority is what makes this possible at all. It shall require a significant expenditure of Darkness. Then... the bridge to Chaos may be restored. So that I might travel again to visit them. Will you bring Darkness to the Sigil for me, please? When possible." },
				{ Cue = "/VO/ZagreusHome_2243", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "I'll do everything I can. Hey maybe you can put in a request with the House Contractor for me? Make it official, right?" },
			},
			-- NyxChaosReunionQuest
			NyxAboutChaos07 =
			{
				SuperPriority = true,
				PlayOnce = true,
				UseableOffSource = true,
				GiftableOffSource = true,
				StatusAnimation = "StatusIconWantsToTalkImportant",
				RequiredCosmetics = { "NyxQuestItem" },
				RequiredFalseFlags = { "InFlashback", },
				-- Please do. Until next time, Nyx...!
				EndCue = "/VO/ZagreusHome_2244",
				EndWait = 0.4,
				{ Cue = "/VO/Nyx_0187", PostLineThreadedFunctionName = "NyxExit", PostLineFunctionArgs = { WaitTime = 4, AnimationState = "NPCNyxExited" },
					SetFlagTrue = "NyxChaosReunionInProgress",
					FadeOutIds = { 427230, 427229, 427225, 427226, 427227, 427228 },
					Text = "At last. The bridge to Chaos is restored. Thank you so very much, my child. I shall be going, then. And I shall presently return. Please, have no fear. And now, farewell. I shall bring Chaos your kindest regards." },
			},
			-- NyxChaosReunionQuest Aftermath
			NyxChaosReunionQuestComplete =
			{
				SuperPriority = true,
				PlayOnce = true,
				UseableOffSource = true,
				RequiredTextLines = { "ChaosAboutNyx06", "NyxInChaos01" },
				RequiredFalseFlags = { "InFlashback", },
				-- I see. Well, take care of each other, please.
				EndCue = "/VO/ZagreusHome_2248",
				EndWait = 0.4,
				{ Cue = "/VO/ZagreusHome_2246", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Text = "Nyx, you're back! So, how did things with Chaos go? Please tell me it went well?" },
				{ Cue = "/VO/Nyx_0189",
					Text = "It did. It did go well, my child, I... appreciated having had a chance to reconnect with my great ancestor. Thanks to your generosity, to both of us. We had to make up for a great deal of lost time." },
				{ Cue = "/VO/ZagreusHome_2247", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Text = "So what happens now? You're back on speaking terms, then... does it mean Chaos will stop by for a visit soon, something like that?" },
				{ Cue = "/VO/Nyx_0190",
					Text = "Chaos is comfortable in their realm. I may, however, visit every now and then. Keep them apprised of what unfolds up here. Beyond that... Chaos knows better than to exert too much influence upon this realm. Beyond having already formulated it, and everything." },
			},
			NyxChaosReunionQuestCompleteAftermath01 =
			{
				PlayOnce = true,
				UseableOffSource = true,
				RequiredTextLines = { "NyxChaosReunionQuestComplete", "ChaosAboutNyx04" },
				RequiredAnyTextLines = { "NyxInChaos02", "NyxInChaos03", "NyxInChaos04", "NyxInChaos05", "NyxInChaos06", "NyxInChaos07", "NyxInChaos08", "NyxInChaos09", "NyxInChaos10", "NyxInChaos11", "NyxInChaos12" },
				RequiredFalseFlags = { "InFlashback", },
				EndVoiceLines =
				{
					PreLineWait = 0.4,
					UsePlayerSource = true,
					RequiredMinElapsedTime = 3,
					-- Not me!
					{ Cue = "/VO/ZagreusHome_3445" },
				},
				{ Cue = "/VO/ZagreusHome_3443", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Text = "How are you and Chaos getting on, lately, Nyx? Sounds like they expected an awful lot from you before. That you should shine as bright as day, something like that? I can't imagine." },
				{ Cue = "/VO/Nyx_0434", Portrait = "Portrait_Nyx_Averted_01",
					Text = "To shine was never quite my strongest suit. During the dawn of creation, there was much to do. This place... all that you see around you... it was more my leaning. Chaos understands that better, now. As for me, the work helped me to know which limits I have." },
				{ Cue = "/VO/ZagreusHome_3444", Portrait = "Portrait_Zag_Defiant_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "What limits are those? You seem capable of anything. Probably even shining bright." },
				{ Cue = "/VO/Nyx_0435", PreLineAnim = "NyxIdleGreeting",
					Text = "Come, child. We each have our limits." },
			},

			NyxAboutMeg01 =
			{
				PlayOnce = true,
				RequiredMinNPCInteractions = { NPC_Nyx_01 = 1 },
				RequiredTextLines = { "FuryEncounter03" },
				RequiredFalseFlags = { "InFlashback", },
				UseableOffSource = true,
				{ Cue = "/VO/Nyx_0077",
					Text = "The Fury Megaera is under orders to prevent your passage out of Tartarus, my child. Conflict between the two of you is unavoidable. It brings me pain to have to see you fight." },
				{ Cue = "/VO/ZagreusHome_0435", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "Don't say that, Nyx. Besides! Conflict between Megaera and me has always put the spark in our relationship. We're going to be fine." },
			},
			NyxAboutMeg02 =
			{
				PlayOnce = true,
				RequiredTextLines = { "MegaeraGift05", "NyxGift03", },
				RequiredFalseFlags = { "InFlashback", },
				UseableOffSource = true,
				EndVoiceLines =
				{
					PreLineWait = 0.5,
					UsePlayerSource = true,
					RequiredMinElapsedTime = 3,
					-- Respectful. Definitely.
					{ Cue = "/VO/ZagreusHome_2160" },
				},
				{ Cue = "/VO/ZagreusHome_3738", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "Nyx, you've known Megaera practically forever. Well, before my time at least, so could you... tell me more about what she was like, back then?" },
				{ Cue = "/VO/Nyx_0104", Portrait = "Portrait_Nyx_Averted_01",
					Text = "{#DialogueItalicFormat}Ah-hahah{#PreviousFormat}. I am pleased to see the two of you on better terms of late. Megaera always was as she appears. Dutiful and quite particular. Thoughtful and dependable. Respectful." },
			},
			NyxAboutMegRelationship01 =
			{
				PlayOnce = true,
				RequiredTextLines = { "NyxGift06" },
				RequiredAnyTextLines = { "BecameCloseWithMegaera01_BMeg_GoToHer", "BecameCloseWithMegaera01Meg_GoToHer" },
				MinRunsSinceAnyTextLines = { TextLines = { "BecameCloseWithMegaera01_BMeg_GoToHer", "BecameCloseWithMegaera01Meg_GoToHer" }, Count = 2 },
				RequiredFalseTextLines = { "NyxAboutMegRelationship01B" },
				RequiredFalseFlags = { "InFlashback", },
				UseableOffSource = true,
				-- I... thanks.
				EndCue = "/VO/ZagreusHome_2216",
				EndWait = 0.45,
				{ Cue = "/VO/Nyx_0162",
					Text = "It pleases me that you and Megaera have come to deeply understand each other, child. I must admit to you, it was my hope that you and she were capable of this result." },
				{ Cue = "/VO/ZagreusHome_2215", Portrait = "Portrait_Zag_Empathetic_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "You're pleased we're together, Nyx? I thought you'd be concerned that such relationships distract from work responsibilities and such. Although, I'm grateful to have your approval." },
				{ Cue = "/VO/Nyx_0163",
					Text = "I have no fear that Megaera shall be distracted, child. Besides; such matters are more likely to inspire, and to spur greater accomplishment. Although, the greater sense of contentment tends to be a sufficient end unto itself." },
			},
			NyxAboutMegRelationship01B =
			{
				PlayOnce = true,
				RequiredTextLines = { "NyxGift06" },
				RequiredAnyTextLines = { "BecameCloseWithMegaera01Meg_BackOff", "BecameCloseWithMegaera01_BMeg_BackOff" },
				MinRunsSinceAnyTextLines = { TextLines = { "BecameCloseWithMegaera01Meg_BackOff", "BecameCloseWithMegaera01_BMeg_BackOff" }, Count = 2 },
				RequiredFalseTextLines = { "NyxAboutMegRelationship01" },
				RequiredFalseFlags = { "InFlashback", },
				UseableOffSource = true,
				-- It is. You're probably right.
				EndCue = "/VO/ZagreusHome_2218",
				EndWait = 0.4,
				{ Cue = "/VO/Nyx_0162",
					Text = "It pleases me that you and Megaera have come to deeply understand each other, child. I must admit to you, it was my hope that you and she were capable of this result." },
				{ Cue = "/VO/ZagreusHome_2217", Portrait = "Portrait_Zag_Empathetic_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "You're happy Meg and I are friends now, Nyx? I appreciate it, certainly, though... I worry it might get in the way of Meg's ability to do her job." },
				{ Cue = "/VO/Nyx_0164",
					Text = "I have no such fear for either one of you. You both are dedicated to your tasks. I trust your friendship can withstand your confrontations every now and then. For that is why the friendship blossomed, is it not?" },
			},

			NyxAboutThanatos01 =
			{
				PlayOnce = true,
				RequiredMinNPCInteractions = { NPC_Nyx_01 = 1 },
				RequiredTextLines = { "ThanatosFieldFirstMeeting" },
				RequiredFalseTextLines = { "Ending01", "ThanatosGift07_A" },
				RequiredFalseFlags = { "InFlashback" },
				UseableOffSource = true,
				{ Cue = "/VO/ZagreusHome_0248", Portrait = "Portrait_Zag_Serious_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "Nyx... I ran into Thanatos out there. He aided me, although I think he's rather upset. That I took off like this." },
				{ Cue = "/VO/Nyx_0086",
					Text = "Thanatos is worried for you, child. I hope you understand whatever anger you may sense from him is not the feeling at the source. Please forgive his rudeness." },
			},
			NyxAboutThanatos02 =
			{
				PlayOnce = true,
				RequiredFalseFlags = { "InFlashback", },
				RequiredTextLines = { "NyxAboutThanatos01" },
				MaxRunsSinceAnyTextLines = { TextLines = { "ThanatosFieldFirstMeeting" }, Count = 6 },
				RequiredMinNPCInteractions = { NPC_Nyx_01 = 1 },
				RequiredFalseTextLines = { "ThanatosGift05" },
				IsIdAlive = 423052,
				UseableOffSource = true,
				-- Oh...
				EndCue = "/VO/ZagreusHome_0561",
				EndWait = 0.55,
				{ Cue = "/VO/Nyx_0085",
					Text = "Thanatos has finally come home. Have you not seen him, child? He has been very much preoccupied of late. It must have gladdened him to see you once again." },
			},

			NyxAboutThanatosRelationship01 =
			{
				PlayOnce = true,
				RequiredTextLines = { "NyxGift06" },
				RequiredAnyTextLines = { "BecameCloseWithThanatos01Than_GoToHim", "BecameCloseWithThanatos01_BThan_GoToHim" },
				RequiredFalseTextLines = { "NyxAboutThanatosRelationship01B" },
				MinRunsSinceAnyTextLines = { TextLines = { "BecameCloseWithThanatos01Than_GoToHim", "BecameCloseWithThanatos01_BThan_GoToHim" }, Count = 2 },
				RequiredFalseFlags = { "InFlashback", },
				UseableOffSource = true,
				EndVoiceLines =
				{
					PreLineWait = 0.4,
					ObjectType = "NPC_Nyx_01",
					-- Then very well.
					{ Cue = "/VO/Nyx_0235" },
				},
				{ Cue = "/VO/Nyx_0165",
					Text = "You and my son... it pleases me to see the two of you have grown so close of late. Thanatos has always led a solitary life. He never would complain of it, and yet... I know he yearned for closeness, in his way." },
				{ Cue = "/VO/ZagreusHome_2219", Portrait = "Portrait_Zag_Serious_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "Thank you, Nyx. I think we each have harbored feelings for each other for some time. We've finally been true about them." },
				{ Cue = "/VO/Nyx_0166",
					Text = "Then, in this you each achieved what you desired. I ask that you take care of my dear son. I often fear that he shall work himself too hard." },
				{ Cue = "/VO/ZagreusHome_2220", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Text = "{#DialogueItalicFormat}Oh{#PreviousFormat}, he's all right. Though, I'll be sure to keep an eye on him." },
			},
			NyxAboutThanatosRelationship01B =
			{
				PlayOnce = true,
				RequiredTextLines = { "NyxGift06" },
				RequiredAnyTextLines = { "BecameCloseWithThanatos01Than_BackOff", "BecameCloseWithThanatos01_BThan_BackOff" },
				RequiredFalseTextLines = { "NyxAboutThanatosRelationship01" },
				MinRunsSinceAnyTextLines = { TextLines = { "BecameCloseWithThanatos01Than_BackOff", "BecameCloseWithThanatos01_BThan_BackOff" }, Count = 2 },
				RequiredFalseFlags = { "InFlashback", },
				UseableOffSource = true,
				EndVoiceLines =
				{
					PreLineWait = 0.35,
					UsePlayerSource = true,
					-- I will.
					{ Cue = "/VO/ZagreusHome_2222" },
				},
				{ Cue = "/VO/Nyx_0165",
					Text = "You and my son... it pleases me to see the two of you have grown so close of late. Thanatos has always led a solitary life. He never would complain of it, and yet... I know he yearned for closeness, in his way." },
				{ Cue = "/VO/ZagreusHome_2221", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "It's difficult to find a friend down here. Though, I have always had a trusted one in Thanatos, and he in me. I think we have a better understanding of that now." },
				{ Cue = "/VO/Nyx_0167",
					Text = "Then, may the bonds of your friendship last for as long as this House shall reign beneath the earth, here in the dark. Take care of my dear son." },
			},

			NyxAboutDusa01 =
			{
				Priority = true,
				PlayOnce = true,
				RequiredMinNPCInteractions = { NPC_Nyx_01 = 1 },
				RequiredTextLines = { "DusaVsNyx03" },
