				GiftableOffSource = true,
				EndVoiceLines =
				{
					PreLineWait = 0.65,
					UsePlayerSource = true,
					-- Whatever you say...
					{ Cue = "/VO/ZagreusHome_2251" },
				},
				{ Cue = "/VO/Nyx_0191",
					Text = "What is it, child? I sense that your concern is of a different nature than it usually is. Yet, it is veiled to me, somehow." },
				{ Cue = "/VO/ZagreusHome_2249", Portrait = "Portrait_Zag_Empathetic_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "Oh, it's just.... Please don't take this the wrong way, Nyx, but I was worried you were being rather harsh to Dusa. You asked her not to speak to me, at all?" },
				{ Cue = "/VO/Nyx_0192",
					Text = "I asked she mind her own responsibilities. I fear she forgets herself, in this House. Though you have an informal air... you are the Prince! The only son of the Lord Hades, Fate-appointed master of this realm. You shall have the company of gods. Not severed gorgon heads." },
				{ Cue = "/VO/ZagreusHome_2250", Portrait = "Portrait_Zag_Defiant_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Emote = "PortraitEmoteFiredUp",
					Text = "{#DialogueItalicFormat}Pff{#PreviousFormat}! If I'm so important, shouldn't {#DialogueItalicFormat}I {#PreviousFormat}be the one to choose whom I get to interact with around here?" },
				{ Cue = "/VO/Nyx_0193",
					PreLineWait = 0.35,
					Text = "You do not fully know your place, my child. The gorgon-head most certainly does not know hers. Please focus on your task at hand, and leave her be." },
			},
			-- Dusa Fired Quest
			NyxAboutDusa02 =
			{
				PlayOnce = true,
				RequiredTextLines = { "NyxAboutDusa01", "NyxGift06", "DusaGift07" },
				RequiredFalseFlags = { "InFlashback", },
				UseableOffSource = true,
				EndVoiceLines =
				{
					PreLineWait = 0.4,
					UsePlayerSource = true,
					-- She'll be fine. No one works harder than Dusa.
					{ Cue = "/VO/ZagreusHome_2224" },
				},
				{ Cue = "/VO/Nyx_0168",
					Text = "You are consorting with the severed gorgon head, my child. Despite my wishes that you maintain respectful distances between yourselves." },
				{ Cue = "/VO/ZagreusHome_2223", Portrait = "Portrait_Zag_Defiant_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Emote = "PortraitEmoteFiredUp",
					Text = "She's my friend, Nyx. Look... I love you very much, you know I do, except... you cannot choose my friends for me! I can't allow that. I know you don't trust her. Don't you trust me?" },
				{ Cue = "/VO/Nyx_0169", PreLineWait = 0.35, Portrait = "Portrait_Nyx_Averted_01",
					Text = "...I do. Then, very well. But understand that your relationship with her does not preclude the need for her continual improvement with respect to her responsibilities." },
			},
			NyxAboutDusa03 =
			{
				SuperPriority = true,
				PlayOnce = true,
				RequiredTextLines = { "DusaVsNyx04" },
				RequiredFalseFlags = { "InFlashback", },
				UseableOffSource = true,
				EndVoiceLines =
				{
					PreLineWait = 0.4,
					UsePlayerSource = true,
					-- I... rngh.
					{ Cue = "/VO/ZagreusHome_2255" },
				},
				{ Cue = "/VO/ZagreusHome_2252", Portrait = "Portrait_Zag_Defiant_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Text = "What happened with Dusa, Nyx? You really fired her because she talked to me? She was my friend!" },
				{ Cue = "/VO/Nyx_0194",
					Text = "She was removed for insubordination, and repeated overextension and lack of proper judgment, child. Your affinity for her is the reason her removal was as amicable as it was." },
				{ Cue = "/VO/ZagreusHome_2253", Portrait = "Portrait_Zag_Defiant_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusCosmeticPurchase", PreLineAnimTarget = "Hero",
					Emote = "PortraitEmoteFiredUp",
					Text = "Insubordination, and repeated lack of what? She worked harder than anyone here to keep the House neat and orderly! I'd thought you would have valued that!" },
				{ Cue = "/VO/Nyx_0195", Portrait = "Portrait_Nyx_Averted_01",
					Text = "Her eagerness to please was to a fault. Perhaps your Olympian relatives require sacrifices, child, but this House requires efficiency, and nothing more." },
				{ Cue = "/VO/ZagreusHome_2254", Portrait = "Portrait_Zag_Defiant_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusInteractionThoughtful", PreLineAnimTarget = "Hero",
					Text = "{#DialogueItalicFormat}Efficiency{#PreviousFormat}... you know who you sound like right now, don't you, Nyx? I can't believe this." },
				{ Cue = "/VO/Nyx_0196",
					PreLineThreadedFunctionName = "PowerWordPresentation", PreLineThreadedFunctionArgs = { WaitTime = 0.65 },
					Text = "If you are bent on undermining my authority, go do so someplace else." },
			},
			NyxAboutDusa04 =
			{
				SuperPriority = true,
				PlayOnce = true,
				RequiredTextLines = { "NyxAboutDusa03" },
				RequiredFalseTextLinesThisRun = { "PersephoneFirstMeeting", "PersephoneMeeting09", "PersephoneMeeting08", "PersephoneMeeting07" },
				RequiredFalseFlags = { "InFlashback", },
				UseableOffSource = true,
				EndVoiceLines =
				{
					PreLineWait = 0.45,
					UsePlayerSource = true,
					-- Yes. We're understood...
					{ Cue = "/VO/ZagreusHome_2172" },
				},
				{ Cue = "/VO/ZagreusHome_2256", Portrait = "Portrait_Zag_Serious_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusInteractionThoughtful", PreLineAnimTarget = "Hero",
					Text = "Nyx... I'm sorry that I raised my voice with you before, it's just... I cared about Dusa, and I know just how much she cared about working here, with you, and all of us... isn't there some way you would reconsider letting her go?" },
				{ Cue = "/VO/Nyx_0197",
					Text = "I appreciate that you have calmed yourself, my child. As for the gorgon head, this House needs steady-tempered vigilance, not unthinking devotion. Despite repeated feedback, she refused to approach any aspect of her duties with restraint." },
				{ Cue = "/VO/ZagreusHome_2257", Portrait = "Portrait_Zag_Defiant_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusCosmeticPurchase", PreLineAnimTarget = "Hero",
					Emote = "PortraitEmoteFiredUp",
					Text = "Wait, you're saying you fired Dusa for {#DialogueItalicFormat}working too hard{#PreviousFormat}? Did you give her any warning?" },
				{ Cue = "/VO/Nyx_0198",
					Text = "She did not understand how to work properly. Despite all that she did, she ultimately was a burden to this House. There was no warning necessary on my part." },
				{ Cue = "/VO/ZagreusHome_2258", Portrait = "Portrait_Zag_Empathetic_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusInteractionThoughtful", PreLineAnimTarget = "Hero",
					Text = "Please give her another chance, Nyx. She can learn, she just needs to know what she did wrong. She gave this work everything. She just pushed too hard. Shouldn't something like that be forgivable?" },
				{ Cue = "/VO/Nyx_0199", Portrait = "Portrait_Nyx_Averted_01", PreLineWait = 0.4,
					SetFlagFalse = "DusaFiredFromJob",
					SetFlagTrue = "DusaNotYetReHired",
					Text = "...It seems you have more patience than I, child. And, I did not realize you cared. But do not ask again for me to rescind my decision. Are we understood?" },
			},
			NyxAboutDusa05 =
			{
				Priority = true,
				PlayOnce = true,
				RequiredTextLines = { "DusaWithNyx03" },
				RequiredFalseFlags = { "InFlashback", },
				UseableOffSource = true,
				EndVoiceLines =
				{
					PreLineWait = 0.45,
					UsePlayerSource = true,
					RequiredMinElapsedTime = 3,
					-- You'll be impressed. You'll see.
					{ Cue = "/VO/ZagreusHome_3447" },
				},
				{ Cue = "/VO/ZagreusHome_3446", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "You really asked Dusa to come back. Thank you, Nyx! I'm very grateful. And Megaera. Probably everybody here! I'm sure she won't let you down." },
				{ Cue = "/VO/Nyx_0436",
					Text = "I know you care for her, child. And I admit I retain several concerns. Not only about the appropriateness of her conduct, but of her reckless inexperience. I do, however, see the possibility that she can work through these issues. Perhaps with your support." },
			},

			NyxAboutDusaRelationship01 =
			{
				PlayOnce = true,
				RequiredTextLines = { "NyxGift06" },
				RequiredTextLines = { "DusaWithNyx03", "BecameCloseWithDusa01" },
				RequiredFalseTextLinesLastRun = { "BecameCloseWithDusa01" },
				RequiredFalseFlags = { "InFlashback", },
				UseableOffSource = true,
				EndVoiceLines =
				{
					PreLineWait = 0.35,
					UsePlayerSource = true,
					-- I will. She'd do do the same for me.
					{ Cue = "/VO/ZagreusHome_2226" },
				},
				{ Cue = "/VO/Nyx_0170", Portrait = "Portrait_Nyx_Averted_01",
					Text = "At first, I was deeply concerned that you were spending so much time with Dusa, child. It seemed unbecoming for the both of you, and threatened to disrupt our custodian's routine. Except... it pleases me to say that, in this case, I was mistaken about you." },
				{ Cue = "/VO/ZagreusHome_2225", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "Thank you for saying so, Nyx. Though, you mean you were mistaken about me, or about Dusa?" },
				{ Cue = "/VO/Nyx_0171",
					Text = "I was mistaken about both of you. That you would influence her in a harmful way, yet here, in point of fact, she has performed her duties with increasing skill. That she would fail to perform her role, or to learn well from her mistakes. Please do continue to look out for her." },
			},

			NyxRevealsArthurAspect01 =
			{
				Priority = true,
				PlayOnce = true,
				UseableOffSource = true,
				RequiredMinNPCInteractions = { NPC_Nyx_01 = 1 },
				StatusAnimation = "StatusIconWantsToTalkImportant",
				RequiredFalseFlags = { "InFlashback", },
				RequiredTextLines = { "AchillesRevealsGuanYuAspect01" },
				RequiredFalseTextLinesThisRun = GameData.LegendaryAspectIntroTextLines,
				RequiredFalseTextLinesLastRun = GameData.LegendaryAspectIntroTextLines,
				RequiredPlayed = { "/VO/ZagreusHome_2027" },
				RequiredMinSuperLockKeysSpentOnWeapon = { Name = "SwordWeapon", Count = 5 },
				-- I'll take my chances it's the former.
				EndCue = "/VO/ZagreusHome_2188",
				EndWait = 0.4,
				{ Cue = "/VO/Nyx_0144",
					Text = "Hold, child. I have a message for you from my daughters, themselves. Concerning the Blade of the Underworld in your possession." },
				{ Cue = "/VO/ZagreusHome_2186", Portrait = "Portrait_Zag_Empathetic_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Text = "Wait, you mean the Fates? And Stygius? I'm honored that they would have taken any notice of me. What is it that they want for me to know?" },
				{ Cue = "/VO/Nyx_0145",
					Text = "They wish for me to impart to you these words, so that your blade, in turn, may hear them uttered from your lips: {#DialogueItalicFormat}I see your kingly pardon from a prison of stone. {#PreviousFormat}Repeat them, and remember." },
				{ Cue = "/VO/ZagreusHome_2187", Portrait = "Portrait_Zag_Serious_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Text = "{#DialogueItalicFormat}I see your kingly pardon from a prison of stone{#PreviousFormat}... this must be for one of the weapon's hidden forms. Please tell your daughters I am very grateful, Nyx." },
				{ Cue = "/VO/Nyx_0146",
					Text = "I shall, child. Although, I'm certain they already know. Though, whether they are aiding you or this is simply what must come to pass, I cannot say." },
			},
			NyxRevealsArthurAspect02 =
			{
				PlayOnce = true,
				UseableOffSource = true,
				RequiredFalseFlags = { "InFlashback", },
				RequiredTextLines = { "NyxRevealsArthurAspect01" },
				RequiredPlayed = { "/VO/ZagreusHome_2047" },
				-- RequiredLastInteractedWeaponUpgrade = "SwordConsecrationTrait",
				-- Well... thank them for me, please.
				EndCue = "/VO/ZagreusHome_2190",
				EndWait = 0.4,
				{ Cue = "/VO/ZagreusHome_2189", Portrait = "Portrait_Zag_Serious_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Text = "Nyx, the words you told me... when I spoke them to Stygius, the blade revealed to me the form of a great leader among men, taking counsel with his loyal retinue about a circular table. But if his reign is inevitable, why would the Fates need me?" },
				{ Cue = "/VO/Nyx_0161",
					Text = "That I cannot say, child. It is peculiar having daughters who know every outcome that shall come to pass. It puts a strain on our relationship, and... I have learned never to ask they tell me more than they are willing to impart." },
			},

			NyxFlashback01 =
			{
				PlayOnce = true,
				RequiredTrueFlags = { "InFlashback", },
				TeleportToId = 391567,
				AngleTowardTargetId = 427101,
				TeleportOffsetX = -10,
				TeleportOffsetY = 60,
				UseableOffSource = true,
				{ Cue = "/VO/ZagreusHome_0187", Portrait = "Portrait_Zag_Defiant_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "You... you're... Nyx, you're not my mother. All this time, you and my father, you... you lied to me?" },
				{ Cue = "/VO/Nyx_0065", Portrait = "Portrait_Nyx_Averted_01", PostLineFunctionName = "ConcludeFlashback",
					Text = "{#DialogueItalicFormat}Oh{#PreviousFormat}, my dear child! So now you know...? So now you know. But, only half the truth. I can explain the rest, so, please.... It never was my wish to hurt you." },

				OnQueuedFunctionName = "CheckDistanceTriggerThread",
				OnQueuedFunctionArgs = PresetEventArgs.NyxFlashback,
			},

			NyxAboutLegendary01 =
			{
				PlayOnce = true,
				UseableOffSource = true,
				RequiredTextLines = { "NyxGift03" },
				RequiredFalseFlags = { "InFlashback", },
				RequiredAnyTextLines = { "MegaeraGift07", "ThanatosGift07_A", "SisyphusGift07_A", "SkellyGift07", "DusaGift07", "AchillesGift07_A" },
				EndVoiceLines =
				{
					PreLineWait = 0.35,
					RequiredMinElapsedTime = 3,
					UsePlayerSource = true,
					-- And I just adore them!
					{ Cue = "/VO/ZagreusHome_2229" },
				},
				{ Cue = "/VO/ZagreusHome_2227", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Text = "Hey Nyx, I would have you know, I'm now proud owner of my very own Chthonic Companion! You really built them to last. Just... not very many of them, apparently. I'd all but given up on ever finding one." },
				{ Cue = "/VO/Nyx_0172",
					Text = "I sensed this outcome, and it pleases me. Though, you never would have simply discovered such a thing. They each are unique artifacts that must be given willingly if they are to retain their shape, and power." },
				{ Cue = "/VO/ZagreusHome_2228", Portrait = "Portrait_Zag_Defiant_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "I have to say, and I do mean this as a compliment: It's hard for me to imagine Night Incarnate herself fashioning these little keepsakes to be given between friends." },
				{ Cue = "/VO/Nyx_0425",
					Text = "Do not mistake them for their unassuming look. Within them is the strength of an unbreaking bond between two souls conjoined." },
			},
			NyxAboutLegendary02 =
			{
				PlayOnce = true,
				UseableOffSource = true,
				RequiredFalseFlags = { "InFlashback", "DusaFiredFromJob" },
				RequiredTextLines = { "DusaGift07", "NyxAboutLegendary01", "NyxGift05", "DusaAboutLegendary01" },
				EndVoiceLines =
				{
					PreLineWait = 0.35,
					RequiredMinElapsedTime = 3,
					UsePlayerSource = true,
					-- That's very calculating when you put it quite like that...
					{ Cue = "/VO/ZagreusHome_2231" },
				},
				{ Cue = "/VO/ZagreusHome_2230", Portrait = "Portrait_Zag_Defiant_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Text = "Nyx, did you really give Dusa quite possibly the sole surviving epsilon edition Chthonic Companion in the entire Underworld? She's very grateful for it, so much so that she entrusted Fidi to my care. They grow in power that way, right?" },
				{ Cue = "/VO/Nyx_0174", Portrait = "Portrait_Nyx_Averted_01",
					Text = "The severed gorgon head is not without redeeming attributes, my child. Though, I expected this companion would soon find its way to you, the way the two of you have grown attached. Thus, in one action, both of you have found encouragement." },
			},

			NyxWithHades01 =
			{
				Partner = "NPC_Hades_01",
				RequiredMinNPCInteractions = { NPC_Nyx_01 = 1, NPC_Hades_01 = 1 },
				RequiredAnyTextLines = { "Flashback_Mother_01", "CerberusStyxMeeting01" },
				RequiredFalseTextLines = { "Ending01" },
				RequiredFalseTextLinesThisRun = { "PersephoneFirstMeeting" },
				RequiredFalseTextLinesLastRun = { "Flashback_Mother_01", },
				RequiredFalseFlags = { "InFlashback", "DusaNotYetReHired" },
				MinRunsSinceAnyTextLines = { TextLines = GameData.HadesMiscPartnerTextLines, Count = 4 },
				PlayOnce = true,
				UseText = "UseListenNPC",
				BlockDistanceTriggers = true,
				StatusAnimation = false,
				UseableOffSource = true,
				GiftableOffSource = true,
				TeleportToId = 370006,
				TeleportOffsetX = -485,
				TeleportOffsetY = 350,
				AngleTowardTargetId = 370006,
				InteractDistance = 400,
				-- I hate it when they fight.
				EndCue = "/VO/ZagreusHome_0484",
				EndWait = 0.65,
				{ Cue = "/VO/Hades_0092", Portrait = "Portrait_Hades_Default_01", PreLineWait = 0.35, Speaker = "NPC_Hades_01",
					Text = "...You are not to aid him in these matters, Nyx; his foolishness is to be scorned and reprimanded, not indulged. Do we have an understanding?" },
				{ Cue = "/VO/Nyx_0108",
					PreLineAnim = "NyxIdleGreeting",
					Text = "We have an understanding, Hades, yes. He is your own son, after all, and therefore your responsibility. I, in turn, have my commitments to this realm, and to this House. You have aided me in them, so I remain, as ever, in your debt." },
				{ Cue = "/VO/Hades_0215", Portrait = "Portrait_Hades_Default_01", Speaker = "NPC_Hades_01",
					Text = "It is not necessary to remind me. Surely all those who once defied my brothers and myself would much prefer a post within my House over eternity within the lowest depths of Tartarus. Not that the darkness there would trouble you." },
			},
			NyxWithHades02 =
			{
				Partner = "NPC_Hades_01",
				RequiredTextLines = { "NyxWithHades01", "NyxGrantsRespec" },
				RequiredFalseTextLines = { "Ending01" },
				RequiredFalseTextLinesThisRun = { "PersephoneFirstMeeting" },
				RequiredFalseFlags = { "InFlashback", "DusaNotYetReHired" },
				MinRunsSinceAnyTextLines = { TextLines = GameData.HadesMiscPartnerTextLines, Count = 4 },
				RequiredAccumulatedMetaPoints = 1200,
				PlayOnce = true,
				UseText = "UseListenNPC",
				BlockDistanceTriggers = true,
				StatusAnimation = false,
				UseableOffSource = true,
				GiftableOffSource = true,
				TeleportToId = 370006,
				TeleportOffsetX = -485,
				TeleportOffsetY = 350,
				AngleTowardTargetId = 370006,
				InteractDistance = 400,
				-- They're at it again... because of me.
				EndCue = "/VO/ZagreusHome_0485",
				EndWait = 0.9,
				{ Cue = "/VO/Hades_0093", Portrait = "Portrait_Hades_Default_01", PreLineWait = 0.35, Speaker = "NPC_Hades_01",
					Text = "...It is plain to me that you are helping him; in spite of my insistence you do not. What do you expect that I should do about this, Nyx? What would you do, in my position? Deception, in my very house and home...! Answer me." },
				{ Cue = "/VO/Nyx_0109",
					PreLineAnim = "NyxIdleGreeting",
					Text = "Hades, my relationship with him remains unchanged. It is the very same relationship from when you first requested I take care of him. He has my full support, and always shall. You must know this. He wishes now to leave. I shall make no attempt to stop him." },
				{ Cue = "/VO/Hades_0094", Portrait = "Portrait_Hades_Default_01", Speaker = "NPC_Hades_01",
					PreLineAnim = "Hades_HouseFistSlam", PreLineAnimTarget = 370006,
					PostLineThreadedFunctionName = "NyxExit", PostLineFunctionArgs = { WaitTime = 0.3, AnimationState = "NPCNyxExited" },
					Text = "{#DialogueItalicFormat}Tsch{#PreviousFormat}. What good is any one of you? He's not even your son; and yet you coddle him. You know as well as I that he cannot succeed. You fill his mind with harmful delusions. If that is what you want for him, then, please! Proceed at pace." },
			},
			NyxWithHades03 =
			{
				Partner = "NPC_Hades_01",
				RequiredMinNPCInteractions = { NPC_Nyx_01 = 1, NPC_Hades_01 = 1 },
				RequiredMinAnyTextLines = { TextLines = { "AthenaFirstPickUp", "ZeusFirstPickUp", "PoseidonFirstPickUp", "AphroditeFirstPickUp", "ArtemisFirstPickUp", "AresFirstPickUp", "DionysusFirstPickUp", "HermesUpgrade", "DemeterUpgrade" }, Count = 3 },
				RequiredFalseTextLinesLastRun = { "Flashback_Mother_01", },
				RequiredFalseTextLines = { "Ending01" },
				RequiredFalseTextLinesThisRun = { "PersephoneFirstMeeting" },
				RequiredFalseFlags = { "InFlashback", "DusaNotYetReHired" },
				MinRunsSinceAnyTextLines = { TextLines = GameData.HadesMiscPartnerTextLines, Count = 4 },
				RequiredMaxCompletedRuns = 25,
				RequiredSeenRooms = { "B_Intro" },
				PlayOnce = true,
				UseText = "UseListenNPC",
				UseInitialInteractSetup = true,
				BlockDistanceTriggers = true,
				StatusAnimation = false,
				UseableOffSource = true,
				GiftableOffSource = true,
				TeleportToId = 370006,
				TeleportOffsetX = -485,
				TeleportOffsetY = 350,
				AngleTowardTargetId = 370006,
				InteractDistance = 400,
				EndVoiceLines =
				{
					PreLineWait = 0.75,
					ObjectType = "NPC_Hades_01",
					PreLineAnim = "Hades_HouseFistSlam",
					-- Nyx! Gah.
					{ Cue = "/VO/Hades_1083" },
				},
				{ Cue = "/VO/Hades_1081", Portrait = "Portrait_Hades_Default_01", PreLineWait = 0.35, Speaker = "NPC_Hades_01",
					PreLineAnim = "Hades_HouseFistSlam", PreLineAnimTarget = 370006,
					Emote = "PortraitEmoteAnger",
					Text = "{#DialogueItalicFormat}Olympus knows of him{#PreviousFormat}?! Bestowing on him all their worthless blessings, here, within my realm! {#DialogueItalicFormat}Our {#PreviousFormat}realm! Explain to me how, exactly, this is possible, Nyx. Either your limitless power has considerably waned, or you are up to something." },
				{ Cue = "/VO/Nyx_0402",
					PreLineAnim = "NyxIdleGreeting",
					PreLineThreadedFunctionName = "PowerWordPresentation", PreLineThreadedFunctionArgs = { WaitTime = 1.0 },
					Text = "Do not question my power, Hades. Your son was bound to be discovered the closer he came to the surface. The Olympians are pleased to know they have a distant relative, and bid him welcome to their mountain. That is all. Their influence is very limited." },
				{ Cue = "/VO/Hades_1082", Portrait = "Portrait_Hades_Default_01", Speaker = "NPC_Hades_01",
					PreLineAnim = "Hades_HouseFistSlam", PreLineAnimTarget = 370006,
					Emote = "PortraitEmoteFiredUp",
					Text = "Blood and darkness! Do not speak to me about their {#DialogueItalicFormat}influence{#PreviousFormat}! I've seen their influence firsthand. You've made a foolish mistake." },
				{ Cue = "/VO/Nyx_0403",
					PreLineWait = 0.35,
					PostLineThreadedFunctionName = "NyxExit", PostLineFunctionArgs = { WaitTime = 0.3, AnimationState = "NPCNyxExited" },
					Text = "You would speak to me of foolish mistakes? You cannot change the course that has been set. Try all you like." },
			},
			NyxWithHades04 =
			{
				Priority = true,
				PlayOnce = true,
				Partner = "NPC_Hades_01",
				RequiredTextLines = { "HadesAfterPersephoneMeeting01", "PersephoneMeeting04" },
				MinRunsSinceAnyTextLines = { TextLines = GameData.HadesMiscPartnerTextLines, Count = 4 },
				RequiredFalseTextLines = { "Ending01" },
				RequiredFalseFlags = { "InFlashback", "DusaNotYetReHired" },
				UseText = "UseListenNPC",
				BlockDistanceTriggers = true,
				StatusAnimation = false,
				UseableOffSource = true,
				GiftableOffSource = true,
				TeleportToId = 370006,
				TeleportOffsetX = -485,
				TeleportOffsetY = 350,
				AngleTowardTargetId = 370006,
				InteractDistance = 400,
				RequiresRunNotCleared = true,
				EndVoiceLines =
				{
					PreLineWait = 1.0,
					ObjectType = "NPC_Hades_01",
					-- Grr....
					{ Cue = "/VO/Hades_1127" },
				},
				{ Cue = "/VO/Hades_1126", Portrait = "Portrait_Hades_Default_01", PreLineWait = 0.35, Speaker = "NPC_Hades_01",
					PreLineAnim = "Hades_HouseFistSlam", PreLineAnimTarget = 370006,
					Text = "What now, Nyx? Do you realize the danger you have put her in? You claim to care for her, but now you threaten to expose her, to {#DialogueItalicFormat}them{#PreviousFormat}!" },
				{ Cue = "/VO/Nyx_0337",
					PreLineAnim = "NyxIdleGreeting",
					PreLineThreadedFunctionName = "PowerWordPresentation", PreLineThreadedFunctionArgs = { WaitTime = 0.85 },		
					Text = "Hades. When have I even once been unaware of precisely what I mean to do? I shall bring no harm to the Queen, and neither shall your son." },
				{ Cue = "/VO/ZagreusHome_3385", Portrait = "Portrait_Zag_Defiant_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Text = "Harm to the Queen, wait... what are you talking about?" },
				{ Cue = "/VO/Nyx_0338",
					Text = "Child! Zagreus. Do you intend to betray the presence of your birthmother to the Olympians?" },
				{ Cue = "/VO/ZagreusHome_3386", Portrait = "Portrait_Zag_Defiant_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenial_Full", PreLineAnimTarget = "Hero",
					Emote = "PortraitEmoteFiredUp",
					Text = "No! Of course not. She... seems content to be all on her own." },
				{ Cue = "/VO/Hades_1184", Portrait = "Portrait_Hades_Default_01", PreLineWait = 0.35, Speaker = "NPC_Hades_01",
					PreLineAnim = "Hades_HouseFistSlam", PreLineAnimTarget = 370006,
					Text = "You take his word for it? How can you trust him not to ruin everything?" },
				{ Cue = "/VO/Nyx_0339",
					PreLineWait = 0.35,
					PostLineThreadedFunctionName = "NyxExit", PostLineFunctionArgs = { WaitTime = 0.5, AnimationState = "NPCNyxExited" },
					Text = "Precisely how I trusted him to get us to this point." },
			},
			NyxWithHades05 =
			{
				Partner = "NPC_Hades_01",
				PlayOnce = true,
				RequiredTextLines = { "OlympianReunionQuestComplete" },
				RequiredFalseFlags = { "InFlashback", "DusaNotYetReHired" },
				MinRunsSinceAnyTextLines = { TextLines = { "OlympianReunionQuestComplete" }, Count = 3 },
				UseText = "UseListenNPC",
				BlockDistanceTriggers = true,
				StatusAnimation = false,
				UseableOffSource = true,
				GiftableOffSource = true,
				TeleportToId = 370006,
				TeleportOffsetX = -485,
				TeleportOffsetY = 350,
				AngleTowardTargetId = 370006,
				InteractDistance = 400,
				RequiresRunNotCleared = true,
				EndVoiceLines =
				{
					{
						PreLineWait = 0.5,
						ObjectType = "NPC_Nyx_01",
						-- You have my gratitude.
						{ Cue = "/VO/Nyx_0239" },
					},
					{
						PreLineWait = 0.8,
						ObjectType = "NPC_Hades_01",
						-- The honor is mine.
						{ Cue = "/VO/Hades_1129" },
					},
				},
				{ Cue = "/VO/Hades_1128", Portrait = "Portrait_Hades_Default_01", PreLineWait = 0.35, Speaker = "NPC_Hades_01",
					Text = "...What I attempt to tell you, Nyx, is I am grateful. For our long-standing collaboration, here. My rule was thrust upon you and your kin. By all rights, you ought have undermined my every move. I often thought you would. Yet once Olympus and I severed ties... you stood by me still." },
				{ Cue = "/VO/Nyx_0340",
					PreLineAnim = "NyxIdleGreeting",
					Text = "I am the Night Itself, as you well know, Hades. As I declared to you when first we met: I shall stand by your side, so long as you remain the fate-appointed ruler of this realm." },
				{ Cue = "/VO/Hades_1185", Portrait = "Portrait_Hades_Default_01", PreLineWait = 0.35, Speaker = "NPC_Hades_01",
					Text = "I shall require no additional reminders. Know, in turn, that I shall always oversee this realm to the full extent of my ability." },
			},

			NyxWithAchilles01 =
			{
				Partner = "NPC_Achilles_01",
				PlayOnce = true,
				RequiredTextLines = { "NyxFirstMeeting", "NyxGift01", "AchillesGift01", "NyxGrantsRespec" },
				RequiredAnyTextLines = { "AchillesFirstMeeting", "AchillesFirstMeeting_Alt" },
				RequiredFalseTextLines = { "PersephoneFirstMeeting" },
				RequiredFalseFlags = { "InFlashback", "DusaNotYetReHired" },
				RequiredSeenRooms = { "B_Boss01" },
				UseText = "UseListenNPC",
				BlockDistanceTriggers = true,
				UseableOffSource = true,
				GiftableOffSource = true,
				StatusAnimation = false,
				InteractDistance = 400,
				TeleportToId = 370009,
				TeleportOffsetX = 200,
				TeleportOffsetY = 60,
				AngleTowardTargetId = 370009,
				-- My faith is prone to shakiness at times.
				EndCue = "/VO/ZagreusHome_0486",
				EndWait = 0.5,
				{ Cue = "/VO/Achilles_0078", Portrait = "Portrait_MaleGhost_Default_01", PreLineWait = 0.35, Speaker = "NPC_Achilles_01",
					Text = "...I've done the best I could, preparing him. Though, I am fearful it was not enough. No one ever has escaped this place." },
				{ Cue = "/VO/Nyx_0083",
					Text = "Your expertise has readied him, Achilles. Yet I sense it is your confidence in him which spurs him on. Do not lose faith in yourself now, lest soon he do the same." },
			},
			NyxWithAchilles02 =
			{
				PlayOnce = true,
				Partner = "NPC_Achilles_01",
				RequiredTextLines = { "OlympianReunionQuestComplete" },
				RequiredFalseFlags = { "InFlashback", "DusaNotYetReHired" },
				MinRunsSinceAnyTextLines = { TextLines = { "OlympianReunionQuestComplete" }, Count = 2 },
				UseText = "UseListenNPC",
				BlockDistanceTriggers = true,
				UseableOffSource = true,
				GiftableOffSource = true,
				StatusAnimation = false,
				InteractDistance = 400,
				TeleportToId = 370009,
				TeleportOffsetX = 200,
				TeleportOffsetY = 60,
				AngleTowardTargetId = 370009,
				EndVoiceLines =
				{
					PreLineWait = 0.45,
					ObjectType = "NPC_Achilles_01",
					PreLineAnim = "AchillesIdleGreeting",
					-- You are most kind, O Night.
					{ Cue = "/VO/Achilles_0338" },
				},
				{ Cue = "/VO/Achilles_0336", Portrait = "Portrait_MaleGhost_Default_01", PreLineWait = 0.35, Speaker = "NPC_Achilles_01",
					PreLineAnim = "AchillesIdleGreeting", PreLineAnimTarget = 370009,
					Text = "...You raised him well, O Night. You know that, I hope? Though, I can't imagine even you could have expected this outcome. Thank you... for making all this possible. For all of us." },
				{ Cue = "/VO/Nyx_0344",
					Text = "You are to thank for this outcome as much as I. Perhaps more so. For I have little choice but to serve this House. I was created in this realm, and it is everything to me. But you? Your loyalty could not have been assumed." },
				{ Cue = "/VO/Achilles_0337", Portrait = "Portrait_MaleGhost_Default_01", Speaker = "NPC_Achilles_01",
					Text = "{#DialogueItalicFormat}Oh{#PreviousFormat}, I don't know. I felt I had no choice, either, except to serve this House. At least at first. But in training the Prince... preparing him... I felt like I was living for something again. As strange as that may sound." },
				{ Cue = "/VO/Nyx_0345",
					Text = "Nevertheless, given the circumstances of your life and servitude, you need not have given so much of yourself. The greatness of your mortal days followed you into death." },
			},
			NyxWithAchillesAfterMyrmidonReunionQuest01 =
			{
				Partner = "NPC_Achilles_01",
				PlayOnce = true,
				RequiredTextLines = { "AchillesAfterMyrmidonReunionQuestComplete01" },
				RequiredFalseTextLinesThisRun = { "PersephoneFirstMeeting" },
				RequiredFalseFlags = { "InFlashback", "DusaNotYetReHired" },
				UseText = "UseListenNPC",
				BlockDistanceTriggers = true,
				UseableOffSource = true,
				GiftableOffSource = true,
				StatusAnimation = false,
				InteractDistance = 400,
				TeleportToId = 370009,
				TeleportOffsetX = 200,
				TeleportOffsetY = 60,
				AngleTowardTargetId = 370009,
				EndVoiceLines =
				{
					PreLineWait = 0.45,
					ObjectType = "NPC_Achilles_01",
					-- Thank you, O Night.
					{ Cue = "/VO/Achilles_0359" },
				},
				{ Cue = "/VO/Achilles_0358", Portrait = "Portrait_MaleGhost_Default_01", PreLineWait = 0.35, Speaker = "NPC_Achilles_01",
					Text = "...I cannot ever repay you for what you have done, O Night. Know only that it is my honor to continue to serve at your pleasure, here." },
				{ Cue = "/VO/Nyx_0422",
					Text = "I am certain I know nothing of what you mean, Achilles. Though, your continued service here is more than welcome. The honor is mine." },
			},

			NyxWithPersephone01 =
			{
				Priority = true,
				PlayOnce = true,
				Partner = "NPC_Persephone_Home_01",
				RequiredTextLines = { "Ending01", "PersephoneHomeMeeting01" },
				RequiredFalseFlags = { "InFlashback", "DusaNotYetReHired", "PersephoneGoingAway" },
				MinRunsSinceAnyTextLines = { TextLines = { "Ending01" }, Count = 2 },
				UseText = "UseListenNPC",
				BlockDistanceTriggers = true,
				UseableOffSource = true,
				GiftableOffSource = true,
				TeleportToId = 555714,
				TeleportOffsetX = -60,
				TeleportOffsetY = 180,
				AngleTowardTargetId = 555714,
				InteractDistance = 360,
				EndVoiceLines = 
				{
					PreLineWait = 0.6,
					ObjectType = "NPC_Nyx_01",
					PreLineAnim = "NyxIdleGreeting",
					-- I do.
					{ Cue = "/VO/Nyx_0312" },
				},
				{ Cue = "/VO/Nyx_0309",
					PreLineWait = 0.35,
					Text = "...Persephone, my Queen... it has been far too long. Although, the passing of the time was very kind. It is an honor to see you again." },
				{ Cue = "/VO/Persephone_0183", Portrait = "Portrait_Persephone_Queen_01", Speaker = "NPC_Persephone_Home_01",
					PreLineAnim = "PersephoneUnderworld_Vulnerable", PreLineAnimTarget = 555714,
					Text = "Oh, please, Nyx! No formalities between us, right? It's wonderful to see you, too, my friend. The days there on the surface, they were very different, but the nights... I was reminded of our times together, here." },
				{ Cue = "/VO/Nyx_0310",
					Portrait = "Portrait_Nyx_Averted_01",
					PreLineWait = 0.35,
					Text = "Good times, I trust." },
				{ Cue = "/VO/Persephone_0184", Portrait = "Portrait_Persephone_Queen_01", Speaker = "NPC_Persephone_Home_01",
					Text = "Good times, most certainly! I try not to remember the others!" },
				{ Cue = "/VO/Nyx_0311",
					PreLineWait = 0.35,
					Text = "We are pleased to have you back. Although, it shall be a difficult adjustment for you once again, I fear. For all of us." },
				{ Cue = "/VO/Persephone_0185", Portrait = "Portrait_Persephone_Queen_01", Speaker = "NPC_Persephone_Home_01",
					Text = "Well, having built a little house all by myself up there, I think I'm up for anything! You know what it's like." },
			},

