		GiftTextLineSets =
		{
			-- grants a gift
			NyxGift01 =
			{
				PlayOnce = true,
				{ Cue = "/VO/ZagreusHome_0118", Portrait = "Portrait_Zag_Serious_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "{#DialogueItalicFormat}Erm{#PreviousFormat}, Nyx? You've done so much for me, I.. thought you might like this." },
				{ Cue = "/VO/Nyx_0445",
					PreLineAnim = "NyxIdleGreeting",
					Text = "You know your father does not like it when Nectar is doled out on the premises, dear child. However, I am not he... and I wished, regardless, to provide to you a token of my affection." },
			},
			NyxGift02 =
			{
				PlayOnce = true,
				RequiredTextLines = { "NyxGift01" },
				{ Cue = "/VO/Nyx_0045",
					PreLineAnim = "NyxIdleGreeting",
					Text = "How can I possibly accept such generosity, my child? Surely others whom you know are more deserving of such offerings." },
				{ Cue = "/VO/ZagreusHome_0196", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					Text = "Nonsense, Nyx. I want you to have it. You've always cared for me. I can't ever repay you for that." },
			},
			NyxGift03 =
			{
				PlayOnce = true,
				RequiredTextLines = { "NyxGift02" },
				{ Cue = "/VO/Nyx_0046", Portrait = "Portrait_Nyx_Averted_01",
					PreLineAnim = "NyxIdleGreeting",
					Text = "Your kindness toward me is unnecessary, child. You know I have no expectation of such gifts, from you or anyone." },
				{ Cue = "/VO/ZagreusHome_0197", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					Text = "I think of you always, Nyx. You raised me as your own, and I am grateful for it." },
			},
			NyxGift04 =
			{
				PlayOnce = true,
				RequiredTextLines = { "NyxGift03" },
				{ Cue = "/VO/Nyx_0047",
					PreLineAnim = "NyxIdleGreeting",
					Text = "You honor me, my child; although I dread that I am not deserving of such generosity. There is no need to flatter me like this." },
				{ Cue = "/VO/ZagreusHome_0198", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Text = "Be that as it may, Nyx! I would still like for you to have it, with my compliments." },
			},
			NyxGift05 =
			{
				PlayOnce = true,
				RequiredTextLines = { "NyxGift04" },
				EndVoiceLines =
				{
					PreLineWait = 0.35,
					ObjectType = "NPC_Nyx_01",
					-- I am grateful.
					{ Cue = "/VO/Nyx_0238" },
				},
				{ Cue = "/VO/Nyx_0048",
					PreLineAnim = "NyxIdleGreeting",
					Text = "No gift which you could bring me can exceed the value of our kinship, child. Though, it moves me to receive this offering." },
				{ Cue = "/VO/ZagreusHome_3509", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "It's nothing, Nyx, really. I know you've always believed in me, and it's not something I ever take for granted." },
			},
			NyxGift06 =
			{
				PlayOnce = true,
				RequiredTextLines = { "NyxGift05" },
				{ Cue = "/VO/Nyx_0175",
					PreLineAnim = "NyxIdleGreeting",
					Text = "Among my many children, both those born to me and surrogate to me, you are one of the most thoughtful and most generous of all." },
				{ Cue = "/VO/ZagreusHome_2232", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "You have always cared for me and protected me. And given me someone to look up to. This is nothing, Nyx." },
			},

			-- high relationship / locked gifts
			NyxGift07 =
			{
				PlayOnce = true,
				RequiredTextLines = { "NyxGift06" },
				{ Cue = "/VO/ZagreusHome_2233", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "Nyx, if you'll forgive me this transgression for a moment, I would like for you to have this vintage bottle of Ambrosia, please. You can hand it off to Megaera as contraband, but... just in case, I wanted you to have it, first." },
				{ Cue = "/VO/Nyx_0176",
					Text = "{#DialogueItalicFormat}Ah{#PreviousFormat}. A crystal bottle come all the way from the highest peaks of Mount Olympus to these depths within the earth. Your father always said he hated it." },
				{ Cue = "/VO/ZagreusHome_2234", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "Though, Father does have questionable taste." },
				{ Cue = "/VO/Nyx_0177",
					Emote = "PortraitEmoteCheerful",
					PreLineAnim = "NyxIdleGreeting",
					Text = "{#DialogueItalicFormat}Ah, hahahaha{#PreviousFormat}. I accept this gift, with gratitude. This is a delicacy I have not experienced myself. Perhaps I may yet save it for appropriate occasions still to come. Thank you, my child." },
			},
			NyxGift08 =
			{
				PlayOnce = true,
				RequiredTextLines = { "NyxGift07" },
				-- That's quite all right. It never spoils and you have all the time in the world.
				EndCue = "/VO/ZagreusHome_2236",
				EndWait = 0.35,
				{ Cue = "/VO/Nyx_0178",
					Text = "By the way, I meant to tell you, child. The Ambrosia that you gave me? Rather than hold onto it indefinitely, I decided recently to sample it. Your father's claims about its flavor are quite false. I have never tasted anything like it." },
				{ Cue = "/VO/ZagreusHome_2235", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Text = "So glad you liked it, Nyx! And, good timing, too, because... I have for you another bottle, here. They say each one is different in its own respect! And besides, the pleasures of Olympus should be shared. Please, take it, won't you?" },
				{ Cue = "/VO/Nyx_0179",
					PreLineAnim = "NyxIdleGreeting",
					Text = "You are persuasive like your mother, and determined like your father, child. But if you so insist, then I shall not deny myself the simple pleasures of this drink. Though rare is the occasion for it here." },
			},
			NyxGift09 =
			{
				PlayOnce = true,
				RequiredTextLines = { "NyxGift08" },
				EndVoiceLines =
				{
					PreLineWait = 0.35,
					ObjectType = "NPC_Nyx_01",
					-- I love you, too, my child.
					{ Cue = "/VO/Nyx_0181" },
				},
				{ Cue = "/VO/ZagreusHome_2237", Portrait = "Portrait_Zag_Empathetic_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "Nyx, along with giving you this bottle of Ambrosia here, there's something I have meant to say to you. The Three Fates have been very kind to me. Because, in denying me my birthmother... they gave me you. Without your steady presence here, I... can't imagine what it would be like." },
				{ Cue = "/VO/Nyx_0180", Portrait = "Portrait_Nyx_Averted_01",
					PreLineAnim = "NyxIdleGreeting",
					Text = "{#DialogueItalicFormat}Oh{#PreviousFormat}. Then, the Three Fates are whimsical, indeed. When you were born, and I was filled with fear for what it meant for you to be alive, to be responsible for you... I must admit I did not guess that, one evening, you would provide for me both such a gift, and such a sentiment." },
				{ Cue = "/VO/ZagreusHome_2238", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PostLineThreadedFunctionName = "MaxedRelationshipPresentation",
					PostLineFunctionArgs = { Text = "NPC_Nyx_01", Icon = "Keepsake_NyxSticker_Max" },
					Text = "Well, I mean every word. I love you, Nyx." },
			},

		},

