				{ Cue = "/VO/Hypnos_0205",
					PreLineAnim = "HypnosWakeUp",
					Emote = "PortraitEmoteSurprise",
					Text = "{#DialogueItalicFormat}Whaa{#PreviousFormat}! Oh, hah, you just startled me there, is all, I was just... um... I..." },
				{ Cue = "/VO/ZagreusHome_2787", Portrait = "Portrait_Zag_Defiant_01", Speaker = "CharProtag",
