				{ Cue = "/VO/Hypnos_0208",
					PreLineAnim = "HypnosIdleGreeting",
					Emote = "PortraitEmoteCheerful",
					Text = "Oh hey! Um, this might be a little weird, but I, {#DialogueItalicFormat}uh{#PreviousFormat}... thanks! I mean, you talked to Thanatos about me, right? At first I thought he was acting funny. But he has definitely been a lot nicer to me, lately!" },
				{ Cue = "/VO/ZagreusHome_2790", Portrait = "Portrait_Zag_Defiant_01", Speaker = "CharProtag",
					Text = "Come on, Hypnos, I didn't have anything to do with that. You seem like you've been working extra hard, and taking some of the feedback you've been getting to heart. I think Thanatos really appreciates that sort of thing. Who doesn't?" },
				{ Cue = "/VO/Hypnos_0209",
					Emote = "PortraitEmoteSurprise",
					PreLineAnim = "HypnosIdleGreeting",
					Text = "I don't know! But I really appreciate that you went out of your way, I mean... I just stand here and greet and register the dead! But you? You're a real friend!" },
