			HypnosWithHades01 =
			{
				Partner = "NPC_Hades_01",
				PlayOnce = true,
				RequiredFalseTextLines = { "HypnosWithHades02" },
				RequiredMinNPCInteractions = { NPC_Hades_01 = 3, NPC_Hypnos_01 = 10 },
				MinRunsSinceAnyTextLines = { TextLines = GameData.HadesMiscPartnerTextLines, Count = 4 },
				UseText = "UseListenNPC",
				BlockDistanceTriggers = true,
				IgnoreSourceEndTextLinesThreadedFunctionName = true,
				UseableOffSource = true,
				GiftableOffSource = true,
				StatusAnimation = false,
				TeleportToId = 370006,
				TeleportOffsetX = -330,
				TeleportOffsetY = 270,
				AngleTowardTargetId = 370006,
				InteractDistance = 400,
				EndVoiceLines =
				{
					PreLineWait = 0.55,
					UsePlayerSource = true,
					RequiredMinElapsedTime = 3,
					-- You've done it now, Hypnos.
					{ Cue = "/VO/ZagreusHome_0741" },
				},
				{ Cue = "/VO/Hades_0340", Portrait = "Portrait_Hades_Default_01", PreLineWait = 0.35, Speaker = "NPC_Hades_01",
					Text = "...You're late with your report, Hypnos. Again. Again! The dead of course can wait, but I'm less patient. Now, explain yourself to me." },
				{ Cue = "/VO/Hypnos_0112",
					PreLineAnim = "HypnosIdleGreeting",
					Text = "{#DialogueItalicFormat}Oh{#PreviousFormat}! Well, let's see, I woke up, I don't know what time it was, and I made sure that every shade had been accounted for, just like I always do, except I couldn't find my list at first, can you believe it? I thought maybe my brother played a little prank, and so..." },
				{ Cue = "/VO/Hades_0341", Portrait = "Portrait_Hades_Default_01", Speaker = "NPC_Hades_01",
					Emote = "PortraitEmoteAnger",
					PreLineAnim = "Hades_HouseFistSlam", PreLineAnimTarget = 370006,
					Text = "Get to the blasted point already, I don't have all day! Or night. {#DialogueItalicFormat}Urgh{#PreviousFormat}! Your report, where is it?" },
				{ Cue = "/VO/Hypnos_0113", FontSize = 24,
					PreLineAnim = "HypnosIdleGreeting",
					Text = "Sure I was just getting to it, Master! My list, I found it over by the lounge, it was inside, but I am not the one who put it there, no sir! So I've been making positively sure it's not been tampered with, that it has everybody's name, cause of death, place of birth, former body weight, any previous allergies or medications..." },
				{ Cue = "/VO/Hades_0342", Portrait = "Portrait_Hades_Default_01", Speaker = "NPC_Hades_01",
					PreLineThreadedFunctionName = "PowerWordPresentation", PreLineThreadedFunctionArgs = { WaitTime = 0.85 },
					PreLineAnim = "Hades_HouseFistSlam", PreLineAnimTarget = 370006,
					Text = "{#DialogueItalicFormat}Enough{#PreviousFormat}!! Blood and darkness, that's enough from you, just... see to it the ledger of the dead is accurate, and get back to your post. {#DialogueItalicFormat}Ugh{#PreviousFormat}, would that you could be more like your brother." },
			},
			HypnosWithHades02 =
			{
				Partner = "NPC_Hades_01",
				PlayOnce = true,
				RequiredTextLines = { "ThanatosWithHypnos05" },
				MinRunsSinceAnyTextLines = { TextLines = { "ThanatosWithHypnos05" }, Count = 5 },
				UseText = "UseListenNPC",
				BlockDistanceTriggers = true,
				IgnoreSourceEndTextLinesThreadedFunctionName = true,
				UseableOffSource = true,
				GiftableOffSource = true,
				StatusAnimation = false,
				TeleportToId = 370006,
				TeleportOffsetX = -330,
				TeleportOffsetY = 270,
				AngleTowardTargetId = 370006,
				InteractDistance = 400,
				EndVoiceLines =
				{
					PreLineWait = 0.5,
					ObjectType = "NPC_Hypnos_01",
					-- OK!
					{ Cue = "/VO/Hypnos_0219" },
				},
				{ Cue = "/VO/Hades_1079", Portrait = "Portrait_Hades_Default_01", PreLineWait = 0.35, Speaker = "NPC_Hades_01",
					Text = "...Your reports have been much more punctual of late, Hypnos. That is to be expected. However... the illustrated edges... a clever detail. Ensures certain reports are easily procured from a stack of parchment." },
				{ Cue = "/VO/Hypnos_0218",
					PreLineAnim = "HypnosIdleGreeting",
					Text = "Oh, {#DialogueItalicFormat}thanks{#PreviousFormat}, Master, glad you like those! Ever since Thanatos made pretty clear to me I was about to lose my job, I started keeping a list of my {#DialogueItalicFormat}own {#PreviousFormat}tasks, which I sort through each day, deciding on priority, estimating time to completion, cost-benefit analysis..." },
				{ Cue = "/VO/Hades_1080", Portrait = "Portrait_Hades_Default_01", Speaker = "NPC_Hades_01",
					Text = "Yes, well. Certainly, then, you have a number of such tasks requiring your attention. Continue working diligently, then." },
			},
			HypnosWithHades03 =
			{
				Partner = "NPC_Hades_01",
				Priority = true,
				PlayOnce = true,
				RequiredTextLines = { "ThanatosWithHypnos08" },
				MinRunsSinceAnyTextLines = { TextLines = GameData.HadesMiscPartnerTextLines, Count = 4 },
			 	RequiredValues = { CurrentEmployeeOfTheMonth = "Hypnos" },
				UseText = "UseListenNPC",
				BlockDistanceTriggers = true,
				UseableOffSource = true,
				GiftableOffSource = true,
				TeleportToId = 370006,
				TeleportOffsetX = -330,
				TeleportOffsetY = 270,
				AngleTowardTargetId = 370006,
				InteractDistance = 400,
				EndVoiceLines =
				{
					PreLineWait = 0.8,
					ObjectType = "NPC_Hades_01",
					-- No.
					{ Cue = "/VO/Hades_1131" },
				},
				{ Cue = "/VO/Hypnos_0220",
					PreLineAnim = "HypnosIdleGreeting",
					Emote = "PortraitEmoteCheerful",
					Text = "Master, I just can't thank you enough for putting my picture on the board there in the lounge! I heard it's a good picture! I haven't had time to see it yet, but I am going to, it's on the list!" },
				{ Cue = "/VO/Hades_1130", Portrait = "Portrait_Hades_Default_01", Speaker = "NPC_Hades_01",
					Text = "Hypnos... that shall be all. Lest I rescind your commendation and cast you out of here." },
				{ Cue = "/VO/Hypnos_0221", PreLineWait = 0.35,
					Emote = "PortraitEmoteDepressed",
					Text = "....Um, could you say all that again with smaller words, Master, because I thought it maybe sounded kind of {#DialogueItalicFormat}bad{#PreviousFormat}?" },
			},

			HypnosWithNyx01 =
			{
				Partner = "NPC_Nyx_01",
				RequiredTextLines = { "NyxAboutHypnos02", "ThanatosWithHypnos02" },
				MinRunsSinceAnyTextLines = { TextLines = { "NyxAboutDusa04" }, Count = 3 },
