					{ Cue = "/VO/Hypnos_0224" },
				},
				{ Cue = "/VO/Hypnos_0222",
					PreLineAnim = "HypnosIdleGreeting",
					Text = "...I just get sleepy, sometimes, Mom, I mean, I've been working so {#DialogueItalicFormat}much{#PreviousFormat}, lately, just over there? You've probably seen me, greeting shades, making my list, creating a welcoming environment..." },
				{ Cue = "/VO/Nyx_0427", Portrait = "Portrait_Nyx_Default_01", Speaker = "NPC_Nyx_01", PreLineWait = 0.35,
					PreLineAnim = "NyxIdleGreeting", PreLineAnimTarget = 370010,	
					Text = "Remember what we previously spoke of, Hypnos. Do not address me thus whilst we are here. Conduct yourself as befitting a servant of the House of Hades... and, continue practicing your independence, please." },
			},
			HypnosWithNyx02 =
			{
				Partner = "NPC_Nyx_01",
				RequiredTextLines = { "ThanatosWithHypnos07", "HypnosWithNyx01" },
