					{
						PreLineWait = 0.5,
						UsePlayerSource = true,
						-- Good work, Hypnos!
						{ Cue = "/VO/ZagreusHome_3439" },
					},
				},
				{ Cue = "/VO/Nyx_0428", Portrait = "Portrait_Nyx_Default_01", Speaker = "NPC_Nyx_01", PreLineWait = 0.35,
					Text = "You have demonstrated sustained competence in your duties, Hypnos. It gladdens me to see measurable progress. Your efforts are to be commended." },
				{ Cue = "/VO/Hypnos_0225",
					PreLineAnim = "HypnosIdleGreeting",
					Emote = "PortraitEmoteCheerful",
					Text = "Aw, wow, {#DialogueItalicFormat}thanks{#PreviousFormat}! Commended by whom, exactly? Megaera? Thanatos? Dusa, maybe! Not the Master himself?!" },
				{ Cue = "/VO/Nyx_0429", Portrait = "Portrait_Nyx_Default_01", Speaker = "NPC_Nyx_01",
					PreLineAnim = "NyxIdleGreeting", PreLineAnimTarget = 370010,
					Text = "Commended by me. I cannot always look after all of my children, as you know. It falls upon them to take care of themselves, as well as their obligations to our domain. As your mother... it is reassuring to see that you are capable." },
				{ Cue = "/VO/Hypnos_0226",
					PreLineAnim = "HypnosIdleGreeting",
					Text = "Capable? Me? {#DialogueItalicFormat}Aw{#PreviousFormat}, Mom... I mean, um, {#DialogueItalicFormat}Mother Nyx{#PreviousFormat}! Nobody's {#DialogueItalicFormat}ever {#PreviousFormat}called me that before!" },
				{ Cue = "/VO/Nyx_0430", Portrait = "Portrait_Nyx_Default_01", Speaker = "NPC_Nyx_01",
					Text = "You are my son. It is to be expected. However... I know it is not effortless, to be of use. Continue to do well. I shall be watching." },
			},

