				UseableOffSource = true,
				InitialGiftableOffSource = true,
				GiftableOffSource = true,
				RequiresRunNotCleared = true,
				RequiredFalseTextLines = { "ThanatosHomeFirstMeetingAlt", "Ending01" },
				{ Cue = "/VO/Thanatos_0207",
					Text = "Go about your business, Zag. What if Lord Hades saw us chatting like this, now...? I risked everything by helping you out there, and still you failed." },
				{ Cue = "/VO/ZagreusHome_0220", Portrait = "Portrait_Zag_Serious_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "Turns out there's quite a lot of dead to fight down in the Underworld. Though I probably got farther than I would have, thanks to you." },
				{ Cue = "/VO/Thanatos_0208", PostLineThreadedFunctionName = "ThanatosExit", PostLineFunctionArgs = { AnimationState = "NPCThanatosExited", WaitTime = 0.3, },
					Text = "Always happy to oblige. Especially when it causes me to risk my station. To risk everything I've worked for." },
				OnQueuedFunctionName = "CheckDistanceTriggerThread",
				OnQueuedFunctionArgs = PresetEventArgs.Brooding,
			},

			ThanatosHomeFirstMeetingAlt =
			{
				SuperPriority = true,
				PlayOnce = true,
				UseInitialInteractSetup = true,
				UseableOffSource = true,
				InitialGiftableOffSource = true,
				GiftableOffSource = true,
				RequiresRunCleared = true,
				RequiredTextLinesThisRun = "ThanatosFieldFirstMeeting",
				RequiredFalseTextLines = { "ThanatosHomeFirstMeeting" },
				{ Cue = "/VO/Thanatos_0583",
					Text = "What are you doing back here, Zagreus? I risked everything by helping you out there. So that was all for nothing, then?" },
				{ Cue = "/VO/ZagreusHome_2500", Portrait = "Portrait_Zag_Serious_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Text = "Far from it, Than. I got where I needed to go, and if not for your help, maybe I wouldn't have. As for why I'm back... as Father would say, I have some business to attend to, here." },
				{ Cue = "/VO/Thanatos_0584",
					PreLineAnim = "ThanatosIdleInhouseFidget_HairFlick",
					PostLineThreadedFunctionName = "ThanatosExit", PostLineFunctionArgs = { AnimationState = "NPCThanatosExited", WaitTime = 0.3 },
					Text = "Well! Don't let me stop you, then. Always happy to risk my station for your sake. To risk everything I've worked for." },
				OnQueuedFunctionName = "CheckDistanceTriggerThread",
				OnQueuedFunctionArgs = PresetEventArgs.Brooding,
			},

			ThanatosHomeMiscMeeting01 =
			{
				PlayOnce = true,
				UseInitialInteractSetup = true,
				UseableOffSource = true,
				GiftableOffSource = true,
				RequiredMinNPCInteractions = { NPC_Thanatos_01 = 1 },
				RequiredFalseTextLines = { "Ending01" },
				{ Cue = "/VO/Thanatos_0209",
					Text = "Be on your way, Zagreus. I have an audience with Lord Hades in a moment. An inquiry into my recent absences." },
				{ Cue = "/VO/ZagreusHome_0221", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Text = "What are you worried about, Than? You've never given him a reason not to trust you." },
				{ Cue = "/VO/Thanatos_0210", PostLineThreadedFunctionName = "ThanatosExit", PostLineFunctionArgs = { AnimationState = "NPCThanatosExited" },
					Text = "Recently, I'm not so sure. Whatever you're doing, it had better be worth it." },
				OnQueuedFunctionName = "CheckDistanceTriggerThread",
				OnQueuedFunctionArgs = PresetEventArgs.Brooding,
			},

			ThanatosHomeRunProgress01 =
			{
				Priority = true,
				PlayOnce = true,
				UseInitialInteractSetup = true,
				UseableOffSource = true,
				RequiredTextLines = { "ThanatosHomeMiscMeeting01", "ThanatosGift02" },
				RequiredFalseTextLines = { "PersephoneFirstMeeting" },
				EndVoiceLines =
				{
					PreLineWait = 0.45,
					UsePlayerSource = true,
					RequiredMinElapsedTime = 3,
					-- Don't be ridiculous, Than.
					{ Cue = "/VO/ZagreusHome_0812" },
				},
				{ Cue = "/VO/Thanatos_0365",
					Text = "I still cannot entirely believe you really think there's any way you're getting out of here." },
				{ Cue = "/VO/ZagreusHome_0810", Portrait = "Portrait_Zag_Defiant_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Text = "Why? You've been out. It's thanks to you the mortals end up down here maybe half the time." },
				{ Cue = "/VO/Thanatos_0366",
					PreLineAnim = "ThanatosIdleInhouseFidget_HairFlick",
					Text = "That's different, that's my job. I can't stay there, and frankly I can barely stand it. Do you have any idea how bright it gets, up there?" },
				{ Cue = "/VO/ZagreusHome_0811", Portrait = "Portrait_Zag_Serious_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "No. But I look forward to finding out for myself." },
				{ Cue = "/VO/Thanatos_0367",
					Text = "{#DialogueItalicFormat}Tsch{#PreviousFormat}, well. We'll see what the Fates have to say about that. Maybe the shade of Daedalus can make you some protective eyewear or something." },
				OnQueuedFunctionName = "CheckDistanceTriggerThread",
				OnQueuedFunctionArgs = PresetEventArgs.Brooding,
