					Text = "You never spoke of her to me. You must have known... you kept the truth from me, the same as Nyx?" },
				{ Cue = "/VO/Thanatos_0212",
					PreLineAnim = "ZagreusTalkDenialReturnToIdle", PreLineAnimTarget = "Hero",
					Text = "It's not like that. I didn't know any more than you. Besides, Lord Hades ordered all of us to never speak of her again. I thought it just a bad relationship. I didn't realize she left her son." },
				{ Cue = "/VO/ZagreusHome_0223", Portrait = "Portrait_Zag_Serious_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusInteractionThoughtful", PreLineAnimTarget = "Hero",
					Text = "Something tells me Father wouldn't let her take me even if she wanted." },
				{ Cue = "/VO/Thanatos_0213",
					Text = "Maybe so. But even still, she did abandon you. Your family's here, if you ask me. But I can understand that... you need answers." },
				OnQueuedFunctionName = "CheckDistanceTriggerThread",
				OnQueuedFunctionArgs = PresetEventArgs.Brooding,
			},
			ThanatosBackstory01_B =
			{
				Priority = true,
				PlayOnce = true,
				UseInitialInteractSetup = true,
				UseableOffSource = true,
				RequiredTextLines = { "ThanatosFieldBackstory01", "ThanatosGift03", "PersephoneFirstMeeting" },
				RequiredAnyTextLines = { "Flashback_Mother_01", "CerberusStyxMeeting01" },
				RequiredFalseTextLines = { "ThanatosBackstory01", "PersephoneMeeting08" },
				EndVoiceLines =
				{
					PreLineWait = 0.45,
					ObjectType = "NPC_Thanatos_01",
					RequiredMinElapsedTime = 3,
					-- Hm.
					{ Cue = "/VO/Thanatos_0179" },
				},
				{ Cue = "/VO/Thanatos_0211",
					Text = "I met her, you know. Persephone, I mean. You look more like your father, but, you have her eyes. One of them, anyway." },
				{ Cue = "/VO/ZagreusHome_0222", Portrait = "Portrait_Zag_Serious_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					Text = "You never spoke of her to me. You must have known... you kept the truth from me, the same as Nyx?" },
				{ Cue = "/VO/Thanatos_0212",
					PreLineAnim = "ZagreusTalkDenialReturnToIdle", PreLineAnimTarget = "Hero",
					Text = "It's not like that. I didn't know any more than you. Besides, Lord Hades ordered all of us to never speak of her again. I thought it just a bad relationship. I didn't realize she left her son." },
				{ Cue = "/VO/ZagreusHome_0223", Portrait = "Portrait_Zag_Serious_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusInteractionThoughtful", PreLineAnimTarget = "Hero",
					Text = "Something tells me Father wouldn't let her take me even if she wanted." },
				OnQueuedFunctionName = "CheckDistanceTriggerThread",
				OnQueuedFunctionArgs = PresetEventArgs.Brooding,
			},

			ThanatosBackstory02 =
			{
				-- cutaway scene
				Priority = true,
				PlayOnce = true,
				RequiredTextLines = { "ThanatosGift06" },
				RequiredAnyTextLines = { "ThanatosBackstory01", "ThanatosBackstory01_B" },
				RequiredFalseTextLines = { "Ending01", "ThanatosAboutStaying01" },
				UseInitialInteractSetup = true,
				UseableOffSource = true,
				-- See you, Than.
				EndCue = "/VO/ZagreusHome_0822",
				EndWait = 0.9,
				{ Cue = "/VO/ZagreusHome_0821", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "Hey, Than. Come join me in the lounge for old time's sake. The coast is clear. The work can wait." },
				{ Cue = "/VO/Thanatos_0172",
					PortraitExitAnimation = "Portrait_Thanatos_Default_01_Exit",
					PostLineThreadedFunctionName = "LoungeRevelryPresentation",
					PostLineFunctionArgs = { Sound2 = "/EmptyCue", Sound3 = "/EmptyCue" },
					Text = "You're really asking me to join you for a drink...? I mean... you know what, sure. The work can wait." },
				{ Cue = "/VO/Thanatos_0582", FadeOutTime = 0.5, FullFadeTime = 7.8, FadeInTime = 2.0, InterSceneWaitTime = 0.5,  TeleportToId = 556834, AngleTowardTargetId = 556835, TeleportHeroToId = 556835, AngleHeroTowardTargetId = 556834,
					PreLineWait = 0.35, AttachedDim = "Lounge",
					FadeInSound = "/Leftovers/World Sounds/MapZoomInShortHigh",
					Text = "...I somehow knew, you know. I always knew you weren't going to stay. You were... so restless here. You just needed a reason to leave! And I hoped you wouldn't find one this quickly. {#DialogueItalicFormat}Eugh{#PreviousFormat}. What am I saying? You have no idea." },
				{ Cue = "/VO/ZagreusHome_0784", Portrait = "Portrait_Zag_Serious_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "No, I know exactly what you're saying, Than. I'm sorry that I'm putting you through all of this, it's just... it's something that I have to do. But, I'm glad we know the truth about me, now. Maybe we can move on from there." },
				{ Cue = "/VO/Thanatos_0174",
					PortraitExitAnimation = "Portrait_Thanatos_Default_01_Exit",
					PreLineAnim = "ThanatosIdleInhouseFidget_HairFlick",
					Text = "{#DialogueItalicFormat}Tsk{#PreviousFormat}... I've always lacked your optimism, Zag. Anyway... the work has waited long enough, I need to get back to it. Thanks for the drink." },
				{ Cue = "/VO/Thanatos_0175", FadeOutTime = 0.5, FullFadeTime = 0.5, TeleportToId = 423055, AngleTowardTargetId = 421122, TeleportHeroToId = 423056, AngleHeroTowardTargetId = 423055, FadeInTime = 0.5, FadeInSound = "/Leftovers/World Sounds/MapZoomInShortHigh", FadeInTime = 0.5, PostLineThreadedFunctionName = "ThanatosExit",
					PostLineFunctionArgs = { AnimationState = "NPCThanatosExited", WaitTime = 0.3, SkipExitReaction = true },
					Text = "...Watch yourself out there, Zagreus. I have to go." },
				OnQueuedFunctionName = "CheckDistanceTriggerThread",
				OnQueuedFunctionArgs = PresetEventArgs.Brooding,
			},

			ThanatosAboutDeath01 =
			{
				PlayOnce = true,
				UseInitialInteractSetup = true,
				UseableOffSource = true,
				RequiredAnyTextLines = { "ThanatosBackstory02", "ThanatosGift07_A", },
				EndVoiceLines =
				{
					PreLineWait = 0.65,
					UsePlayerSource = true,
					RequiredMinElapsedTime = 3,
					-- Yeah, seriously.
					{ Cue = "/VO/ZagreusHome_1452" },
				},
				{ Cue = "/VO/ZagreusHome_1450", Portrait = "Portrait_Zag_Empathetic_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Text = "Thanatos, I've meant to ask... is it really the case that many mortals have a crippling fear of death?" },
				{ Cue = "/VO/Thanatos_0398",
					Text = "It is, I'm quite feared, up there. Most of them cannot distinguish between me, and, say, Lord Ares, or your father, even. We fill them with despair. No helping it." },
				{ Cue = "/VO/ZagreusHome_1451", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "Why not? I mean, isn't it kind of a big problem that they have such negative associations, here? Can't we... I don't know, do something to educate them all?" },
				{ Cue = "/VO/Thanatos_0399",
					PreLineAnim = "ThanatosIdleInhouseFidget_HairFlick",
					Text = "We've all the time to educate them, here. Their ignorance of death is solved almost as soon as they experience their first. Though I suppose they might be better off knowing before the fact." },
				OnQueuedFunctionName = "CheckDistanceTriggerThread",
				OnQueuedFunctionArgs = PresetEventArgs.Brooding,
			},
			ThanatosAboutDeath02 =
			{
				PlayOnce = true,
				UseInitialInteractSetup = true,
				UseableOffSource = true,
				RequiredTextLines = { "ThanatosAboutDeath01" },
				EndVoiceLines =
				{
					PreLineWait = 0.65,
					UsePlayerSource = true,
					-- Didn't know you saw it that way.
					{ Cue = "/VO/ZagreusHome_1455" },
				},
				{ Cue = "/VO/ZagreusHome_1453", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "Than? I had another question about death. So... why is it mortals grieve so much about the thing, I mean... it definitely hurts there for a little while, but... it's not that bad." },
				{ Cue = "/VO/Thanatos_0400",
					Text = "They see it as their one and only chance. Imagine if, after you perished, that was it. Back to the infinite Chaos. Everything about you. Returned to how it was before you were even there." },
				{ Cue = "/VO/ZagreusHome_1454", Portrait = "Portrait_Zag_Defiant_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Text = "But that's absurd. One life? Though... {#DialogueItalicFormat}eugh{#PreviousFormat}. That's horrible, even to think about." },
				{ Cue = "/VO/Thanatos_0401",
					Text = "I know, right? Some of them know that isn't how it is, and try to tell their kind about what it is we do here, but... it's slow going, getting the information out. Either way! I get to be the bearer of good news." },
				OnQueuedFunctionName = "CheckDistanceTriggerThread",
				OnQueuedFunctionArgs = PresetEventArgs.Brooding,
			},
			ThanatosAboutDeath03 =
			{
				PlayOnce = true,
				UseInitialInteractSetup = true,
				UseableOffSource = true,
				RequiredTextLines = { "ThanatosAboutDeath02" },
				EndVoiceLines =
				{
					PreLineWait = 0.65,
					UsePlayerSource = true,
					-- It really is.
					{ Cue = "/VO/ZagreusHome_1459" },
				},
				{ Cue = "/VO/ZagreusHome_1456", Portrait = "Portrait_Zag_Serious_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Text = "Hey Than? Something's been troubling me still, with what you said, about how mortals look at death. If most of them believe their life is all they have... how do they live? I mean, I can't even imagine what that's like. I'd be a lot more careful, at the very least!" },
				{ Cue = "/VO/Thanatos_0402",
					Text = "They live with all their hearts, or at least try. I think there's something to it, truthfully. Though, some of them, they fall apart. They get here without me. On their own, gripped with despair. If only they knew what your father always says about all this." },
				{ Cue = "/VO/ZagreusHome_1457", Portrait = "Portrait_Zag_Defiant_01", Speaker = "CharProtag",
					Text = "What, you mean that {#DialogueItalicFormat}'there is no escape'{#PreviousFormat}? From what? The mortals are up there, not here. Not while they're technically alive in their sense, anyway." },
				{ Cue = "/VO/Thanatos_0403",
					Text = "I mean, you can't escape your problems, Zagreus. You can run from your family, but you can't run from everything. You have no choice but to confront it, and work through it, sooner or later, one way or another. You, and all of us." },
				{ Cue = "/VO/ZagreusHome_1458", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag", PreLineWait = 0.35,
					PreLineAnim = "ZagreusInteractionThoughtful", PreLineAnimTarget = "Hero",
					Text = "...I'm starting to notice that, yes. Though, to a mortal who has yet to die... don't they think of death as some sort of release? You come to end their misery, something like that?" },
				{ Cue = "/VO/Thanatos_0404",
					Text = "I can release them from their decaying bodies. Sometimes, the Fates demand I bring them in sooner. That's about as much as I can do. Some of them get it early on, how to live. It's quite impressive, really, that they learn so quick." },
				OnQueuedFunctionName = "CheckDistanceTriggerThread",
				OnQueuedFunctionArgs = PresetEventArgs.Brooding,
			},

			ThanatosAboutShifting01 =
			{
				PlayOnce = true,
				UseInitialInteractSetup = true,
				UseableOffSource = true,
				RequiredTextLines = { "ThanatosGift06", },
				EndVoiceLines =
				{
					PreLineWait = 0.4,
					UsePlayerSource = true,
					-- I can see that.
					{ Cue = "/VO/ZagreusHome_2502" },
				},
				{ Cue = "/VO/ZagreusHome_2501", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "Hey, just how far are you able to shift, anyway, Than? I never could go more than a blade's length in front of me, while you're out there skipping across the entire world probably." },
				{ Cue = "/VO/Thanatos_0585",
					Text = "Most of it, yes. Don't beat yourself up, though, Zagreus. It's a practiced skill but I've apparently been able to do it since birth. Mother Nyx says I was a real handful." },
				OnQueuedFunctionName = "CheckDistanceTriggerThread",
				OnQueuedFunctionArgs = PresetEventArgs.Brooding,
			},

			ThanatosAboutBalcony01 =
			{
				PlayOnce = true,
				UseInitialInteractSetup = true,
				UseableOffSource = true,
				GiftableOffSource = true,
				RequiredTextLines = { "ThanatosGift05", },
				EndVoiceLines =
				{
					PreLineWait = 0.65,
					UsePlayerSource = true,
					-- Go get them.
					{ Cue = "/VO/ZagreusHome_2807" },
				},
				{ Cue = "/VO/ZagreusHome_2805", Portrait = "Portrait_Zag_Defiant_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "Hey, what is it you're always staring at out there, anyway, Than? I don't see anything." },
				{ Cue = "/VO/Thanatos_0612",
					Text = "The River Styx is right in front of you, and you don't see anything, Zag? I can watch it endlessly. Studying the competition, you might say. Most mortals expect to get here by river, with Charon. Not with me." },
				{ Cue = "/VO/ZagreusHome_2806", Portrait = "Portrait_Zag_Defiant_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "So you're locked in ruthless battle with a river, searching it for any signs of weakness, that sort of thing? That sounds stressful." },
				{ Cue = "/VO/Thanatos_0613",
					PreLineAnim = "ThanatosIdleInhouseFidget_HairFlick", PostLineThreadedFunctionName = "ThanatosExit", PostLineFunctionArgs = { AnimationState = "NPCThanatosExited", WaitTime = 2.5, SkipExitReaction = true },
					Text = "I find it rather soothing as a matter of fact. A break from having to deal constantly with mortal souls. Speaking of which... I have to pick somebody up right now." },
				OnQueuedFunctionName = "CheckDistanceTriggerThread",
				OnQueuedFunctionArgs = PresetEventArgs.Brooding,
			},

			ThanatosAboutNemesisAspect01 =
			{
				PlayOnce = true,
				UseInitialInteractSetup = true,
				UseableOffSource = true,
				RequiredTextLines = { "ThanatosGift05" },
				RequiredTraitsTaken = { "SwordCriticalParryTrait" },
				EndVoiceLines =
				{
					PreLineWait = 0.4,
					UsePlayerSource = true,
					-- I'm sure she's lovely, then.
					{ Cue = "/VO/ZagreusHome_2504" },
				},
				{ Cue = "/VO/ZagreusHome_2503", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "Than, my blade, the one I'm borrowing from Father anyway, it... revealed to me an older aspect of itself, of Nemesis. Nyx never mentions her. Have you two ever met?" },
				{ Cue = "/VO/Thanatos_0586",
					Text = "In case you haven't noticed, most of us who sprang from Nyx, we're not exactly close. Nemesis is fully occupied meting out justice on the surface. Our paths are not designed to cross. And based on everything I know, that's fine with me." },
				OnQueuedFunctionName = "CheckDistanceTriggerThread",
				OnQueuedFunctionArgs = PresetEventArgs.Brooding,
			},
			ThanatosAboutErisAspect01 =
			{
				PlayOnce = true,
				UseInitialInteractSetup = true,
				UseableOffSource = true,
				RequiredTextLines = { "ThanatosGift05" },
				RequiredMinRunsWithWeapons = { GunWeapon = 5 },
				RequiredTraitsTaken = { "GunGrenadeSelfEmpowerTrait" },
				RequiredWeapon = "GunWeapon",
				EndVoiceLines =
				{
					PreLineWait = 0.4,
					UsePlayerSource = true,
					-- All right, then!
					{ Cue = "/VO/ZagreusHome_2506" },
				},
				{ Cue = "/VO/Thanatos_0587",
					Text = "How's that Rail of Adamant been working out for you lately? Always figured the direct approach was more your style. Though, I happened to have a look at your permanent record lately, and turns out you're doing a lot of work with that thing." },
				{ Cue = "/VO/ZagreusHome_2505", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "Why, Thanatos, you're learning to pry in others' business! The Rail's been working out just fine, thanks. Though, now that you mention it, how well do you know Eris, one of Nyx's daughters, right? Seems she wielded the Rail before me." },
				{ Cue = "/VO/Thanatos_0588",
					Text = "The mortals call her Strife. She and her children... it's thanks partly to their dedicated efforts I'm so busy all the time. Though, it is best we didn't speak of her, I think." },
				OnQueuedFunctionName = "CheckDistanceTriggerThread",
				OnQueuedFunctionArgs = PresetEventArgs.Brooding,
			},
			-- alt below
			ThanatosAboutSurface01 =
			{
				PlayOnce = true,
				UseInitialInteractSetup = true,
				UseableOffSource = true,
				RequiredTextLines = { "ThanatosGift03" },
				RequiredFalseTextLines = { "ThanatosAboutSurface01_B", "PersephoneFirstMeeting" },
				RequiredMaxRunsCleared = 0,
				EndVoiceLines =
				{
					PreLineWait = 0.65,
					UsePlayerSource = true,
					-- Huh. Maybe. I hope.
					{ Cue = "/VO/ZagreusHome_1462" },
				},
				{ Cue = "/VO/ZagreusHome_1460", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Text = "Thanatos, I ought have asked by now, but... what's it like? Up there I mean, outside. You never talk about it, I... almost forget that's where you go, for work." },
				{ Cue = "/VO/Thanatos_0405",
					Text = "What can I say? It's... different. I never stay up there for very long, don't think I could, starts making me feel ill. Too many sensations all at once. And when the chariot of Helios is in the sky, it gets so bright that I can barely see." },
				{ Cue = "/VO/ZagreusHome_1461", Portrait = "Portrait_Zag_Defiant_01", Speaker = "CharProtag",
					Text = "That sounds awful. If it's that bad, why would all the Olympians chat it up so much?" },
				{ Cue = "/VO/Thanatos_0406",
					Text = "There's no accounting for their taste, I guess. But, I'm discounting certain aspects that are not so bad. You know what? I'll not spoil the surprise. I have a feeling you're going to see for yourself soon enough." },
				OnQueuedFunctionName = "CheckDistanceTriggerThread",
				OnQueuedFunctionArgs = PresetEventArgs.Brooding,
			},
			ThanatosAboutSurface01_B =
			{
				PlayOnce = true,
				UseInitialInteractSetup = true,
				UseableOffSource = true,
				RequiredTextLines = { "ThanatosGift03", "PersephoneFirstMeeting" },
				RequiredFalseTextLines = { "ThanatosAboutSurface01" },
				{ Cue = "/VO/ZagreusHome_1460", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Text = "Thanatos, I ought have asked by now, but... what's it like? Up there I mean, outside. You never talk about it, I... almost forget that's where you go, for work." },
				{ Cue = "/VO/Thanatos_0405",
					Text = "What can I say? It's... different. I never stay up there for very long, don't think I could, starts making me feel ill. Too many sensations all at once. And when the chariot of Helios is in the sky, it gets so bright that I can barely see." },
				{ Cue = "/VO/ZagreusHome_1461", Portrait = "Portrait_Zag_Defiant_01", Speaker = "CharProtag",
					Text = "That sounds awful. If it's that bad, why would all the Olympians chat it up so much?" },
				{ Cue = "/VO/Thanatos_0663",
					Text = "There's no accounting for their taste, I guess." },
				OnQueuedFunctionName = "CheckDistanceTriggerThread",
				OnQueuedFunctionArgs = PresetEventArgs.Brooding,
			},

			ThanatosAboutCharon01 =
			{
				PlayOnce = true,
				UseInitialInteractSetup = true,
				UseableOffSource = true,
				RequiredTextLines = { "CharonGift03", "ThanatosGift05" },
				RequiredSeenRooms = { "D_Hub" },
				-- I never thought of it like that.
				EndCue = "/VO/ZagreusHome_1449",
				EndWait = 0.4,
				{ Cue = "/VO/ZagreusHome_1447", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Text = "What's going on with Charon, by the way, if you can tell me, Than? He's... never been particularly talkative, but he really seems to know the inner workings of this place." },
				{ Cue = "/VO/Thanatos_0396",
					Text = "I correspond with Charon all the time, but even I don't know the full extent of his responsibilities. He's an independent contractor of sorts, been here forever. Your father gives him plenty of space to work." },
				{ Cue = "/VO/ZagreusHome_1448", Portrait = "Portrait_Zag_Defiant_01", Speaker = "CharProtag",
					Text = "He must be compensated pretty well for having done this for so long. Tending the influx of new souls and all of that." },
				{ Cue = "/VO/Thanatos_0397",
					PreLineAnim = "ThanatosIdleInhouseFidget_HairFlick",
					Text = "I doubt he's in it for the compensation, Zagreus. There is no Underworld without Charon. I think he knows that, and your father knows it, too." },
				OnQueuedFunctionName = "CheckDistanceTriggerThread",
				OnQueuedFunctionArgs = PresetEventArgs.Brooding,
			},
			-- rename, was a 2 below
			ThanatosAboutCharon02A =
			{
				PlayOnce = true,
				UseInitialInteractSetup = true,
				UseableOffSource = true,
				RequiredTextLines = { "CharonGift04", "ThanatosAboutCharon01" },
				MinRunsSinceAnyTextLines = { TextLines = { "ThanatosAboutCharon01" }, Count = 2 },
				-- Fair enough.
				EndCue = "/VO/ZagreusHome_2508",
				EndWait = 0.4,
				{ Cue = "/VO/ZagreusHome_2507", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "Hey, were you ever close with Charon, Than? Seems like no matter how much I try, I can't quite get through to him." },
				{ Cue = "/VO/Thanatos_0589",
					Text = "Wouldn't be surprised if you were closer to him than I am. Not all of Nyx's sons and daughters keep in steady touch. Though at least we're not always at each others' throats, like the other side of your family." },
				OnQueuedFunctionName = "CheckDistanceTriggerThread",
				OnQueuedFunctionArgs = PresetEventArgs.Brooding,
			},

			ThanatosAboutCharon02 =
			{
				PlayOnce = true,
				UseInitialInteractSetup = true,
				UseableOffSource = true,
				RequiredTextLines = { "SkellyAboutSources04" },
				EndVoiceLines =
				{
					PreLineWait = 0.4,
					UsePlayerSource = true,
					RequiredMinElapsedTime = 3,
					-- Did he really think that far ahead...?
					{ Cue = "/VO/ZagreusHome_2515" },
				},
				{ Cue = "/VO/ZagreusHome_2513", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Text = "Than, you're not going to believe this, but... Charon's been helping me. All on his own, apparently! I was so sure that Nyx or someone put him up to it. That Skelly character I mentioned, Charon hired him!" },
				{ Cue = "/VO/Thanatos_0592",
					AngleTowardHero = true,
					Text = "What are you talking about, Zagreus? Just because Charon's selling you things doesn't mean he's helping you. Wait, are you sure?" },
				{ Cue = "/VO/ZagreusHome_2514", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "I'm sure! His helper, Skelly, he's a lot more talkative, and finally let on. Although, he couldn't tell me how come Charon did a thing like this, I mean... we weren't ever close..." },
				{ Cue = "/VO/Thanatos_0593",
					Text = "{#DialogueItalicFormat}Hmm{#PreviousFormat}. All I can think of is... Charon's very dedicated to this House. Like some of us. Maybe he thought... he could help settle things, between you and your father. One way or another." },
				OnQueuedFunctionName = "CheckDistanceTriggerThread",
				OnQueuedFunctionArgs = PresetEventArgs.Brooding,
			},

			ThanatosAboutCharonFight01 =
			{
				Priority = true,
				PlayOnce = true,
				UseInitialInteractSetup = true,
				UseableOffSource = true,
				RequiredEncounterThisRun = "BossCharon",
				RequiredFalseDeathRoom = "CharonFight01",
				EndVoiceLines =
				{
					PreLineWait = 0.4,
					UsePlayerSource = true,
					RequiredMinElapsedTime = 3,
					-- Don't think you will.
					{ Cue = "/VO/ZagreusHome_2510" },
				},
				{ Cue = "/VO/ZagreusHome_2509", Portrait = "Portrait_Zag_Defiant_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "Hey, Than, now hypothetically, if I were to have vanquished Charon in single combat after having, I don't know, say, helped myself to one of his wares at no charge, how long would you say he'd hold it over me?" },
				{ Cue = "/VO/Thanatos_0590",
					Text = "Everything is an exchange with Charon, Zag. If there was something you refused to pay for, then I'm going to assume that's why you're back here, now. The debt is paid in obols, or in blood. Or their closest counterparts, I guess." },
				OnQueuedFunctionName = "CheckDistanceTriggerThread",
				OnQueuedFunctionArgs = PresetEventArgs.Brooding,
			},
			ThanatosAboutSkelly01 =
			{
				PlayOnce = true,
				UseInitialInteractSetup = true,
				UseableOffSource = true,
				RequiredTextLines = { "SkellyGift01", "ThanatosGift03", "SkellyGift01", "NyxGift01" },
				-- Didn't think so.
				EndCue = "/VO/ZagreusHome_2512",
				EndWait = 0.4,
				{ Cue = "/VO/ZagreusHome_2511", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "Than, level with me on this. You didn't happen to hire a shifty character called Skelly to assist me here and there, did you?" },
				{ Cue = "/VO/Thanatos_0591",
					Emote = "PortraitEmoteSurprise",
					Text = "Who, what? No. Why would you even ask?" },
				OnQueuedFunctionName = "CheckDistanceTriggerThread",
				OnQueuedFunctionArgs = PresetEventArgs.Brooding,
			},

			ThanatosAboutPersephone01 =
			{
				PlayOnce = true,
				UseInitialInteractSetup = true,
				UseableOffSource = true,
				RequiredTextLines = { "ThanatosGift03", "ThanatosFieldBackstory01" },
				RequiredAnyTextLines = { "ThanatosBackstory01", "ThanatosBackstory01_B", "PersephoneFirstMeeting" },
				RequiredFalseTextLines = { "Ending01" },
				EndVoiceLines =
				{
					PreLineWait = 0.4,
					UsePlayerSource = true,
					RequiredMinElapsedTime = 3,
					-- Radiance, huh... doesn't sound like Father at all.
					{ Cue = "/VO/ZagreusHome_2518" },
				},
				{ Cue = "/VO/ZagreusHome_2516", Portrait = "Portrait_Zag_Empathetic_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "Do you remember anything else about my mother, Than? Just, anything. You weren't close, I guess?" },
				{ Cue = "/VO/Thanatos_0614",
					Text = "We weren't. She kept to herself as I recall, and I would do the same. Unlike my present company, I do not ask too many questions. It was quite clear to me she wasn't from this place." },
				{ Cue = "/VO/ZagreusHome_2517", Portrait = "Portrait_Zag_Defiant_01", Speaker = "CharProtag",
					Text = "Father isn't from this place, either. How was she any different?" },
				{ Cue = "/VO/Thanatos_0615",
					Text = "Sometimes I have to be reminded that Lord Hades wasn't born here, like the rest of us. But Persephone, she... she had a radiance about her, I suppose. Reminded me of the outside, but... I don't mean that in a negative respect." },
				OnQueuedFunctionName = "CheckDistanceTriggerThread",
				OnQueuedFunctionArgs = PresetEventArgs.Brooding,
			},

			ThanatosAboutSisyphus01 =
			{
				PlayOnce = true,
				UseInitialInteractSetup = true,
				UseableOffSource = true,
				RequiredTextLines = { "ThanatosGift08", "SisyphusMeeting_AboutMegAndThanatos01", "SisyphusGift06" },
				{ Cue = "/VO/ZagreusHome_1463", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Text = "Than, you don't hold a grudge against old Sisyphus, do you? He seems rather apprehensive about you. I understand you met not on the best of terms." },
				{ Cue = "/VO/Thanatos_0407",
					Text = "He is a brazen one, I have to give him that. I hadn't realized that you were close. Despite some questionable choices on your part, I don't think you're the poorest judge of character, and so... maybe I have him wrong." },
				{ Cue = "/VO/ZagreusHome_1464", Portrait = "Portrait_Zag_Defiant_01", Speaker = "CharProtag",
					Text = "I only have a faint impression of the man he used to be, although it seems he's changed a lot, since then. Thank you for understanding, anyway." },
				OnQueuedFunctionName = "CheckDistanceTriggerThread",
				OnQueuedFunctionArgs = PresetEventArgs.Brooding,
			},
			ThanatosAboutSisyphusLiberationQuestComplete01 =
			{
				PlayOnce = true,
				UseableOffSource = true,
				UseInitialInteractSetup = true,
				RequiredTextLines = { "SisyphusLiberationQuestComplete" },
				EndVoiceLines =
				{
					PreLineWait = 0.4,
					UsePlayerSource = true,
					-- Don't think you will.
					{ Cue = "/VO/ZagreusHome_2525" },
				},
				{ Cue = "/VO/Thanatos_0621",
					Text = "I heard you stayed the knave-king's sentence. I should be angry with you. Sisyphus must be the only mortal {#DialogueItalicFormat}ever {#PreviousFormat}to have humiliated me. But Mother Nyx, she... must have backed you, didn't she..." },
				{ Cue = "/VO/ZagreusHome_2524", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "We did what needed to be done. As much as Father likes making examples out of wretched shades, an eternal sentence felt a little excessive in this case. Besides... Sisyphus isn't leaving, anyway. Seems to have found his niche." },
				{ Cue = "/VO/Thanatos_0622",
					Text = "Oh? Well, he's an odd one through and through, I guess. As long as I don't have to deal with him again." },
				OnQueuedFunctionName = "CheckDistanceTriggerThread",
				OnQueuedFunctionArgs = PresetEventArgs.Brooding,
			},

			ThanatosAboutAchilles01 =
			{
				PlayOnce = true,
				UseInitialInteractSetup = true,
				UseableOffSource = true,
				RequiredTextLines = { "ThanatosGift05" },
				RequiredAnyTextLines = { "AchillesGift05_A", "AchillesGift05_B" },
				{ Cue = "/VO/ZagreusHome_2526", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					Text = "You and Achilles ever get to talk much, Than?" },
				{ Cue = "/VO/Thanatos_0623",
					Text = "I have my orders, he has his. What do you think? Even back when he was still alive, there was no real chance I was going to be the one to take him here, given his line of work." },
				{ Cue = "/VO/ZagreusHome_2527", Portrait = "Portrait_Zag_Serious_01", Speaker = "CharProtag",
					Text = "Because he was a warrior. When mortals die a violent death, they're no longer your case, are they?" },
				{ Cue = "/VO/Thanatos_0624",
					Text = "No. I tend to those in less of a hurry to come here." },
				OnQueuedFunctionName = "CheckDistanceTriggerThread",
				OnQueuedFunctionArgs = PresetEventArgs.Brooding,
			},
			ThanatosAboutMyrmidonReunionQuestComplete01 =
			{
				PlayOnce = true,
				UseInitialInteractSetup = true,
				UseableOffSource = true,
				RequiredMinNPCInteractions = { NPC_Thanatos_01 = 1 },
				RequiredTextLines = { "MyrmidonReunionQuestComplete", "ThanatosGift01" },
				MinRunsSinceAnyTextLines = { TextLines = { "MyrmidonReunionQuestComplete" }, Count = 4 },
				RequiredFalseTextLinesLastRun = { "MyrmidonReunionQuestComplete" },
				AreIdsNotAlive = { 370009 },
				EndVoiceLines =
				{
					PreLineWait = 0.4,
					UsePlayerSource = true,
					-- It is. I think it is.
					{ Cue = "/VO/ZagreusHome_2529" },
				},
				{ Cue = "/VO/Thanatos_0625",
					Text = "Your trainer's been absent from his post, more so than usual. You voided his pact with Lord Hades, didn't you? I don't know how you did it." },
				{ Cue = "/VO/ZagreusHome_2528", Portrait = "Portrait_Zag_Defiant_01", Speaker = "CharProtag",
					Text = "You know he's more than my trainer, Than. As for how I did it, turns out being the first and only son of Hades has its privileges. Now Achilles gets to be with someone he loves. Who loves him back." },
				{ Cue = "/VO/Thanatos_0626",
					Text = "Love is common among mortals. Is theirs really so special as to be worth the trouble? Not just on your part, but theirs?" },
				OnQueuedFunctionName = "CheckDistanceTriggerThread",
				OnQueuedFunctionArgs = PresetEventArgs.Brooding,
			},

			ThanatosAboutMegaera01 =
			{
				PlayOnce = true,
				UseInitialInteractSetup = true,
				UseableOffSource = true,
				GiftableOffSource = true,
				RequiredTextLines = { "ThanatosGift05", "MegaeraGift05", "MegaeraBedroom01", "Fury2FirstAppearance", "Fury3FirstAppearance" },
				RequiredFalseTextLines = { "MegaeraGift09" },
				RequiredKillsThisRun = { "Harpy" },
				EndVoiceLines =
				{
					PreLineWait = 0.85,
					UsePlayerSource = true,
					RequiredMinElapsedTime = 2,
					-- ...Oh.
					{ Cue = "/VO/ZagreusHome_0818" },
				},
				{ Cue = "/VO/Thanatos_0370",
					Text = "You've seen a lot of Megaera lately, haven't you, Zag?" },
				{ Cue = "/VO/ZagreusHome_0816", Portrait = "Portrait_Zag_Serious_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Text = "It's not like that, Than. She's there to slow me down. Get in my way." },
				{ Cue = "/VO/Thanatos_0371",
					PreLineAnim = "ThanatosIdleInhouseFidget_HairFlick",
					Text = "If you insist. Although I think it's much more complicated than you say." },
				{ Cue = "/VO/ZagreusHome_0817", Portrait = "Portrait_Zag_Defiant_01", Speaker = "CharProtag",
					Text = "Yeah... her sisters are back. They're... hard to deal with. I don't think Meg is on good terms with either one." },
				{ Cue = "/VO/Thanatos_0372", PostLineThreadedFunctionName = "ThanatosExit", PostLineFunctionArgs = { AnimationState = "NPCThanatosExited", WaitTime = 0.35, SkipExitReaction = true },
					Text = "That's not what I meant. But fine, you've told me all I need to know, for now." },
				OnQueuedFunctionName = "CheckDistanceTriggerThread",
				OnQueuedFunctionArgs = PresetEventArgs.Brooding,
			},
			ThanatosAboutMegaera02 =
			{
				PlayOnce = true,
				UseInitialInteractSetup = true,
				UseableOffSource = true,
				RequiredTextLines = { "ThanatosGift08", "MegaeraGift08", "SisyphusBackstory03" },
				EndVoiceLines =
				{
					PreLineWait = 0.85,
					UsePlayerSource = true,
					RequiredMinElapsedTime = 2,
					-- I know what you mean.
					{ Cue = "/VO/ZagreusHome_2540" },
				},
				{ Cue = "/VO/ZagreusHome_2538", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "You and Meg go back a long way, don't you? I forget sometimes you two are pretty close." },
				{ Cue = "/VO/Thanatos_0638",
					Text = "Our responsibilities must have us cross paths often, yes. But I didn't really get to know her until after the whole humiliating affair with Sisyphus." },
				{ Cue = "/VO/ZagreusHome_2539", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Text = "Oh, right. He got stuck in Tartarus after that, under Meg's personal supervision. So you bonded over your shared disdain for Sisyphus?" },
				{ Cue = "/VO/Thanatos_0639",
					Text = "We each had to blow off some steam, yes. That was some of the hardest I ever worked, back then, and her, too, I think. When you go through something like that with someone, it brings you closer, I suppose." },
				OnQueuedFunctionName = "CheckDistanceTriggerThread",
				OnQueuedFunctionArgs = PresetEventArgs.Brooding,
			},

			ThanatosAboutBeingCloseWithMegaera01 =
			{
				PlayOnce = true,
				UseableOffSource = true,
				UseInitialInteractSetup = true,
				RequiredTextLines = { "BecameCloseWithThanatos01Than_GoToHim", "BecameCloseWithMegaera01_BMeg_GoToHer" },
				RequiredFalseTextLines = { "MegaeraWithThanatosBedroom01" },
				EndVoiceLines =
				{
					PreLineWait = 0.65,
					UsePlayerSource = true,
					-- ...Sure. Sounds good.
					{ Cue = "/VO/ZagreusHome_1486" },
				},
				{ Cue = "/VO/ZagreusHome_1483", Portrait = "Portrait_Zag_Empathetic_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusInteractionThoughtful", PreLineAnimTarget = "Hero",
					Text = "Than, there's something that I meant to tell you. Ask you, rather. You're close with Megaera, so, what I mean is... you know about {#DialogueItalicFormat}us{#PreviousFormat}, right?" },
				{ Cue = "/VO/Thanatos_0421",
					Text = "Yes, of course. Why do you ask?" },
				{ Cue = "/VO/ZagreusHome_1484", Portrait = "Portrait_Zag_Empathetic_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "Well, it's just... I figured since you deal with mortals all the time, I know that some of them, they choose a single suitor. It's a valid custom, if it's yours...!" },
				{ Cue = "/VO/Thanatos_0422",
					PreLineAnim = "ThanatosIdleInhouseFidget_HairFlick",
					Text = "The mortals have their customs, yes, indeed. Quite a variety, in point of fact. However, none as yet have stuck with me, I think. So don't concern yourself with things like that." },
				{ Cue = "/VO/ZagreusHome_1485", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Text = "You're saying you're not angry with me? Or Meg, for that matter?" },
				{ Cue = "/VO/Thanatos_0423", Portrait = "Portrait_Thanatos_Pleased_01",
					Text = "Not any more than usual. Tell her that I said hello when next you meet." },
				OnQueuedFunctionName = "CheckDistanceTriggerThread",
				OnQueuedFunctionArgs = PresetEventArgs.Brooding,
			},

			ThanatosAboutBeingCloseWithDusa01 =
			{
				PlayOnce = true,
				UseableOffSource = true,
				UseInitialInteractSetup = true,
				RequiredTextLines = { "BecameCloseWithDusa01" },
				RequiredAnyTextLines = { "BecameCloseWithThanatos01Than_GoToHim", "BecameCloseWithThanatos01Than_BackOff" },
				EndVoiceLines =
				{
					PreLineWait = 0.4,
					UsePlayerSource = true,
					-- ...Yeah, well... thanks, Than.
					{ Cue = "/VO/ZagreusHome_2494" },
				},
				{ Cue = "/VO/ZagreusHome_2493", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "Than, you don't take issue with me being close with Dusa, do you? She brings such warmth to this place... I really appreciate having her here." },
				{ Cue = "/VO/Thanatos_0577",
					Text = "You don't need to ask me such things, Zagreus. They're your feelings." },
				OnQueuedFunctionName = "CheckDistanceTriggerThread",
				OnQueuedFunctionArgs = PresetEventArgs.Brooding,
			},

			ThanatosAboutAres01 =
			{
				PlayOnce = true,
				UseInitialInteractSetup = true,
				UseableOffSource = true,
				RequiredTextLines = { "AresAboutThanatos02", },
				-- Disturbingly all right!
				EndCue = "/VO/ZagreusHome_1446",
				EndWait = 0.4,
				{ Cue = "/VO/ZagreusHome_1444", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Text = "Than, what's your relationship with Ares on Olympus? I know he deals in death himself, but... his approach seems rather different from your own." },
				{ Cue = "/VO/Thanatos_0394",
					Text = "I do not care for his approach at all. But he has been professionally courteous with me. More than I can say for most of your Olympian relatives, I suppose." },
				{ Cue = "/VO/ZagreusHome_1445", Portrait = "Portrait_Zag_Defiant_01", Speaker = "CharProtag",
					Text = "You mean the rest of the Olympians don't treat you well?" },
				{ Cue = "/VO/Thanatos_0395",
					Text = "Discounting Hermes, I should say, the rest of them have nothing much to do with me at all. I can imagine why, I know it's complicated with your father here. But as for Ares... I think he's all right." },
				OnQueuedFunctionName = "CheckDistanceTriggerThread",
				OnQueuedFunctionArgs = PresetEventArgs.Brooding,
			},
			ThanatosAboutNyxAndAres01 =
			{
				PlayOnce = true,
				UseableOffSource = true,
				UseInitialInteractSetup = true,
				RequiredAnyTextLines = { "AresAboutNyx01", "AresAboutNyx01_B" },
				RequiredAnyOtherTextLines = { "ThanatosGift04", "ThanatosGift04_B" },
				RequiredFalseTextLines = { "NyxAboutAres05", "NyxAboutAres05_B" },
				EndVoiceLines =
				{
					PreLineWait = 0.4,
					UsePlayerSource = true,
					-- ...Eh, you're probably right...
					{ Cue = "/VO/ZagreusHome_2497" },
				},
				{ Cue = "/VO/ZagreusHome_2495", Portrait = "Portrait_Zag_Defiant_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusInteractionThoughtful", PreLineAnimTarget = "Hero",
					Text = "Than? I thought you ought to know that Ares, he... I think he's taken quite an interest in Nyx. Keeps asking me to talk to her on his behalf." },
				{ Cue = "/VO/Thanatos_0578",
					Text = "Finally. It's about time Olympus took notice of Mother and all her accomplishments." },
				{ Cue = "/VO/ZagreusHome_2496", Portrait = "Portrait_Zag_Defiant_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "Finally? You don't think Ares has untoward intentions or anything like that?" },
				{ Cue = "/VO/Thanatos_0579",
					Text = "He'd be a hollow fool if he did. Why do you think they fear us on Olympus, Zagreus? Because of Hypnos? I don't think Mother Nyx requires your concern. Though, by all means, talk to her about it, if you like." },
				OnQueuedFunctionName = "CheckDistanceTriggerThread",
				OnQueuedFunctionArgs = PresetEventArgs.Brooding,
			},
			ThanatosAboutNyxAndChaos01 =
			{
				PlayOnce = true,
				UseableOffSource = true,
				UseInitialInteractSetup = true,
				RequiredTextLines = { "NyxChaosReunionQuestComplete", "ThanatosGift06" },
				EndVoiceLines =
				{
					PreLineWait = 0.4,
					UsePlayerSource = true,
					-- Least I could do for all she's done for me.
					{ Cue = "/VO/ZagreusHome_2499" },
				},
				{ Cue = "/VO/Thanatos_0580",
					Text = "Mother Nyx told me you restored the Eldest Sigil, Zagreus. I couldn't believe that was your doing. What you did... it meant a lot to her." },
				{ Cue = "/VO/ZagreusHome_2498", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusInteractionThoughtful", PreLineAnimTarget = "Hero",
					Text = "It was my pleasure. Say, did Nyx ever let on to you of her relationship with Chaos, at any point?" },
				{ Cue = "/VO/Thanatos_0581",
					PreLineAnim = "ThanatosIdleInhouseFidget_HairFlick",
					Text = "No, not everyone is quite as sharing as you are, Zag. Nor as inclined to pry. But still... in this case, sounds like it worked out. So, thank you. If she hasn't said as much." },
				OnQueuedFunctionName = "CheckDistanceTriggerThread",
				OnQueuedFunctionArgs = PresetEventArgs.Brooding,
			},

			ThanatosAboutNyxAndDusa01 =
			{
				Priority = true,
				PlayOnce = true,
				UseableOffSource = true,
				GiftableOffSource = true,
				UseInitialInteractSetup = true,
				RequiredTextLines = { "NyxAboutDusa01", "ThanatosGift03" },
				RequiredFalseTextLines = { "DusaWithNyx03" },
				EndVoiceLines =
				{
					PreLineWait = 0.6,
					UsePlayerSource = true,
					-- ...Tsch.
					{ Cue = "/VO/ZagreusHome_2485" },
				},
				{ Cue = "/VO/Thanatos_0569",
					Text = "I can tell when Mother Nyx is upset, Zagreus. And I know better than to ask her, so I'm going to ask you. What happened?" },
				{ Cue = "/VO/ZagreusHome_2483", Portrait = "Portrait_Zag_Defiant_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Text = "Probably because Nyx took issue with me chatting with Dusa from time to time. Is that really so unbecoming of me? Not even Father seems to care." },
				{ Cue = "/VO/Thanatos_0570",
					Emote = "PortraitEmoteFiredUp",
					AngleTowardHero = true,					
					Text = "Well, Mother cares! And I, for one, know better than to question it. None of us would be here were it not for her. If she has a request for you, you honor it." },
				{ Cue = "/VO/ZagreusHome_2484", Portrait = "Portrait_Zag_Defiant_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "You're telling me I should just start ignoring Dusa from now on, like you do?" },
				{ Cue = "/VO/Thanatos_0571", PostLineThreadedFunctionName = "ThanatosExit", PostLineFunctionArgs = { AnimationState = "NPCThanatosExited", WaitTime = 0.3, SkipExitReaction = true },
					Text = "You're a god. I'm telling you to learn to act like one." },
				OnQueuedFunctionName = "CheckDistanceTriggerThread",
				OnQueuedFunctionArgs = PresetEventArgs.Brooding,
			},
			ThanatosAboutNyxAndDusa02 =
			{
				SuperPriority = true,
				PlayOnce = true,
				UseableOffSource = true,
				InitialGiftableOffSource = true,
				UseInitialInteractSetup = true,
				RequiredFalseTextLinesThisRun = { "PersephoneFirstMeeting", "Ending01", "OlympianReunionQuestComplete" },
				RequiredTextLines = { "ThanatosAboutNyxAndDusa01" },
				MaxRunsSinceAnyTextLines = { TextLines = { "ThanatosAboutNyxAndDusa01" }, Count = 6 },
				RequiredFalseTextLines = { "DusaWithNyx03" },
				EndVoiceLines =
				{
					PreLineWait = 0.4,
					UsePlayerSource = true,
					-- Thanks...
					{ Cue = "/VO/ZagreusHome_2487" },
				},
				{  Cue = "/VO/ZagreusHome_2486", Portrait = "Portrait_Zag_Empathetic_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "I'm sorry I raised my voice with you last time, Than. I know Nyx puts so much into this place, and has nurtured so many of us here." },
				{ Cue = "/VO/Thanatos_0572",
					Text = "It's not me you should be apologizing to, Zag. Although, with Mother Nyx, sometimes it's best to leave her be. As for Dusa... I didn't mean to suggest you should abandon your friend." },
			},
			ThanatosAboutNyxAndDusa03 =
			{
				Priority = true,
				PlayOnce = true,
				UseableOffSource = true,
				UseInitialInteractSetup = true,
				RequiredTextLines = { "DusaVsNyx04", "ThanatosGift03" },
				RequiredFalseTextLines = { "DusaWithNyx03", "MegaeraAboutDusaVsNyx02" },
				GiftableOffSource = true,
				EndVoiceLines =
				{
					PreLineWait = 0.6,
					UsePlayerSource = true,
					-- Maybe you should.
					{ Cue = "/VO/ZagreusHome_2490" },
				},
				{ Cue = "/VO/ZagreusHome_2488", Portrait = "Portrait_Zag_Empathetic_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusInteractionThoughtful", PreLineAnimTarget = "Hero",
					Text = "Nyx let her go, Than. Dusa, I mean, she's gone. What do I do?" },
				{ Cue = "/VO/Thanatos_0573",
					PreLineAnim = "ThanatosIdleInhouseFidget_HairFlick",
					Text = "If I were you, I would start getting over it as soon as possible. But, I don't see you doing that. Just remember... maybe this isn't about you. Dusa's job may have been far too much for her. Mother Nyx is strict, but she is fair." },
				{ Cue = "/VO/ZagreusHome_2489", Portrait = "Portrait_Zag_Defiant_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Text = "Then she should give Dusa another chance. Has Meg heard? She'll be furious." },
				{ Cue = "/VO/Thanatos_0574", PostLineThreadedFunctionName = "ThanatosExit", PostLineFunctionArgs = { AnimationState = "NPCThanatosExited", WaitTime = 3, SkipExitReaction = true },
					Text = "Megaera will be no such thing. Some of us don't default to questioning every decision from our superiors." },
			},
			ThanatosAboutNyxAndDusa04 =
			{
				Priority = true,
				PlayOnce = true,
				UseableOffSource = true,
				UseInitialInteractSetup = true,
				RequiredTextLines = { "DusaWithNyx03", "ThanatosGift03" },
				MaxRunsSinceAnyTextLines = { TextLines = { "DusaWithNyx03" }, Count = 15 },
				EndVoiceLines =
				{
					PreLineWait = 0.4,
					UsePlayerSource = true,
					-- I don't think we have to worry about that.
					{ Cue = "/VO/ZagreusHome_2492" },
				},
				{ Cue = "/VO/Thanatos_0575",
					Text = "I saw that Dusa's back. You must have worked your ways on Mother Nyx. Don't know how you do it. Can't remember the last time I saw her budge on anything." },
				{ Cue = "/VO/ZagreusHome_2491", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Text = "Nyx genuinely cares about this House and all of us. She had to come around on something like this. Dusa's one of the hardest workers here. Even if she has room to improve." },
				{ Cue = "/VO/Thanatos_0576",
					PreLineAnim = "ThanatosIdleInhouseFidget_HairFlick",
					Text = "Incessant hard work can lead to trouble, Zagreus. I think maybe that's why Mother was concerned. We all know how your father can get. She doesn't need someone like Dusa taking after him." },
			},

			ThanatosAboutHypnos01 =
			{
				PlayOnce = true,
				UseInitialInteractSetup = true,
				UseableOffSource = true,
				RequiredTextLines = { "ThanatosWithHypnos01", "HypnosGift03" },
				RequiredAnyTextLines = { "ThanatosGift04", "ThanatosGift04_B" },
				EndVoiceLines =
				{
					PreLineWait = 0.4,
					UsePlayerSource = true,
					-- He's like an annoying little brother to me.
					{ Cue = "/VO/ZagreusHome_0815" },
				},
				{ Cue = "/VO/ZagreusHome_0813", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "Than, you know, Hypnos, he adores you. Besides just driving you to madness, now and then." },
				{ Cue = "/VO/Thanatos_0368",
					PreLineAnim = "ThanatosIdleInhouseFidget_HairFlick",
					Text = "I'm rather used to the relationship, thanks, Zagreus. Seems like you got to know him pretty well." },
				{ Cue = "/VO/ZagreusHome_0814", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Text = "Well, dying repeatedly can end up bringing people closer, I suppose." },
				{ Cue = "/VO/Thanatos_0369",
					Text = "I don't know what's worse, dying repeatedly or Hypnos. Although, I guess I'm grateful for your patience with him, Zag." },
				OnQueuedFunctionName = "CheckDistanceTriggerThread",
				OnQueuedFunctionArgs = PresetEventArgs.Brooding,
			},
			-- brothers quest
			ThanatosAboutHypnos02 =
			{
				Priority = true,
				PlayOnce = true,
				UseInitialInteractSetup = true,
				UseableOffSource = true,
				RequiredTextLines = { "ThanatosAboutHypnos01", "HypnosAboutThanatos03" },
				EndVoiceLines =
				{
					PreLineWait = 0.4,
					UsePlayerSource = true,
					RequiredMinElapsedTime = 3,
					-- Probably!
					{ Cue = "/VO/ZagreusHome_2534" },
				},
				{ Cue = "/VO/ZagreusHome_2532", Portrait = "Portrait_Zag_Defiant_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Text = "Got to tell you something, Than. You can be pretty mean to Hypnos! And that's coming from someone who's mean to Hypnos a lot. Believe me, I completely understand, but... still." },
				{ Cue = "/VO/Thanatos_0628",
					PreLineAnim = "ThanatosIdleInhouseFidget_HairFlick",
					Text = "He's utterly incompetent, Zag. He wouldn't even be here if Mother Nyx wasn't trying to whip him into some sort of shape." },
				{ Cue = "/VO/ZagreusHome_2533", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusInteractionThoughtful", PreLineAnimTarget = "Hero",
					Text = "Yeah, but... pretty much none of this would be here, were it not for Nyx, right? Anyway my point is not that you should go easy on him, but... there must be some way not to get as frustrated. You'll both be better off." },
				{ Cue = "/VO/Thanatos_0629",
					Emote = "PortraitEmoteFiredUp",
					Text = "I know, it's just...! You're right. I catch myself feeling particularly horrid after talking to him. He can be very frustrating, and self-absorbed... though, I know he means well. Probably." },
				OnQueuedFunctionName = "CheckDistanceTriggerThread",
				OnQueuedFunctionArgs = PresetEventArgs.Brooding,
			},
			-- brothers quest
			ThanatosAboutHypnos03 =
			{
				Priority = true,
				PlayOnce = true,
				UseInitialInteractSetup = true,
				UseableOffSource = true,
				RequiredTextLines = { "ThanatosWithHypnos07" },
				EndVoiceLines =
				{
					PreLineWait = 0.4,
					UsePlayerSource = true,
					-- ...Who doesn't have room for improvement, though, right?
					{ Cue = "/VO/ZagreusHome_2537" },
				},
				{ Cue = "/VO/ZagreusHome_2535", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Text = "I can hardly believe it. Hypnos finally came out on top, huh? Even Father seemed pleased with him. That's more than I get." },
				{ Cue = "/VO/Thanatos_0630",
					Text = "I can hardly believe it either, Zagreus. But then again, he's one of Mother Nyx's offspring, so we shouldn't be so surprised. Though, I have to thank you for talking to me about him a while back..." },
				{ Cue = "/VO/ZagreusHome_2536", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "Oh, that? I noticed you two getting along better recently. So what's your secret?" },
				{ Cue = "/VO/Thanatos_0631", Portrait = "Portrait_Thanatos_Pleased_01",
					PreLineAnim = "ThanatosIdleInhouseFidget_HairFlick",
					Text = "He loves lists. Doesn't do well with subtlety, so much, but spell things out for him, and he will get right on it. Long as he's awake, at least. He's still working on that part." },
				OnQueuedFunctionName = "CheckDistanceTriggerThread",
				OnQueuedFunctionArgs = PresetEventArgs.Brooding,
			},
			ThanatosAboutSingersReunionQuestComplete01 =
			{
				PlayOnce = true,
				UseableOffSource = true,
				GiftableOffSource = true,
				UseInitialInteractSetup = true,
				RequiredTextLines = { "ThanatosGift01", "OrpheusAboutSingersReunionQuest01" },
				EndVoiceLines =
				{
					PreLineWait = 0.4,
					UsePlayerSource = true,
					-- ...Than, wait...!
					{ Cue = "/VO/ZagreusHome_2523" },
				},
				{ Cue = "/VO/Thanatos_0618",
					Text = "I saw you voided the old pact between Orpheus and your father. So now he gets to see his wife from time to time?" },
				{ Cue = "/VO/ZagreusHome_2521", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "He does, and I did, Than. I think we can stand to see a little less of him if it means he can be happier for the remainder of eternity." },
				{ Cue = "/VO/Thanatos_0619",
					PreLineAnim = "ThanatosIdleInhouseFidget_HairFlick",
					Text = "You really need to learn to stop meddling in others' affairs. Did either of them even ask your help? I can't imagine Orpheus did, and I'm guessing his wife's feelings were complicated, too, from what I know of the circumstances of their final meeting." },
				{ Cue = "/VO/ZagreusHome_2522", Portrait = "Portrait_Zag_Defiant_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Emote = "PortraitEmoteSurprise",
					Text = "What? Orpheus is my mate, Than. And I came to know his muse, Eurydice, as well. They still had feelings for each other, but with no way to communicate." },
				{ Cue = "/VO/Thanatos_0620", PostLineThreadedFunctionName = "ThanatosExit", PostLineFunctionArgs = { AnimationState = "NPCThanatosExited", WaitTime = 0.3, SkipExitReaction = true },
					Text = "So you decided to step in. {#DialogueItalicFormat}Tsch{#PreviousFormat}. I think my instincts on the subject are just not the same as yours. I know your intentions were in the right place, but... you know what, forget it." },
				OnQueuedFunctionName = "CheckDistanceTriggerThread",
				OnQueuedFunctionArgs = PresetEventArgs.Brooding,
			},

			ThanatosAboutQuestLog01 =
			{
				PlayOnce = true,
				UseableOffSource = true,
				UseInitialInteractSetup = true,
				RequiredMinNPCInteractions = { NPC_Thanatos_01 = 1 },
				RequiredCosmetics = { "QuestLog", },
				RequiredFalseFlags = { "InFlashback" },
				EndVoiceLines =
				{
					PreLineWait = 0.4,
					UsePlayerSource = true,
					RequiredMinElapsedTime = 2,
					-- I can hazard a guess...
					{ Cue = "/VO/ZagreusHome_2520" },
				},
				{ Cue = "/VO/Thanatos_0616",
					Text = "I saw the Fated List of Minor Prophecies was requisitioned through the House Contractor, Zagreus. Didn't take you for an agent of the Fates." },
				{ Cue = "/VO/ZagreusHome_2519", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "Turns out I'm full of surprises, Thanatos. Though, the Three Fates are your sisters, aren't they? Surprised you didn't requisition the List yourself." },
				{ Cue = "/VO/Thanatos_0617",
					PreLineAnim = "ThanatosIdleInhouseFidget_HairFlick",
					Text = "Guess it just wasn't meant to be. Though, I don't know them at all. Keep only to themselves, somewhere. Sometimes I wonder whether everything really is preordained, or whether they're making all this up as they go." },
				OnQueuedFunctionName = "CheckDistanceTriggerThread",
				OnQueuedFunctionArgs = PresetEventArgs.Brooding,
			},

			ThanatosRunCleared01 =
			{
				Priority = true,
				PlayOnce = true,
				UseableOffSource = true,
				GiftableOffSource = true,
				RequiresRunCleared = true,
				UseInitialInteractSetup = true,
				RequiredTextLines = { "PersephoneFirstMeeting" },
				RequiredFalseTextLines = { "Ending01" },
				-- I'm planning to.
				EndCue = "/VO/ZagreusHome_1550",
				EndWait = 0.45,
				{ Cue = "/VO/Thanatos_0544",
					AngleTowardHero = true,
					Text = "You took Lord Hades down... you really did. I've never seen him quite like this before. I could scarce believe it when he came up through the Pool of Styx, himself..." },
				{ Cue = "/VO/ZagreusHome_1549", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Text = "Seemed only fair in the grand scheme of things. He slew his own parents at some point, right? I think the Fates enjoy this sort of thing." },
				{ Cue = "/VO/Thanatos_0545", PostLineThreadedFunctionName = "ThanatosExit", PostLineFunctionArgs = { AnimationState = "NPCThanatosExited", WaitTime = 3, SkipExitReaction = true, },
					Text = "Come on, Zag, take some credit where it's due. Though... you'll forgive me if I keep my distance for a little while, as I think you have some issues to resolve. You watch yourself." },
				OnQueuedFunctionName = "CheckDistanceTriggerThread",
				OnQueuedFunctionArgs = PresetEventArgs.Brooding,
			},

			ThanatosAboutStaying01 =
			{
				Priority = true,
				PlayOnce = true,
				UseableOffSource = true,
				GiftableOffSource = true,
				UseInitialInteractSetup = true,
				RequiredTextLines = { "ThanatosFieldAboutPersephoneFirstMeeting01", "ThanatosGift05" },
				EndVoiceLines =
				{
					PreLineWait = 0.4,
					UsePlayerSource = true,
					-- I'll hold you to that.
					{ Cue = "/VO/ZagreusHome_3432" },
				},
				{ Cue = "/VO/Thanatos_0551",
					PreLineWait = 0.35,
					Text = "...You're really staying, Zagreus? I mean... between the generally ill-conceived attempts to reach the surface every so often." },
				{ Cue = "/VO/ZagreusHome_3431", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "I am. I figured it's the only way I could continue interrupting you while you try to do your job. You work too hard, Than, live a little, right?" },
				{ Cue = "/VO/Thanatos_0552", PostLineThreadedFunctionName = "ThanatosExit", PostLineFunctionArgs = { AnimationState = "NPCThanatosExited", WaitTime = 1.85, SkipExitReaction = true },
					Text = "{#DialogueItalicFormat}Heh{#PreviousFormat}. Well then, I'll see what I can do about that. You'll have to show me what you mean by {#DialogueItalicFormat}'live a little'{#PreviousFormat}, though." },
				OnQueuedFunctionName = "CheckDistanceTriggerThread",
				OnQueuedFunctionArgs = PresetEventArgs.Brooding,
			},

			-- misnomer; event moved later
			ThanatosAboutPersephoneMeeting02 =
			{
				Priority = true,
				PlayOnce = true,
				UseInitialInteractSetup = true,
				UseableOffSource = true,
				RequiredTextLines = { "PersephoneMeeting04" },
				RequiredAnyTextLines = { "ThanatosBackstory01", "ThanatosBackstory01_B" },
				RequiredAnyOtherTextLines = { "ThanatosFieldAboutPersephoneFirstMeeting01", "ThanatosGift05" },
				RequiredFalseTextLines = { "Ending01" },
				EndVoiceLines =
				{
					PreLineWait = 0.45,
					UsePlayerSource = true,
					-- Yeah.
					{ Cue = "/VO/ZagreusHome_3231" },
				},
				{ Cue = "/VO/ZagreusHome_3228", Portrait = "Portrait_Zag_Empathetic_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "Have to tell you something, Than. My birthmother... she didn't abandon me. Or at least... she didn't think she did." },
				{ Cue = "/VO/Thanatos_0647",
					Text = "She didn't {#DialogueItalicFormat}think {#PreviousFormat}she did? What are you talking about, Zag? She left when you were born. Was never seen or spoken of again, until all this with you." },
				{ Cue = "/VO/ZagreusHome_3229", Portrait = "Portrait_Zag_Serious_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Text = "Well, it turns out... she never knew I was alive, at all. Complications when I was born. She had been struggling already here, and it was all too much, I guess. By the time Nyx brought me back... my mother was long gone." },
				{ Cue = "/VO/Thanatos_0648",
					Emote = "PortraitEmoteSurprise",
					AngleTowardHero = true,
					Text = "{#DialogueItalicFormat}What{#PreviousFormat}? But, Mother Nyx, and your father, they... could have sent for her, let her know... they must have known where she went!" },
				{ Cue = "/VO/ZagreusHome_3230", Portrait = "Portrait_Zag_Serious_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusInteractionThoughtful", PreLineAnimTarget = "Hero",
					Text = "Nyx knew. But, Father must have convinced her that my mother... that it would be for the best if she never found out. About me. So she could live in peace. Away from all of this. And away from Olympus." },
				{ Cue = "/VO/Thanatos_0649",
					PreLineWait = 0.5,
					Text = "That's... a lot to take in." },
				OnQueuedFunctionName = "CheckDistanceTriggerThread",
				OnQueuedFunctionArgs = PresetEventArgs.Brooding,
			},

			ThanatosAboutRelationship01 =
			{
				Priority = true,
				PlayOnce = true,
				UseableOffSource = true,
				GiftableOffSource = true,
				UseInitialInteractSetup = true,
				RequiredTextLines = { "ThanatosGift06" },
				RequiredAnyTextLines = { "Ending01", "ThanatosBackstory02" },
				RequiredFalseTextLines = { "BecameCloseWithThanatos01", "BecameCloseWithThanatos01_B" },
				-- Wait! Ugh.
				EndCue = "/VO/ZagreusHome_2542",
				EndWait = 0.4,
				{ Cue = "/VO/Thanatos_0640",
					PreLineWait = 0.35,
					Text = "...Answer me something, Zagreus. What am I to you, exactly, as of late? Because, sometimes, with you, I..." },
				{ Cue = "/VO/ZagreusHome_2541", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "I'm sometimes at a loss myself, Thanatos. Though one thing I know is it's always really good running into you out there, it's... well, exciting. Sparring with you and all that! And then, I know it means that I might get to see you here." },
				{ Cue = "/VO/Thanatos_0641",
					AngleTowardHero = true,
					PostLineThreadedFunctionName = "ThanatosExit", PostLineFunctionArgs = { AnimationState = "NPCThanatosExited", WaitTime = 0.2, SkipExitReaction = true },
					Text = "Well, here I am. But you didn't answer my question. Though, you know something? Forget I asked. I've got to go." },
				OnQueuedFunctionName = "CheckDistanceTriggerThread",
				OnQueuedFunctionArgs = PresetEventArgs.Brooding,
			},
			ThanatosAboutRelationship02 =
			{
				PlayOnce = true,
				UseableOffSource = true,
				UseInitialInteractSetup = true,
				RequiredAnyTextLines = { "BecameCloseWithThanatos01Than_GoToHim", "BecameCloseWithThanatos01_BThan_GoToHim" },
				MinRunsSinceAnyTextLines = { TextLines = { "BecameCloseWithThanatos01Than_GoToHim", "BecameCloseWithThanatos01_BThan_GoToHim" }, Count = 8 },
				-- ...Good.
				EndCue = "/VO/ZagreusHome_2545",
				EndWait = 0.4,
				{ Cue = "/VO/ZagreusHome_2543", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "Hey, Than, tell me something, and forgive the awkward question here, but... are we good? I mean... between us. Any infuriating habits I should know about, that sort of thing?" },
				{ Cue = "/VO/Thanatos_0642",
					PreLineAnim = "ThanatosIdleInhouseFidget_HairFlick",
					Text = "You know I don't have time to list all your infuriating habits, Zagreus. Though... if any of them were outside my usual tolerance threshold, I promise to let you know, all right?" },
				{ Cue = "/VO/ZagreusHome_2544", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusInteractionThoughtful", PreLineAnimTarget = "Hero",
					Text = "So then, we're good. Because I think we're good, but I just wanted to make sure." },
				{ Cue = "/VO/Thanatos_0643", Portrait = "Portrait_Thanatos_Pleased_01", PostLineThreadedFunctionName = "ThanatosExit", PostLineFunctionArgs = { AnimationState = "NPCThanatosExited", WaitTime = 1.2, SkipExitReaction = true },
					Text = "We're good. See you, all right?" },
				OnQueuedFunctionName = "CheckDistanceTriggerThread",
				OnQueuedFunctionArgs = PresetEventArgs.Brooding,
			},

			ThanatosAboutRelationship03 =
			{
				PlayOnce = true,
				UseableOffSource = true,
				UseInitialInteractSetup = true,
				RequiredTextLines = { "ThanatosAboutRelationship02", "Ending01" },
				MinRunsSinceAnyTextLines = { TextLines = { "ThanatosAboutRelationship02" }, Count = 8 },
				EndVoiceLines =
				{
					PreLineWait = 0.5,
					UsePlayerSource = true,
					RequiredMinElapsedTime = 3,
					-- Definitely.
					{ Cue = "/VO/ZagreusHome_2548" },
				},
				{ Cue = "/VO/Thanatos_0644", Portrait = "Portrait_Thanatos_Pleased_01",
					PreLineWait = 0.35,
					Text = "...It just struck me how much you've changed, Zag. I still remember when you kept trying to singe Cerberus' tail with your feet. Haven't seen you do that in a while." },
				{ Cue = "/VO/ZagreusHome_2546", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "Oh, I definitely learned my lesson there. But, thanks, I guess. You've opened up a lot, yourself, by the way. Talking to you lately, it's a world apart from the first time. You looked like you were going to slice my head off." },
				{ Cue = "/VO/Thanatos_0645", Portrait = "Portrait_Thanatos_Pleased_01",
					PreLineAnim = "ThanatosIdleInhouseFidget_HairFlick",
					Text = "It sure must have taken a lot of courage for you to come right up and tell me you thought I looked better before I cut my hair. I still ask myself how come I didn't keep ignoring you after that. Or slice your head off." },
				{ Cue = "/VO/ZagreusHome_2547", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Text = "Well, the option is always available, I guess. Hey, you ever think of growing it out again?" },
				{ Cue = "/VO/Thanatos_0646", Portrait = "Portrait_Thanatos_Default_01",
					Text = "No. It kept getting in my way. Sliced it right off and never looked back. So... plenty of personal growth all around, then?" },
				OnQueuedFunctionName = "CheckDistanceTriggerThread",
				OnQueuedFunctionArgs = PresetEventArgs.Brooding,
			},

			-- ending
			ThanatosAfterPersephoneMeeting03 =
			{
				Priority = true,
				PlayOnce = true,
				UseableOffSource = true,
				UseInitialInteractSetup = true,
				RequiredTextLines = { "ThanatosGift06" },
				RequiredAnyTextLines = { "ThanatosFieldAboutPersephoneFirstMeeting01", "ThanatosAboutPersephoneMeeting02" },
				RequiredFalseTextLinesLastRun = { "ThanatosAboutPersephoneMeeting02" },
				RequiredFalseTextLines = { "Ending01" },
				RequiresLastRunCleared = true,
				EndVoiceLines =
				{
					PreLineWait = 0.5,
					UsePlayerSource = true,
					RequiredMinElapsedTime = 3,
					-- I hope so.
					{ Cue = "/VO/ZagreusHome_3233" },
				},
				{ Cue = "/VO/Thanatos_0650",
					Text = "How's everything been going with your mother, Zagreus? You've been pretty quiet about the whole thing, is everything all right? You can tell me." },
				{ Cue = "/VO/ZagreusHome_3232", Portrait = "Portrait_Zag_Serious_01", Speaker = "CharProtag",
					Text = "Thank you for asking, Than. I don't know how much more I can say right now, given everything. I'm still figuring it out myself. I'm thankful you've been there to help me get to see her from time to time." },
				{ Cue = "/VO/Thanatos_0651",
					Text = "Well, if there's something more that I can do to support you in this, let me know, all right? Sounds like you're making progress, at least." },
				OnQueuedFunctionName = "CheckDistanceTriggerThread",
				OnQueuedFunctionArgs = PresetEventArgs.Brooding,
			},

			ThanatosPostEnding01 =
			{
				SuperPriority = true,
				PlayOnce = true,
				UseInitialInteractSetup = true,
				UseableOffSource = true,
				GiftableOffSource = true,
				RequiredTextLines = { "Ending01" },
				MaxRunsSinceAnyTextLines = { TextLines = { "Ending01" }, Count = 20 },
				EndVoiceLines =
				{
					PreLineWait = 0.5,
					UsePlayerSource = true,
					-- Bye, Than.
					{ Cue = "/VO/ZagreusHome_3240" },
				},
				{ Cue = "/VO/Thanatos_0660",
					Text = "So, between us... all your attempting to break out of here, over and over... you're now authorized to keep that up? Exposing weaknesses in Underworld security, huh." },
				{ Cue = "/VO/ZagreusHome_3238", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "Something like that. I guess I've gotten pretty decent about escaping from a place that's known for being inescapable. Lets me keep in touch with the other side of the family. Work with you from time to time." },
				{ Cue = "/VO/Thanatos_0661",
					PreLineAnim = "ThanatosIdleInhouseFidget_HairFlick",
					Text = "I have to say I never thought I'd hear you talking about looking forward to working, Zag. You feeling all right? Spending too much time on the surface, maybe?" },
				{ Cue = "/VO/ZagreusHome_3239", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Text = "Oh I'm feeling fine. I never thought I was much good at anything, but... if my fated purpose is to be a master of frustrating Father's efforts to make everything strictly perfect, I think I'm OK with that." },
				{ Cue = "/VO/Thanatos_0662",
					PostLineThreadedFunctionName = "ThanatosExit", PostLineFunctionArgs = { AnimationState = "NPCThanatosExited", WaitTime = 2.0, SkipExitReaction = true },
					Text = "Well, then, I'll be looking forward to working with you in a more official capacity. Even if we're still supposed to keep it all under wraps, for sake of appearances. See you out there." },
				OnQueuedFunctionName = "CheckDistanceTriggerThread",
				OnQueuedFunctionArgs = PresetEventArgs.Brooding,
			},

			ThanatosPostEnding02 =
			{
				PlayOnce = true,
				UseInitialInteractSetup = true,
				UseableOffSource = true,
				GiftableOffSource = true,
				RequiredTextLines = { "Ending01", "ThanatosGift05" },
				MinRunsSinceAnyTextLines = { TextLines = { "Ending01", "ThanatosPostEnding01" }, Count = 4 },
				AreIdsAlive = { 555714 },
				EndVoiceLines =
				{
					PreLineWait = 0.4,
					UsePlayerSource = true,
					RequiredMinElapsedTime = 2,
					-- Oh I entirely agree with that.
					{ Cue = "/VO/ZagreusHome_3235" },
				},
				{ Cue = "/VO/Thanatos_0652",
					Text = "Have to tell you, Zag. It's different, with your mother around here. Don't mean to be so obvious, but... it's something about your father, I suppose." },
				{ Cue = "/VO/ZagreusHome_3234", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "I think they have a lot of work ahead of them. As long as they're trying... he's trying... I think I'm good with that, for now. Though, I am glad she's here." },
				{ Cue = "/VO/Thanatos_0653",
					PostLineThreadedFunctionName = "ThanatosExit", PostLineFunctionArgs = { AnimationState = "NPCThanatosExited", WaitTime = 1.8, SkipExitReaction = true },
					Text = "I'm beginning to think we all are. And, please don't take this the wrong way, but... I think you and your father have a lot of work ahead of you, as well." },
				OnQueuedFunctionName = "CheckDistanceTriggerThread",
				OnQueuedFunctionArgs = PresetEventArgs.Brooding,
			},

			ThanatosPostEpilogue01 =
			{
				SuperPriority = true,
				PlayOnce = true,
				UseInitialInteractSetup = true,
				UseableOffSource = true,
				GiftableOffSource = true,
				RequiredTextLines = { "OlympianReunionQuestComplete", "ThanatosGift03" },
				EndVoiceLines =
				{
					PreLineWait = 0.45,
					UsePlayerSource = true,
					-- Bye, Than.
					{ Cue = "/VO/ZagreusHome_3240" },
				},
				{ Cue = "/VO/Thanatos_0658",
					Text = "Your mother's very clever, Zagreus. I always thought you got that from your father, now I'm not so sure. So all is well with the Olympians, again?" },
				{ Cue = "/VO/ZagreusHome_3236", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Text = "For now, I think it is. But, you know how they are. How we all are, I guess." },
				{ Cue = "/VO/Thanatos_0659", Portrait = "Portrait_Thanatos_Pleased_01",
					PreLineAnim = "ThanatosIdleInhouseFidget_HairFlick",
					PostLineThreadedFunctionName = "ThanatosExit", PostLineFunctionArgs = { AnimationState = "NPCThanatosExited", WaitTime = 1.7, SkipExitReaction = true },
					Text = "{#DialogueItalicFormat}Hah{#PreviousFormat}! Yes, indeed. Well, then! I guess enjoy it while it lasts, as mortals sometimes say. I'll see you out there." },
				OnQueuedFunctionName = "CheckDistanceTriggerThread",
				OnQueuedFunctionArgs = PresetEventArgs.Brooding,
			},

			ThanatosWithHades01 =
			{
				Priority = true,
				Partner = "NPC_Hades_01",
				RequiredTextLines = { "ThanatosHomeMiscMeeting01" },
				RequiredFalseTextLines = { "Ending01" },
			 	RequiredFalseValues = { CurrentEmployeeOfTheMonth = "Thanatos" },
				PlayOnce = true,
				UseText = "UseListenNPC",
				BlockDistanceTriggers = true,
				StatusAnimation = false,
				UseableOffSource = true,
				GiftableOffSource = true,
				TeleportToId = 370006,
				TeleportOffsetX = -485,
				TeleportOffsetY = 350,
				AngleTowardTargetId = 370006,
				InteractDistance = 400,
				EndVoiceLines =
				{
					PreLineWait = 0.5,
					UsePlayerSource = true,
					RequiredMinElapsedTime = 3,
					-- <Sigh>
					{ Cue = "/VO/ZagreusHome_0788" },
				},
				{ Cue = "/VO/Hades_0354", Portrait = "Portrait_Hades_Default_01", Speaker = "NPC_Hades_01",
					Text = "Such tardiness is oft to be expected from your brother, but from you, Thanatos? You're practically the god of punctuality." },
				{ Cue = "/VO/Thanatos_0373",
					Text = "My lord, I apologize sincerely for the lapse in my performance as of late. I think my duties following the recent war above took more of a toll from me than I expected." },
				{ Cue = "/VO/Hades_0355", Portrait = "Portrait_Hades_Default_01", Speaker = "NPC_Hades_01",
					Text = "Thanatos, even we gods require rest from time to time. Someday we might get it around here, once this accursed House is well in order, finally. Until then, I appreciate your diligence. Do not overextend yourself." },
				{ Cue = "/VO/Thanatos_0374", PostLineThreadedFunctionName = "ThanatosExit", PostLineFunctionArgs = { AnimationState = "NPCThanatosExited", WaitTime = 0.3, SkipExitReaction = true },
					PostLineAnim = "Hades_HouseWritingLoop_IdleLoop_Start", PostLineAnimTarget = 370006,
					Text = "Thank you, my lord. I am at your service. I'll be getting back to my assignment now." },
			},

			ThanatosWithHades02 =
			{
				PlayOnce = true,
				Partner = "NPC_Hades_01",
				RequiredTextLines = { "ThanatosWithHades01", "ThanatosGift03" },
				RequiredFalseTextLines = { "ThanatosWithHades03" },
				RequiredEncounterThisRun = "ThanatosTartarus",
				RequiredFalseTextLines = { "Ending01" },
			 	RequiredFalseValues = { CurrentEmployeeOfTheMonth = "Thanatos" },
				StatusAnimation = false,
				UseText = "UseListenNPC",
				BlockDistanceTriggers = true,
				UseableOffSource = true,
				GiftableOffSource = true,
				TeleportToId = 370006,
				TeleportOffsetX = -485,
				TeleportOffsetY = 350,
				AngleTowardTargetId = 370006,
				InteractDistance = 400,
				-- <Sigh>
				EndCue = "/VO/ZagreusField_1637",
				EndWait = 0.3,
				{ Cue = "/VO/Hades_0095", Portrait = "Portrait_Hades_Default_01", Speaker = "NPC_Hades_01", PreLineWait = 0.4,
					PostLineThreadedFunctionName = "ThanatosExit", PostLineFunctionArgs = { AnimationState = "NPCThanatosExited", WaitTime = 0.4, SkipExitReaction = true },
					PostLineAnim = "Hades_HouseWritingLoop_IdleLoop_Start", PostLineAnimTarget = 370006,
					Text = "...You have been rather slow about your duties, lately, Thanatos. And I'm receiving incident reports from Tartarus involving {#DialogueItalicFormat}you{#PreviousFormat}. Remember your responsibilities. And, remember, {#DialogueItalicFormat}I am watching you{#PreviousFormat}. Now, get out of my sight." },
			},

			ThanatosWithHades03 =
			{
				Priority = true,
				Partner = "NPC_Hades_01",
				RequiredTextLines = { "ThanatosGift06" },
				RequiredFalseTextLinesLastRun = { "ThanatosGift06" },
				MinRunsSinceAnyTextLines = { TextLines = { "ThanatosWithHades02" }, Count = 1 },
				RequiredFalseTextLines = { "Ending01", "ThanatosWithHades04" },
			 	RequiredFalseValues = { CurrentEmployeeOfTheMonth = "Thanatos" },
				PlayOnce = true,
				StatusAnimation = false,
				UseText = "UseListenNPC",
				BlockDistanceTriggers = true,
				UseableOffSource = true,
				GiftableOffSource = true,
				TeleportToId = 370006,
				TeleportOffsetX = -485,
				TeleportOffsetY = 350,
				AngleTowardTargetId = 370006,
				InteractDistance = 400,
				-- <Sigh>
				EndCue = "/VO/ZagreusHome_0788",
				EndWait = 0.8,
				{ Cue = "/VO/Hades_0625", Portrait = "Portrait_Hades_Default_01", Speaker = "NPC_Hades_01",
					PreLineAnim = "Hades_HouseFistSlam", PreLineAnimTarget = 370006,
					Emote = "PortraitEmoteAnger",
					PreLineWait = 0.35,
					Text = "You've been abetting him, Thanatos. Behind my back! Betrayal. I would ask you {#DialogueItalicFormat}why{#PreviousFormat}. Well?" },
				{ Cue = "/VO/Thanatos_0473",
					PreLineWait = 0.5,
					Text = "My lord, he and I... please. Go lock me up in Tartarus, or deal whichever justice you see fit, but... my loyalty is not subject to change. And I cannot stand by and watch you fight. The matter between you and Zagreus... there has to be some resolution, here." },
				{ Cue = "/VO/Hades_0626", Portrait = "Portrait_Hades_Default_01", Speaker = "NPC_Hades_01",
					PreLineWait = 0.4,
					Text = "You dare to lecture me? {#DialogueItalicFormat}Tsch{#PreviousFormat}. My loyal subjects are too few of late, I think. This is {#DialogueItalicFormat}your {#PreviousFormat}realm, not mine; I'll not cast you into that wretched pit. It's not like I have someone to replace you here, besides. Now then, begone." },
				{ Cue = "/VO/Thanatos_0474",
					PreLineWait = 0.35,
					PostLineThreadedFunctionName = "ThanatosExit", PostLineFunctionArgs = { AnimationState = "NPCThanatosExited", WaitTime = 0.5, SkipExitReaction = true },
					PostLineAnim = "Hades_HouseWritingLoop_IdleLoop_Start", PostLineAnimTarget = 370006,
					Text = "I'm deeply sorry, lord..." },
			},

			ThanatosWithHades04 =
			{
				Partner = "NPC_Hades_01",
				RequiredTextLines = { "ThanatosGift06", "Ending01", "LordHadesPostEnding01" },
				RequiredFalseTextLinesLastRun = { "ThanatosGift06", "Ending01" },
				MinRunsSinceAnyTextLines = { TextLines = { "ThanatosWithHades01", "ThanatosWithHades02", "ThanatosWithHades03" }, Count = 10 },
				PlayOnce = true,
				UseText = "UseListenNPC",
				BlockDistanceTriggers = true,
				UseableOffSource = true,
				GiftableOffSource = true,
				TeleportToId = 370006,
				TeleportOffsetX = -485,
				TeleportOffsetY = 350,
				AngleTowardTargetId = 370006,
				InteractDistance = 400,
				{ Cue = "/VO/Thanatos_0546",
					PreLineWait = 0.5,
					Text = "You wished to see me once again, my lord? It's been... a while since I had an audience with you." },
				{ Cue = "/VO/Hades_1186", Portrait = "Portrait_Hades_Default_01", Speaker = "NPC_Hades_01",
					PreLineWait = 0.35,
					Text = "It has. The last time, what was it you said? That there must be some resolution to the {#DialogueItalicFormat}matter{#PreviousFormat}, as you called it, with my son? Well it is settled, now. Isn't it, boy?" },
				{ Cue = "/VO/ZagreusHome_3429", Portrait = "Portrait_Zag_Defiant_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "I mean, you're still trying just as hard as ever to prevent me from escaping past the threshold of your realm, so, I don't know if I'd exactly call the matter {#DialogueItalicFormat}settled{#PreviousFormat}. But... it does feel different, now." },
				{ Cue = "/VO/Hades_1187", Portrait = "Portrait_Hades_Default_01", Speaker = "NPC_Hades_01",
					PreLineWait = 0.35,
					Text = "And so you see... I would continue in our mutual good graces, if you please. This House can only function if the upper management is in accord. What do you say?" },
				{ Cue = "/VO/Thanatos_0547",
					PreLineWait = 0.4,
					PreLineAnim = "ThanatosIdleInhouseFidget_HairFlick",
					Text = "I say, you honor me, my lord. I appreciate you telling me, yourself. And I concur that, for the good of all the Underworld, it is imperative that each of us continue to perform at our full strength." },
				{ Cue = "/VO/Hades_1188", Portrait = "Portrait_Hades_Default_01", Speaker = "NPC_Hades_01",
					PostLineThreadedFunctionName = "ThanatosExit", PostLineFunctionArgs = { AnimationState = "NPCThanatosExited", WaitTime = 0.4, SkipExitReaction = true },
					PostLineAnim = "Hades_HouseWritingLoop_IdleLoop_Start", PostLineAnimTarget = 370006,
					Text = "Then, as you were. Dismissed." },
			},

			ThanatosWithHypnos01 =
			{
				Partner = "NPC_Hypnos_01",
				PlayOnce = true,
				RequiredMinNPCInteractions = { NPC_Thanatos_01 = 1 },
				UseText = "UseListenNPC",
				BlockDistanceTriggers = true,
				UseableOffSource = true,
				GiftableOffSource = true,
				TeleportToId = 370024,
				TeleportOffsetX = 110,
				TeleportOffsetY = 100,
				AngleTowardTargetId = 370024,
				InteractDistance = 400,
				EndVoiceLines =
				{
					PreLineWait = 0.8,
					UsePlayerSource = true,
					-- Oh...
					{ Cue = "/VO/ZagreusHome_0561" },
				},
				{ Cue = "/VO/Thanatos_0218",
					PreLineAnim = "ThanatosIdleInhouseFidget_HairFlick",
					Text = "Hypnos, really, that is the worst idea I think I've ever heard. Do you understand how little sense that makes?" },
				{ Cue = "/VO/Hypnos_0086", Portrait = "Portrait_ChildGhost_Default_01", Speaker = "NPC_Hypnos_01",
					PreLineAnim = "HypnosIdleGreeting", PreLineAnimTarget = 370024,
					Emote = "PortraitEmoteCheerful",
					Text = "I'm just saying, maybe if you just, I don't know, had all the mortals die at the same time, it'd save you a bunch of trips, and then we'd have more time to spend together, huh?" },
				{ Cue = "/VO/Thanatos_0219", PostLineThreadedFunctionName = "ThanatosExit", PostLineFunctionArgs = { AnimationState = "NPCThanatosExited", SkipExitReaction = true },
					Text = "I don't know why I bother with you, honestly. Get back to work, and let me do the same." },
			},
			ThanatosWithHypnos02 =
			{
				Partner = "NPC_Hypnos_01",
				RequiredTextLines = { "ThanatosWithHades01", "ThanatosWithHypnos01" },
				PlayOnce = true,
				UseText = "UseListenNPC",
				BlockDistanceTriggers = true,
				UseableOffSource = true,
				GiftableOffSource = true,
				TeleportToId = 370024,
				TeleportOffsetX = 110,
				TeleportOffsetY = 100,
				AngleTowardTargetId = 370024,
				InteractDistance = 400,
				EndVoiceLines =
				{
					PreLineWait = 0.9,
					UsePlayerSource = true,
					-- Oof.
					{ Cue = "/VO/ZagreusField_1146" },
				},
				{ Cue = "/VO/Thanatos_0220",
					PreLineAnim = "ThanatosIdleInhouseFidget_HairFlick",
					Text = "You cannot keep falling asleep on the job like this, Hypnos. This is completely unacceptable." },
				{ Cue = "/VO/Hypnos_0087", Portrait = "Portrait_ChildGhost_Default_01", Speaker = "NPC_Hypnos_01",
					Text = "Oh I just nodded off a little while, Thanatos! I rest easy knowing that you're here! But if I goofed, why, I guess you could kill me dead, or something, huh?" },
				{ Cue = "/VO/Thanatos_0221", PostLineThreadedFunctionName = "ThanatosExit", PostLineFunctionArgs = { AnimationState = "NPCThanatosExited", SkipExitReaction = true },
					Text = "Don't give me any ideas. Pull yourself together, and quit slacking off. The House needs help, and you've got a job to do. Now get to it!" },
			},
			-- brothers quest
			ThanatosWithHypnos03 =
			{
				Partner = "NPC_Hypnos_01",
				RequiredAnyTextLines = { "Ending01", "ThanatosAboutHypnos02" },
				MinRunsSinceAnyTextLines = { TextLines = { "ThanatosAboutHypnos02" }, Count = 1 },
				PlayOnce = true,
				UseText = "UseListenNPC",
				BlockDistanceTriggers = true,
				UseableOffSource = true,
				GiftableOffSource = true,
				TeleportToId = 370024,
				TeleportOffsetX = 110,
				TeleportOffsetY = 100,
				AngleTowardTargetId = 370024,
				InteractDistance = 400,
				EndVoiceLines =
				{
					PreLineWait = 0.7,
					UsePlayerSource = true,
					-- Oof, sorry Hypnos.
					{ Cue = "/VO/ZagreusHome_2774" },
				},
				{ Cue = "/VO/Thanatos_0632", PreLineWait = 0.3,
					PreLineAnim = "ThanatosIdleInhouseFidget_HairFlick",
					Emote = "PortraitEmoteDepressed",
					Text = "...Hypnos, this list has a bunch of tasks from I don't know how long ago, I mean... I took several of these last year! And you've already submitted this week's registrations, why isn't that marked complete?" },
				{ Cue = "/VO/Hypnos_0193", Portrait = "Portrait_ChildGhost_Default_01", Speaker = "NPC_Hypnos_01",
					Emote = "PortraitEmoteCheerful",
					Text = "Don't you know it, I was just about to get to that! Then you showed up, and I got so distracted, it just kind of slipped my mind! It happens, right?" },
				{ Cue = "/VO/Thanatos_0633",
					Text = "You need to focus, Brother. Or it'll be Lord Hades reprimanding you again, and that is not something you want." },
				{ Cue = "/VO/Hypnos_0194", Portrait = "Portrait_ChildGhost_Default_01", PreLineAnim = "HypnosIdleGreeting", PreLineAnimTarget = 370024, Speaker = "NPC_Hypnos_01", PostLineThreadedFunctionName = "ThanatosExit", PostLineFunctionArgs = { AnimationState = "NPCThanatosExited", SkipExitReaction = true, WaitTime = 0.3 },
					Text = "Oh, I don't know about that, last time I talked to Master he made {#DialogueItalicFormat}pretty clear {#PreviousFormat}he never wanted to speak to me again!" },
			},
			-- brothers quest
			ThanatosWithHypnos04 =
			{
				Partner = "NPC_Hypnos_01",
				RequiredTextLines = { "ThanatosWithHypnos03" },
				MinRunsSinceAnyTextLines = { TextLines = { "ThanatosWithHypnos03" }, Count = 2 },
				PlayOnce = true,
				UseText = "UseListenNPC",
				BlockDistanceTriggers = true,
				UseableOffSource = true,
				GiftableOffSource = true,
				TeleportToId = 370024,
				TeleportOffsetX = 110,
				TeleportOffsetY = 100,
				AngleTowardTargetId = 370024,
				InteractDistance = 400,
				EndVoiceLines =
				{
					PreLineWait = 0.35,
					ObjectType = "NPC_Hypnos_01",
					-- Don't worry, I won't!
					{ Cue = "/VO/Hypnos_0198" },
				},
				{ Cue = "/VO/Thanatos_0536", PreLineWait = 0.3,
					Text = "Hypnos, you're not irreplaceable, you understand? If you don't shape up, quickly, someone else is going to have your job." },
				{ Cue = "/VO/Hypnos_0197", Portrait = "Portrait_ChildGhost_Default_01", Speaker = "NPC_Hypnos_01",
					Emote = "PortraitEmoteCheerful",
					PreLineAnim = "HypnosIdleGreeting", PreLineAnimTarget = 370024,
					Text = "Oh I hear you loud and clear, Thanatos! And, let me tell you, they will have the {#DialogueItalicFormat}greatest job in all the Underworld{#PreviousFormat}!" },
				{ Cue = "/VO/Thanatos_0537",
					PreLineAnim = "ThanatosIdleInhouseFidget_HairFlick", PostLineThreadedFunctionName = "ThanatosExit", PostLineFunctionArgs = { AnimationState = "NPCThanatosExited", WaitTime = 2.2, SkipExitReaction = true },
					Text = "I feel like I'm not getting through to you, Brother. Well, after the inevitable happens, don't come telling me I didn't try." },
			},
			-- brothers quest
			ThanatosWithHypnos05 =
			{
				Priority = true,
				Partner = "NPC_Hypnos_01",
				RequiredTextLines = { "ThanatosWithHypnos04", "HypnosGift04" },
				MinRunsSinceAnyTextLines = { TextLines = { "ThanatosWithHypnos04" }, Count = 2 },
				PlayOnce = true,
				UseText = "UseListenNPC",
				BlockDistanceTriggers = true,
				UseableOffSource = true,
				GiftableOffSource = true,
				TeleportToId = 370024,
				TeleportOffsetX = 110,
				TeleportOffsetY = 100,
				AngleTowardTargetId = 370024,
				InteractDistance = 400,
				EndVoiceLines =
				{
					PreLineWait = 0.4,
					ObjectType = "NPC_Thanatos_01",
					-- If that'll be all?
					{ Cue = "/VO/Thanatos_0155" },
				},
				{ Cue = "/VO/Thanatos_0634", PreLineWait = 0.35,
					Text = "...Well, how often do you consult these lists you make? If you're spending too much time just making lists, you're not leaving time to get anything done." },
				{ Cue = "/VO/Hypnos_0195", Portrait = "Portrait_ChildGhost_Default_01", Speaker = "NPC_Hypnos_01",
					Emote = "PortraitEmoteAffection",
					PreLineAnim = "HypnosIdleGreeting", PreLineAnimTarget = 370024,
					Text = "Oh, I {#DialogueItalicFormat}love {#PreviousFormat}making my lists... I always illustrate the edges, each one is {#DialogueItalicFormat}unique{#PreviousFormat}! That way anyone who gets to review the work, it's a whole {#DialogueItalicFormat}experience{#PreviousFormat}, you know? But... I guess it does take up a lot of time." },
				{ Cue = "/VO/Thanatos_0635",
					PreLineAnim = "ThanatosIdleInhouseFidget_HairFlick",
					Text = "You'll have to work around the constraints of your responsibilities. You do take breaks, don't you? Before and after each break, maybe that's a time to make sure your own list of priorities is up-to-date." },
				{ Cue = "/VO/Hypnos_0196", Portrait = "Portrait_ChildGhost_Default_01", Speaker = "NPC_Hypnos_01", PostLineThreadedFunctionName = "ThanatosExit", PostLineFunctionArgs = { AnimationState = "NPCThanatosExited", WaitTime = 2.1, SkipExitReaction = true },
					Text = "Wait, my own list of priorities...? But, you're saying I can just go and make a list... for me? Huh. Whoa. {#DialogueItalicFormat}Whoa{#PreviousFormat}...." },
			},
			-- brothers quest
			ThanatosWithHypnos06 =
			{
				Priority = true,
				Partner = "NPC_Hypnos_01",
				RequiredTextLines = { "ThanatosWithHypnos05", "HypnosGift05", "ThanatosGift05" },
				MinRunsSinceAnyTextLines = { TextLines = { "ThanatosWithHypnos05" }, Count = 3 },
				PlayOnce = true,
				UseText = "UseListenNPC",
				BlockDistanceTriggers = true,
				UseableOffSource = true,
				GiftableOffSource = true,
				TeleportToId = 370024,
				TeleportOffsetX = 110,
				TeleportOffsetY = 100,
				AngleTowardTargetId = 370024,
				InteractDistance = 400,
				EndVoiceLines =
				{
					{
						PreLineWait = 0.4,
						UsePlayerSource = true,
						ObjectType = "NPC_Hypnos_01",
						-- Aww, thanks!
						{ Cue = "/VO/Hypnos_0200", PreLineAnim = "HypnosIdleGreeting" },
					},
					{
						PreLineWait = 0.15,
						UsePlayerSource = true,
						-- Good work, Hypnos!
						{ Cue = "/VO/ZagreusHome_2777" },
					},
				},
				{ Cue = "/VO/Thanatos_0538", PreLineWait = 0.3,
					Text = "I've heard that your reports have been immaculate of late, Hypnos. I have to know, who's helping you with them? It can't be Zagreus." },
				{ Cue = "/VO/Hypnos_0199", Portrait = "Portrait_ChildGhost_Default_01", Speaker = "NPC_Hypnos_01",
					Emote = "PortraitEmoteAffection",
					PreLineAnim = "HypnosIdleGreeting", PreLineAnimTarget = 370024,
					Text = "Oh, {#DialogueItalicFormat}haha{#PreviousFormat}, no, he doesn't help. It's just I took to listing only the important stuff up top, and spending just a bit more time on the priority reports, and... guess it's working out!" },
				{ Cue = "/VO/Thanatos_0539",
					PreLineAnim = "ThanatosIdleInhouseFidget_HairFlick", PostLineThreadedFunctionName = "ThanatosExit", PostLineFunctionArgs = { AnimationState = "NPCThanatosExited", WaitTime = 2.1, SkipExitReaction = true },
					Text = "Look, whatever the case... the work's improved. It's been observed. You ought to keep it up." },
			},
			-- brothers quest
			ThanatosWithHypnos07 =
			{
				Priority = true,
				Partner = "NPC_Hypnos_01",
				RequiredTextLines = { "ThanatosWithHypnos06", "HypnosGift06" },
				MinRunsSinceAnyTextLines = { TextLines = { "ThanatosWithHypnos06" }, Count = 2 },
				PlayOnce = true,
				UseText = "UseListenNPC",
				BlockDistanceTriggers = true,
				UseableOffSource = true,
				GiftableOffSource = true,
				TeleportToId = 370024,
				TeleportOffsetX = 110,
				TeleportOffsetY = 100,
				AngleTowardTargetId = 370024,
				InteractDistance = 400,
				EndVoiceLines =
				{
					PreLineWait = 0.4,
					ObjectType = "NPC_Hypnos_01",
					-- Huh, that is right!
					{ Cue = "/VO/Hypnos_0202" },
				},
				{ Cue = "/VO/Thanatos_0540", PreLineWait = 0.3,
					Text = "...Though, what I mean to say is, that... I'm sorry, Hypnos. You're my brother, but I haven't always treated you that way. Would you be willing to forgive me?" },
				{ Cue = "/VO/Hypnos_0201", Portrait = "Portrait_ChildGhost_Default_01", Speaker = "NPC_Hypnos_01",
					Emote = "PortraitEmoteAffection",
					PreLineAnim = "HypnosIdleGreeting", PreLineAnimTarget = 370024,
					Text = "Aww, come on, Thanatos, are you kidding me, right here? We're like two skeletons in a crypt! You're the best, and you're always looking out for me. Even when you're not around for what can seem like aeons at a time!" },
				{ Cue = "/VO/Thanatos_0541",
					PreLineAnim = "ThanatosIdleInhouseFidget_HairFlick", PostLineThreadedFunctionName = "ThanatosExit", PostLineFunctionArgs = { AnimationState = "NPCThanatosExited", WaitTime = 2.4, SkipExitReaction = true },
					Text = "Well let's not get ahead of ourselves, here. We each have got important jobs to do. Isn't that right, Brother?" },
			},
			-- brothers quest
			ThanatosWithHypnos08 =
			{
				Priority = true,
				Partner = "NPC_Hypnos_01",
				RequiredTextLines = { "ThanatosWithHypnos07", "ThanatosAboutHypnos03" },
				MinRunsSinceAnyTextLines = { TextLines = { "ThanatosWithHypnos07" }, Count = 2 },
			 	RequiredValues = { CurrentEmployeeOfTheMonth = "Hypnos" },
				PlayOnce = true,
				UseText = "UseListenNPC",
				BlockDistanceTriggers = true,
				UseableOffSource = true,
				GiftableOffSource = true,
				TeleportToId = 370024,
				TeleportOffsetX = 110,
				TeleportOffsetY = 100,
				AngleTowardTargetId = 370024,
				InteractDistance = 400,
				EndVoiceLines =
				{
					{
						PreLineWait = 0.4,
						UsePlayerSource = true,
						ObjectType = "NPC_Hypnos_01",
						-- Will do!
						{ Cue = "/VO/Hypnos_0204" },
					},
					{
						PreLineWait = 0.15,
						UsePlayerSource = true,
						-- Hey, he did it.
						{ Cue = "/VO/ZagreusHome_2776" },
					},
				},
				{ Cue = "/VO/Thanatos_0636", PreLineWait = 0.35, Portrait = "Portrait_Thanatos_Pleased_01",
					Text = "...Congratulations, Brother! I saw you finally made the board. You made us proud." },
				{ Cue = "/VO/Hypnos_0203", Portrait = "Portrait_ChildGhost_Default_01", Speaker = "NPC_Hypnos_01",
					Emote = "PortraitEmoteCheerful",
					Text = "Sure did! That's something, huh? Though, sorry Thanatos, can't really chat right now, the shades are stacking up, there's greetings to be done, and sign-ins... lots of work to do, you know?" },
				{ Cue = "/VO/Thanatos_0637",
					PreLineAnim = "ThanatosIdleInhouseFidget_HairFlick", Portrait = "Portrait_Thanatos_Pleased_01", PostLineThreadedFunctionName = "ThanatosExit", PostLineFunctionArgs = { AnimationState = "NPCThanatosExited", WaitTime = 2.1, SkipExitReaction = true },
					Text = "I definitely know the feeling. Well then, let's keep it up, the both of us, all right?" },
			},

			ThanatosWithNyx01 =
			{
				PlayOnce = true,
				Partner = "NPC_Nyx_01",
				RequiredAnyTextLines = { "ThanatosGift01", "NyxGift02" },
				RequiredFalseTextLinesThisRun = { "PersephoneFirstMeeting", "Ending01", "OlympianReunionQuestComplete" },
				MinRunsSinceAnyTextLines = { TextLines = { "NyxAboutDusa04" }, Count = 4 },
				RequiredFalseTextLines = { "OlympianReunionQuestComplete" },
				UseText = "UseListenNPC",
				BlockDistanceTriggers = true,
				UseableOffSource = true,
				TeleportToId = 370010,
				TeleportOffsetX = -120,
				TeleportOffsetY = 30,
				AngleTowardTargetId = 370010,
				InteractDistance = 375,
				EndVoiceLines =
				{
					PreLineWait = 0.35,
					ObjectType = "NPC_Nyx_01",
					-- Farewell.
					{ Cue = "/VO/Nyx_0137" },
				},
				{ Cue = "/VO/Thanatos_0542",
					Text = "Mother, is there something I can do to help, with all of this? I spend too much time away from the House. Please, let me know." },
				{ Cue = "/VO/Nyx_0136", Portrait = "Portrait_Nyx_Default_01", Speaker = "NPC_Nyx_01", PreLineWait = 0.35,
					Text = "You are already doing what you can to help, my son. Continue doing so. Matters are at stake here concerning you and all of us. Your support and choices in this shape the outcome that shall come to pass." },
				{ Cue = "/VO/Thanatos_0543",
					PostLineThreadedFunctionName = "ThanatosExit", PostLineFunctionArgs = { AnimationState = "NPCThanatosExited", WaitTime = 2.1, SkipExitReaction = true },
					Text = "I understand, and I will do my best. Thank you for taking care of everything. I've every faith in you, Mother. And in this House. Farewell." },
			},
			ThanatosWithNyx02 =
			{
				PlayOnce = true,
				Partner = "NPC_Nyx_01",
				RequiredTextLines = { "OlympianReunionQuestComplete", "ThanatosGift10", "NyxGift09" },
				MinRunsSinceAnyTextLines = { TextLines = { "OlympianReunionQuestComplete", "NyxAboutDusa04" }, Count = 5 },
				UseText = "UseListenNPC",
				BlockDistanceTriggers = true,
				UseableOffSource = true,
				TeleportToId = 370010,
				TeleportOffsetX = -120,
				TeleportOffsetY = 30,
				AngleTowardTargetId = 370010,
				InteractDistance = 375,
				EndVoiceLines =
				{
					{
						PreLineWait = 0.45,
						ObjectType = "NPC_Nyx_01",
						-- Thank you, my son.
						{ Cue = "/VO/Nyx_0298" },
					},
					{
						PreLineWait = 0.15,
						UsePlayerSource = true,
						-- Heh.
						{ Cue = "/VO/ZagreusHome_3190" },
					},
				},
				{ Cue = "/VO/Thanatos_0554",
					Text = "Am I imagining it, Mother, or has this House been running relatively smoothly as of late? Although I know I must be tempting the Three Fates by putting it like this." },
				{ Cue = "/VO/Nyx_0297", Portrait = "Portrait_Nyx_Default_01", Speaker = "NPC_Nyx_01",
					Text = "It is through a concerted effort that the House thus thrives, for now. Though, it shall do so only through continued diligence on your behalf, and that of many others here. It is a good occasion to take brief reprieve. But we need you working to the best of your ability, here, Thanatos." },
				{ Cue = "/VO/Thanatos_0555",
					PostLineThreadedFunctionName = "ThanatosExit", PostLineFunctionArgs = { AnimationState = "NPCThanatosExited", WaitTime = 2.1, SkipExitReaction = true },
					Text = "Well... know that I am ever-grateful, Mother. And I love you very much. Where would we be right now if not for you? I don't take what you've done for us for granted, nor does anybody here. You rest assured." },
			},

			ThanatosWithMegaera01 =
			{
				PlayOnce = true,
				Partner = "NPC_FurySister_01",
				RequiredAnyTextLines = { "MegaeraMeeting01", "MegaeraMeeting01_B", "MegaeraMeeting01_Alt", "MegaeraMeeting01_Alt_B", "ThanatosGift01" },
				RequiredMinNPCInteractions = { NPC_Thanatos_01 = 1 },
