		GiftTextLineSets =
		{
			ThanatosGift01 =
			{
				PlayOnce = true,
				{ Cue = "/VO/ZagreusHome_0116", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "It isn't much, Than, but I got you something." },
				{ Cue = "/VO/Thanatos_0177",
					Text = "You want me to take that off your hands, fine. But you are taking this from me, and if anybody asks, we're even. Or, scratch that, don't tell anyone about this, understand?" },
			},
			ThanatosGift02 =
			{
				PlayOnce = true,
				RequiredTextLines = { "ThanatosGift01" },
				{ Cue = "/VO/Thanatos_0166",
					Text = "What, really...? Come on, what is this, Zagreus. You think handing this to me makes up for everything you've put us through?" },
				{ Cue = "/VO/ZagreusHome_0224", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "It's just something I found out there that I thought you might like, Than. Don't think anything of it." },
			},
			ThanatosGift03 =
			{
				PlayOnce = true,
				RequiredTextLines = { "ThanatosGift02" },
				{ Cue = "/VO/Thanatos_0167",
					Text = "Why bother with such empty gestures, Zag. You've said you're getting out of here; best focus on that and leave me be." },
				{ Cue = "/VO/ZagreusHome_0779", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "It's not an empty gesture, Than. Besides, if I wasn't trying to stay focused, I'd be drinking it myself." },
			},
			ThanatosGift04 =
			{
				PlayOnce = true,
				RequiredTextLines = { "ThanatosGift03" },
				RequiredFalseTextLines = { "Ending01", "ThanatosGift04_B" },
				{ Cue = "/VO/Thanatos_0168",
					Text = "You shouldn't be doing this, Zag. You picked sides, and things are not the same. A momentary stab at thoughtfulness can't fix something like that." },
				{ Cue = "/VO/ZagreusHome_0225", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "We've been through a lot, Than, and I think we'll be going through a lot more yet. Consider it a token of our kinship." },
			},
			ThanatosGift04_B =
			{
				PlayOnce = true,
				RequiredTextLines = { "ThanatosGift03", "Ending01" },
				RequiredFalseTextLines = { "ThanatosGift04" },
				{ Cue = "/VO/Thanatos_0664",
					Text = "Don't know what you're expecting, giving this to me. If there's a catch here, I don't want to know." },
				{ Cue = "/VO/ZagreusHome_0225", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "We've been through a lot, Than, and I think we'll be going through a lot more yet. Consider it a token of our kinship." },
			},

			ThanatosGift05 =
			{
				PlayOnce = true,
				RequiredAnyTextLines = { "ThanatosGift04", "ThanatosGift04_B" },
				{ Cue = "/VO/Thanatos_0169",
					Text = "What is it with the generosity lately...? Look I'll just stash this with the rest if you really don't have a better thing to do with it than dump it off on me." },
				{ Cue = "/VO/ZagreusHome_0780", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Text = "Hey, that's an idea. One of these days or nights, we'll have enough of this stuff to throw a feast to make all of Olympus jealous." },
			},
			ThanatosGift06 =
			{
				PlayOnce = true,
				RequiredTextLines = { "ThanatosGift05" },
				{ Cue = "/VO/Thanatos_0170",
					Text = "You can't be serious. You're serious...? {#DialogueItalicFormat}Eh{#PreviousFormat}, you know what, fine. I'll take this and we'll go about our business. Though I don't see why you bother." },
				{ Cue = "/VO/ZagreusHome_0781", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Text = "Why I bother? Why, how else can I show you how I feel except by handing you these bottles from time to time?" },
			},

			-- high relationship / locked gifts
			ThanatosGift07_A =
			{
				PlayOnce = true,
				RequiredTextLines = { "ThanatosGift06" },
				{ Cue = "/VO/ZagreusHome_1465", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "Hey Than. For all the times you've bailed me out, and just, all the times you've been around for me... I thought I'd get you this. The real thing." },
				{ Cue = "/VO/Thanatos_0556",
					Emote = "PortraitEmoteSurprise",
					AngleTowardHero = true,
					Text = "Ambrosia...! Zagreus, where did you even... don't you think this is a bit excessive?" },
				{ Cue = "/VO/ZagreusHome_2479", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Text = "You say that like it's a bad thing. Little bit of excess from time to time never hurt anyone I'm sure." },
				{ Cue = "/VO/Thanatos_0557",
					Text = "{#DialogueItalicFormat}Oh{#PreviousFormat}, you would be surprised. Though if you're going to embarrass me, you know what, I am going to reciprocate. {#DialogueItalicFormat}Behold{#PreviousFormat}!" },
			},

			ThanatosGift08 =
			{
				PlayOnce = true,
				UseInitialInteractSetup = true,
				RequiredTextLines = { "ThanatosGift07_A" },
				EndVoiceLines =
				{
					PreLineWait = 0.35,
					UsePlayerSource = true,
					-- Yeah, but I wanted to!
					{ Cue = "/VO/ZagreusHome_2482" },
				},
				{ Cue = "/VO/ZagreusHome_2480", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "For you, Thanatos. The Olympians might be accustomed to this stuff but I think we should have our share of it down here. At least you should." },
				{ Cue = "/VO/Thanatos_0558",
					Text = "You know... the Olympians do have good taste in certain things. The last bottle you gave me... it was... better than expected. Though, I almost decided not to try it at all." },
				{ Cue = "/VO/ZagreusHome_2481", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Text = "But try it you did! And now, you'll get to try this one for comparison's sake. Each one's supposed to be different you know." },
				{ Cue = "/VO/Thanatos_0559", PreLineWait = 0.3, Portrait = "Portrait_Thanatos_Pleased_01",
					Text = "Different, huh. Well, then, I guess I'll have to see what you mean by that. Thank you, Zag. You didn't have to do this." },
			},

			ThanatosGift09 =
			{
				PlayOnce = true,
				UseInitialInteractSetup = true,
				RequiredTextLines = { "ThanatosGift08" },
				EndVoiceLines =
				{
					PreLineWait = 0.4,
