				{ Cue = "/VO/ZagreusHome_1467", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "I hope you're ready for some more of the real thing, there, Than, because I got you some, and I won't be taking {#DialogueItalicFormat}'Tsch, what is this, Zag' {#PreviousFormat}for an answer." },
				{ Cue = "/VO/Thanatos_0409",
					AngleTowardHero = true,
					Emote = "PortraitEmoteDepressed",
					Text = "Ugh, what is this? Come on, already, Zagreus, are you just messing with me, now? How did you even manage to get more of this, much less decide to hand it off to me?" },
				{ Cue = "/VO/ZagreusHome_1468", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Text = "In order: no, I'm not; by ransacking my father's realm repeatedly; and it's because I like you, Thanatos. In case you still have some misgivings about that." },
				{ Cue = "/VO/Thanatos_0411",
					Text = "You {#DialogueItalicFormat}like {#PreviousFormat}me, Zagreus...? I never thought, I... don't know why that sounds so strange, coming from you. Given everything that's happened as of late." },
				{ Cue = "/VO/ZagreusHome_1469", Portrait = "Portrait_Zag_Serious_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "Yeah. Look, if you don't feel the same way about me at this point, I would rather know. Cease all these coy gift exchanges, and all that." },
				{ Cue = "/VO/Thanatos_0412", PostLineThreadedFunctionName = "ThanatosExit",
					PostLineFunctionArgs = { AnimationState = "NPCThanatosExited", WaitTime = 0.3, SkipExitReaction = true },
					Emote = "PortraitEmoteFiredUp",
					Text = "I never said anything like that! You know what, there's... a bunch of mortals I have to go fetch. Good-bye." },
