		InteractTextLineSets =
		{
			ThanatosFieldFirstMeeting =
			{
				PlayOnce = true,
				InitialGiftableOffSource = true,
				EndVoiceLines =
				{
					PreLineWait = 0.9,
					UsePlayerSource = true,
					-- <Sigh>
					{ Cue = "/VO/ZagreusField_3444" },
				},
				{ Cue = "/VO/Thanatos_0195",
					AngleTowardHero = true,
					Text = "You left, without so much as telling me good-bye. I suppose you knew I'd catch up with you sooner or later, is that it? No escaping death, and all?" },
				{  Cue = "/VO/ZagreusField_0514", Portrait = "Portrait_Zag_Serious_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "I left when it was necessary, Than. I thought of you and hoped you'd understand. I have to do this." },
				{ Cue = "/VO/Thanatos_0196", PostLineThreadedFunctionName = "ThanatosExit",
					PostLineFunctionArgs = { WaitTime = 0.4, SkipExitReaction = true },
					Text = "That's more motivation than I've ever heard from you. Well, if you won't say it, I'll say it. Good-bye, Zagreus." },
			},
			ThanatosFieldMiscMeeting01 =
			{
				PlayOnce = true,
				RequiredTextLines = { "ThanatosFieldFirstMeeting", },
				{ Cue = "/VO/Thanatos_0197",
					Text = "I've got to hand it to you, Zag. You're easy to track down. The shades of Tartarus are beginning to fear you even more than your Lord Father." },
				{ Cue = "/VO/ZagreusField_0515", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Text = "Oh they won't fear me once they get to know me." },
				{ Cue = "/VO/Thanatos_0198", PostLineThreadedFunctionName = "ThanatosExit",
					PostLineFunctionArgs = { WaitTime = 0.35, SkipExitReaction = true },
					Text = "I'm not so sure about that. Even I'm beginning to fear you, I think. Seems I don't know you as well as I thought." },
			},
			ThanatosFieldMiscMeeting02 =
			{
				PlayOnce = true,
				InitialGiftableOffSource = true,
				RequiredTextLines = { "ThanatosFieldFirstMeeting", },
				EndVoiceLines =
				{
					PreLineWait = 0.4,
					UsePlayerSource = true,
					-- I don't know what I'm supposed to say when he gets like this...
					{ Cue = "/VO/ZagreusField_0516" },
				},
				{ Cue = "/VO/Thanatos_0199", PostLineThreadedFunctionName = "ThanatosExit",
					PostLineFunctionArgs = { WaitTime = 0.35, SkipExitReaction = true },
					Text = "What's with the look? You had your hands full, now you don't thanks to me... {#DialogueItalicFormat}Tsch{#PreviousFormat}, seems I'm left to thanking myself, since you're too proud to do it." },
			},
			ThanatosFieldMiscMeeting03 =
			{
				PlayOnce = true,
				RequiredTextLines = { "ThanatosFieldFirstMeeting", },
				RequiredFalseBiome = "Elysium",
				{ Cue = "/VO/Thanatos_0200",
					Text = "You seemed a little winded, there, no? Not even close to the surface, yet, either." },
				{  Cue = "/VO/ZagreusField_0517", Portrait = "Portrait_Zag_Defiant_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Text = "Thanks for the vote of confidence there, Than." },
				{ Cue = "/VO/Thanatos_0201", PostLineThreadedFunctionName = "ThanatosExit",
					PostLineFunctionArgs = { WaitTime = 0.35 },
					Text = "You're very welcome. Now, I'd better get back, before Lord Hades catches on to this." },
			},
			ThanatosFieldMiscMeeting05 =
			{
				PlayOnce = true,
				RequiredTextLines = { "ThanatosFieldFirstMeeting" },
				{  Cue = "/VO/ZagreusField_0520", Portrait = "Portrait_Zag_Serious_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenial_Full", PreLineAnimTarget = "Hero",
					Text = "I told you I don't need your help here, Thanatos." },
				{ Cue = "/VO/Thanatos_0204", PostLineThreadedFunctionName = "ThanatosExit",
					PostLineFunctionArgs = { WaitTime = 0.35 },
					Text = "It wasn't being offered. Just happened to be in the area is all. Carry on, Zagreus." },
			},
			ThanatosFieldBackstory01 =
			{
				SuperPriority = true,
				PlayOnce = true,
				RequiredTextLines = { "ThanatosGift01" },
				RequiredAnyTextLines = { "Flashback_Mother_01", "CerberusStyxMeeting01" },
				RequiredFalseTextLines = { "Ending01", "ThanatosGift06", "ThanatosAboutPersephoneMeeting02", "ThanatosFieldAboutPersephoneFirstMeeting01" },
				{ Cue = "/VO/Thanatos_0205",
					Text = "Still running from yourself, I see. How's that been working out for you thus far?" },
				{ Cue = "/VO/ZagreusField_0519", Portrait = "Portrait_Zag_Serious_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Text = "I'm searching for my mother. You still live at home with yours. You don't know what it's like." },
				{ Cue = "/VO/Thanatos_0206", PostLineThreadedFunctionName = "ThanatosExit",
					PostLineFunctionArgs = { WaitTime = 0.35 },
					PreLineAnim = "ThanatosTalkDismissal_Start",
					PostLineAnim = "ThanatosTalkDismissal_Return",
					Text = "Mother Nyx was like a mother to you, too. And this is how you repay her. You should be ashamed of yourself, and learn your place." },
			},
			ThanatosFieldBackstory02 =
			{
				PlayOnce = true,
				RequiredTextLines = { "ThanatosFieldBackstory01", "ThanatosGift02" },
				RequiredFalseTextLines = { "Ending01", "ThanatosAboutPersephone01", "ThanatosFieldAboutPersephoneFirstMeeting01" },
				{ Cue = "/VO/Thanatos_0202",
					Text = "Listen to me. I don't know how else to put this, but, I want you to come home. Voluntarily. It's not just you swept up in all this nonsense now. And, I know you didn't ask for me to get involved, but, what did you expect?" },
				{ Cue = "/VO/ZagreusField_0518", PreLineWait = 0.35, Portrait = "Portrait_Zag_Empathetic_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "...Than. Home is never going to be the same for me. There's no use trying to pretend. I have to go." },
				{ Cue = "/VO/Thanatos_0203", PostLineThreadedFunctionName = "ThanatosExit",
					PostLineFunctionArgs = { WaitTime = 0.35 },
					PreLineAnim = "ThanatosTalkDismissal_Start",
					PostLineAnim = "ThanatosTalkDismissal_Return",
					Text = "You don't {#DialogueItalicFormat}have {#PreviousFormat}to do anything of the sort. You have no idea how good you've had it here. Maybe someday you'll come to understand." },
			},

			ThanatosFieldBuildingTrust01 =
			{
				Priority = true,
				PlayOnce = true,
				InitialGiftableOffSource = true,
				ObjectivesCompleted = { Name = "PlayerKills", Min = 8, },
				ObjectiveCompletedLastOffer = "PlayerKills",
				RequiredTextLines = { "ThanatosFieldAboutRelationship01" },
				EndVoiceLines =
				{
					PreLineWait = 0.65,
					UsePlayerSource = true,
					-- It is all right. Thanks, Than.
					{ Cue = "/VO/ZagreusField_2864" },
				},
				{ Cue = "/VO/ThanatosField_0002",
					AngleTowardHero = true,
					Text = "Outdid me once again, there, huh. {#DialogueItalicFormat}Tsch{#PreviousFormat}. I have to admit, you're not as bad at all of this as I expected. First I found you, I was certain that you had no chance at all." },
				{  Cue = "/VO/ZagreusField_2862", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "I seem to have this whole easy-to-underestimate thing about me, based on a decent sum of my relationships. In any case, I always welcome your appearances out here. I know it's not been easy for you, Than." },
				{ Cue = "/VO/ThanatosField_0003",
					Text = "That's fair as an assessment, I would say. But then again, it's never been an easy time for me. I know I have to do my job. But I don't have to keep on helping you, like this." },
				{  Cue = "/VO/ZagreusField_2863", Portrait = "Portrait_Zag_Defiant_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					PreLineWait = 0.35,
					Text = "...What, you're telling me this is it? You're just going to leave me to my own devices, then, I've proved myself to you, something like that?" },
				{ Cue = "/VO/ThanatosField_0004", PostLineThreadedFunctionName = "ThanatosExit",
					PostLineFunctionArgs = { WaitTime = 2.4, SkipExitReaction = true },
					PreLineAnim = "ThanatosTalkDismissal",
					Emote = "PortraitEmoteFiredUp",
					Text = "No, you deadbeat, I am telling you the opposite! Why do you think I keep on showing up? You may not really need me, Zagreus, but I will take these opportunities to help. I take it that's all right." },
			},

			ThanatosFieldAboutLegendary01 =
			{
				PlayOnce = true,
				RequiredTrait = "ThanatosAssistTrait",
				EndVoiceLines =
				{
					PreLineWait = 0.5,
					UsePlayerSource = true,
					-- Accurate.
					{ Cue = "/VO/ZagreusField_2866" },
				},
				{ Cue = "/VO/ThanatosField_0005", Portrait = "Portrait_Thanatos_Pleased_01",
					Text = "Good to see you taking little Mort there on one of your strolls. Means I can better keep an eye on you out there." },
				{ Cue = "/VO/ZagreusField_2865", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Text = "He's been great to have around, for sure. Still can't believe you found him after all this time! You used to always carry him around, I was so sad for you when he got lost." },
				{ Cue = "/VO/ThanatosField_0006", Portrait = "Portrait_Thanatos_Pleased_01", PostLineThreadedFunctionName = "ThanatosExit", PostLineFunctionArgs = { AnimationState = "NPCThanatosExited", WaitTime = 2, SkipExitReaction = true },
					Text = "And I cannot believe you still remember that. Regardless, he will not be getting lost again under your supervision, accurate?" },
			},

			ThanatosFieldAboutRelationship01 =
			{
				SuperPriority = true,
				PlayOnce = true,
				RequiredTextLines = { "ThanatosAboutRelationship01" },
				InitialGiftableOffSource = true,
				EndVoiceLines =
				{
					PreLineWait = 0.4,
					UsePlayerSource = true,
					-- You too.
					{ Cue = "/VO/ZagreusField_3517" },
				},
				{  Cue = "/VO/ZagreusField_3515", Portrait = "Portrait_Zag_Empathetic_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "About when we last spoke. I care about you, Thanatos. A lot. Though, you asked what you are to me... how am I supposed to even answer that?" },
				{ Cue = "/VO/ThanatosField_0223",
					Text = "{#DialogueItalicFormat}Ugh{#PreviousFormat}, I don't know. Sometimes I miss not feeling anything. Can't always trust what feelings say. I get to thinking, are we only friends, or... look, this is embarrassing." },
				{  Cue = "/VO/ZagreusField_3516", Portrait = "Portrait_Zag_Empathetic_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusInteractionThoughtful", PreLineAnimTarget = "Hero",
					Text = "To think discussing feelings would be even harder than annihilating Father's forces. But... I've been asking myself the same thing, Than. If it's any reassurance." },
				{ Cue = "/VO/ThanatosField_0224", PostLineThreadedFunctionName = "ThanatosExit", PostLineFunctionArgs = { AnimationState = "NPCThanatosExited", WaitTime = 1.8, SkipExitReaction = true },
					Text = "Well, if you come up with any answers, I guess let me know. Good running into you." },
			},

			ThanatosFieldAboutSingersReunionQuestComplete01 =
			{
				SuperPriority = true,
				PlayOnce = true,
				RequiredTextLines = { "ThanatosAboutSingersReunionQuestComplete01" },
				EndVoiceLines =
				{
					PreLineWait = 0.4,
					UsePlayerSource = true,
					-- I'm glad you did, though, Than.
					{ Cue = "/VO/ZagreusField_3512" },
				},
				{ Cue = "/VO/ThanatosField_0219",
					Text = "Hey. I didn't mean to lay the guilt on you for what you did for Orpheus, Zag. I just worry when we get involved... we can make matters worse." },
				{  Cue = "/VO/ZagreusField_3511", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Text = "You haven't ever made anything worse, Than. But as for me, I realized what you meant. I was so eager to do something for Orpheus when I first met Eurydice, I... never asked them if it was OK." },
				{ Cue = "/VO/ThanatosField_0220", PostLineThreadedFunctionName = "ThanatosExit", PostLineFunctionArgs = { AnimationState = "NPCThanatosExited", WaitTime = 2.2, SkipExitReaction = true },
					Text = "At least things turned out well for them. But that was fortunate. Family troubles, personal relations, and the like... it's sensitive business. Though, who am I to talk. You didn't ask me to show up, like this." },
			},

			ThanatosFieldBecameCloseBackOffAftermath01 =
			{
				PlayOnce = true,
				RequiredTextLines = { "BecameCloseWithThanatos01Than_BackOff" },
				EndVoiceLines =
				{
					PreLineWait = 0.4,
					UsePlayerSource = true,
					TriggerCooldowns = { "ThanatosEncounterStartVoiceLines" },
					-- All right.
					{ Cue = "/VO/ZagreusField_3514" },
				},
				{  Cue = "/VO/ZagreusField_3513", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "Hey, Than, are you OK? I mean, with everything, lately?" },
				{ Cue = "/VO/ThanatosField_0221", Portrait = "Portrait_Thanatos_Pleased_01", PostLineThreadedFunctionName = "ThanatosExit", PostLineFunctionArgs = { AnimationState = "NPCThanatosExited", WaitTime = 1.8, SkipExitReaction = true },
					Text = "I am. Though, thanks for asking, Zag. The risk of being close with anyone is sometimes things get complicated, fast. Admittedly it's not a risk I often take, but... in your case, it's been worth it, all in all. We're good, all right?" },
			},

			ThanatosFieldAboutPersephoneFirstMeeting01 =
			{
				SuperPriority = true,
				PlayOnce = true,
				RequiredTextLines = { "ThanatosGift03", "ThanatosFieldBackstory02", "PersephoneMeeting03" },
				RequiredFalseTextLines = { "Ending01", "ThanatosAboutPersephoneMeeting02" },
				RequiresLastRunCleared = true,
				EndVoiceLines =
				{
					PreLineWait = 0.4,
					UsePlayerSource = true,
					TriggerCooldowns = { "ThanatosEncounterStartVoiceLines" },
					-- I know what you mean.
					{ Cue = "/VO/ZagreusField_4660" },
				},
				{ Cue = "/VO/Thanatos_0548",
					Emote = "PortraitEmoteSurprise",
					Text = "What are you doing back here, Zag? You made it out." },
				{  Cue = "/VO/ZagreusField_4658", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Text = "I did... but I had to come back. And, not exactly voluntarily. I'll be visiting up there from time to time, though... it looks like I'll be staying here." },
				{ Cue = "/VO/Thanatos_0549",
					Text = "Visiting from time to time...? I don't understand." },
				{  Cue = "/VO/ZagreusField_4659", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "It's my mother. She's alive and well! It's just that I can't stay with her up there for very long! So we'll have to catch up... one visit at a time." },
				{ Cue = "/VO/Thanatos_0550", PostLineThreadedFunctionName = "ThanatosExit", PostLineFunctionArgs = { AnimationState = "NPCThanatosExited", WaitTime = 1.8, SkipExitReaction = true },
					Text = "I see. Well then... that's good. That's good, because... it means that I won't have to track you down up top. Because, between us, I can only take so much of it up there." },
			},

