			DusaDate01 =
			{
				-- cutaway scene
				Priority = true,
				PlayOnce = true,
				UseableOffSource = true,
				GiftableOffSource = true,
				-- [location: west hall north corner]
				TeleportToId = 421081,
				TeleportOffsetX = 30,
				TeleportOffsetY = 80,
				AngleTowardTargetId = 421081,
				RequiredTextLines = { "DusaMiscMeeting05", "DusaGift03" },
				EndVoiceLines =
				{
					PreLineWait = 0.5,
					UsePlayerSource = true,
					-- Bye for now.
					{ Cue = "/VO/ZagreusHome_0276" },
				},
				{ Cue = "/VO/ZagreusHome_0201", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					Text = "{#DialogueItalicFormat}Erm{#PreviousFormat}, Ms. Dusa, I wanted to, {#DialogueItalicFormat}uh{#PreviousFormat}, cordially invite you to an outing in the lounge? Remember when you asked me about a drink?" },
				{ Cue = "/VO/Dusa_0043", PreLineAnim = "NPCMedusaEmbarrassed", PortraitExitWait = 0.35, StartSound = "/SFX/DusaRattle",
					Emote = "PortraitEmoteSurprise",
					PostLineThreadedFunctionName = "LoungeRevelryPresentation",
					PostLineFunctionArgs = { Sound2 = "/EmptyCue", Sound3 = "/EmptyCue" },
					Text = "{#DialogueItalicFormat}Aaah! {#PreviousFormat}Oh, oh, oh, really, what you mean, right now, you're... I... I guess I'm overdue to take a break..." },
				{ Cue = "/VO/Dusa_0044", FadeOutTime = 0.5, FullFadeTime = 7.8, FadeInTime = 2.0, FadeInSound = "/Leftovers/World Sounds/MapZoomInShortHigh", TeleportToId = 391545, AngleTowardTargetId = 391469, TeleportHeroToId = 391469, AngleHeroTowardTargetId = 391545, PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero", AttachedDim = "Lounge",
					PreLineWait = 0.35,
					Emote = "PortraitEmoteAffection",
					Text = "...And, well, since I don't exactly fit the mold of your traditional gorgon, I mean, I have no body for example, I had trouble finding work! That is until Lord Hades took me in, and then, I met you, and, well, I just love this job!!" },
				{ Cue = "/VO/ZagreusHome_3112", Portrait = "Portrait_Zag_Empathetic_01", PortraitExitAnimation = "Portrait_Zag_Empathetic_01_Exit", Speaker = "CharProtag",
					Text = "{#DialogueItalicFormat}Oh{#PreviousFormat}! I can definitely relate at least to some of that." },
				{ Cue = "/VO/Dusa_0045", FadeOutTime = 0.5, FullFadeTime = 0.5, TeleportToId = 421081, AngleTowardTargetId = 421122, TeleportHeroToId = 421122, AngleHeroTowardTargetId = 421081, FadeInTime = 0.5, FadeInSound = "/Leftovers/World Sounds/MapZoomInShortHigh", PreLineAnim = "NPCMedusaEmbarrassed", PreLineWait = 0.35, PostLineFunctionArgs = { Animation = "NPCMedusaHiding", }, PostLineFunctionName = "SetAnimationState",
					Emote = "PortraitEmoteAffection",
					Text = "{#DialogueItalicFormat}<Sigh> {#PreviousFormat}I, I, I, thanks, Your Highness. Prince. Prince Zagreus!" },
				OnQueuedFunctionName = "CheckDistanceTriggerThread",
				OnQueuedFunctionArgs = PresetEventArgs.Cleaning,
				StatusAnimation = false,
			},

			DusaDateAftermath01 =
			{
				Priority = true,
				PlayOnce = true,
				UseableOffSource = true,
				GiftableOffSource = true,
				-- [location: east lounge]
				TeleportToId = 421079,
				TeleportOffsetX = -10,
				TeleportOffsetY = -60,
				AngleTowardTargetId = 421079,
				RequiredTextLines = { "DusaDate01" },
				RequiredFalseTextLines = { "BecameCloseWithDusa01", "Ending01" },
				RequiredFalseFlags = { "DusaNotYetReHired" },
				EndVoiceLines =
				{
					PreLineWait = 0.9,
					ObjectType = "NPC_Dusa_01",
					-- Oh...
					{ Cue = "/VO/Dusa_0509" },
				},
				{ Cue = "/VO/Dusa_0507", StartSound = "/SFX/DusaRattle",
					Emote = "PortraitEmoteAffection",
					Text = "{#DialogueItalicFormat}Um{#PreviousFormat}, P-Prince, the way you look so sad sometimes, when you come back in here, out of the Pool of Styx? Is everything OK? Because if there was something I could do..." },
				{ Cue = "/VO/ZagreusHome_2608", Portrait = "Portrait_Zag_Empathetic_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "Oh, it's nothing for you to be concerned about, Dusa. I'm just having a harder time fighting my blasted way out of this place than I anticipated. If you'll excuse my language." },
				{ Cue = "/VO/Dusa_0508", StartSound = "/SFX/DusaRattle",
					Emote = "PortraitEmoteSurprise", Portrait = "Portrait_Dusa_Empathetic_01", PortraitExitAnimation = "Portrait_Dusa_Empathetic_01_Exit",
					Text = "{#DialogueItalicFormat}Oh{#PreviousFormat}, I... this is silly, but, I'd no idea anybody could beat {#DialogueItalicFormat}you{#PreviousFormat}, I mean, the Prince? Greater than the mighty Achilles, more cunning than his father, faster than Hermes! But... you're just struggling, like me?" },
				{ Cue = "/VO/ZagreusHome_2609", Portrait = "Portrait_Zag_Empathetic_01", Speaker = "CharProtag",
					Text = "I am. And that stuff you heard about me isn't true." },
				OnQueuedFunctionName = "CheckDistanceTriggerThread",
				OnQueuedFunctionArgs = PresetEventArgs.Cleaning,
				StatusAnimation = false,
			},

			DusaLoungeRenovationQuest01 =
			{
				Priority = true,
				PlayOnce = true,
				UseableOffSource = true,
				GiftableOffSource = true,
				-- [location: east lounge]
				TeleportToId = 421079,
				TeleportOffsetX = -10,
				TeleportOffsetY = -60,
				AngleTowardTargetId = 421079,
				RequiredTextLines = { "DusaGift04" },
				RequiredAnyTextLines = { "DusaDateAftermath01", "Ending01" },
				IgnoreStartTextLinesAngleTowardHero = true,
				EndVoiceLines =
				{
					PreLineWait = 0.65,
					UsePlayerSource = true,
					-- Isn't there something I can do to help...?
					{ Cue = "/VO/ZagreusHome_1922" },
				},
				{ Cue = "/VO/Dusa_0118", PreLineWait = 0.35, StartSound = "/SFX/DusaRattle",
					PreLineThreadedFunctionName = "PlayEmoteAnimFromSource", PreLineThreadedFunctionArgs = { Emote = "PortraitEmoteSurprise", DoShake = true, WaitTime = 6.7 },
					Text = "...sorted the cutlery, wiped the counters, replaced the candles, did the dishes, swept the floor, inspected the barrels, let's see what else, {#DialogueItalicFormat}oh{#PreviousFormat}!! The Prince, it's you!" },
				{ Cue = "/VO/ZagreusHome_1920", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Text = "It's me! Dusa, I'm impressed you're able to manage this whole lounge all by yourself. How do you do it, what's your secret?" },
				{ Cue = "/VO/Dusa_0119",
					PreLineThreadedFunctionName = "PlayEmoteAnimFromSource", PreLineThreadedFunctionArgs = { Emote = "PortraitEmoteNervous", DoShake = true, WaitTime = 0.5 },
					-- Emote = "PortraitEmoteNervous",
					AngleTowardHero = true,
					Text = "{#DialogueItalicFormat}Hahaha{#PreviousFormat}, secret? I'm two weeks behind schedule with this place, at least! Fortunately, no one here seems very good at keeping time, so, no one's gotten on my case just yet, but... oh, you aren't going to tell, are you?" },
				{ Cue = "/VO/ZagreusHome_1921", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "Of course not. What's behind schedule, though? This place doesn't seem any worse for wear than usual." },
				{ Cue = "/VO/Dusa_0120", PreLineWait = 0.35,
					PostLineFunctionArgs = { Animation = "NPCMedusaHiding", }, PostLineFunctionName = "SetAnimationState",
					Text = "Well, that's just it! Miss Nyx, she tasked me with a pretty big project a while back, to make this place {#DialogueItalicFormat}worthy of the name{#PreviousFormat}, which means... there's so much left to do, I need to get everything ready, please, excuse me, Prince!" },
			},
			DusaLoungeRenovationQuest02 =
			{
				Priority = true,
				PlayOnce = true,
				UseableOffSource = true,
				GiftableOffSource = true,
				-- [location: lounge northwest]
				TeleportToId = 421078,
				TeleportOffsetX = 30,
				TeleportOffsetY = 80,
				AngleTowardTargetId = 421078,
				RequiredTextLines = { "DusaLoungeRenovationQuest01" },
				RequiredAnyTextLines = { "DusaGift05", "DusaGift05_Alt" },
				RequiredFalseTextLines = { "DusaLoungeRenovationQuest02_Alt" },
				IgnoreStartTextLinesAngleTowardHero = true,
				RequiredMaxAnyCosmetics =
				{
					Cosmetics = GameData.LoungeCosmetics,
					Count = 12,
				},
				EndVoiceLines =
				{
					PreLineWait = 0.5,
					UsePlayerSource = true,
					-- She's running herself ragged.
					{ Cue = "/VO/ZagreusHome_1925" },
				},
				{ Cue = "/VO/Dusa_0121", PreLineWait = 0.35, StartSound = "/SFX/DusaRattle",
					PreLineThreadedFunctionName = "PlayEmoteAnimFromSource", PreLineThreadedFunctionArgs = { Emote = "PortraitEmoteSurprise", DoShake = true, WaitTime = 7.45 },
					Text = "...patch up the seating, update the menu, clean the stove, stock the honor bar, sweep the rug, dust the rafters, mop the flooring, pay the broker, oh!! Um, sorry, Prince, way behind on my assignment, so if you'll excuse me, please...?" },
				{ Cue = "/VO/ZagreusHome_1923", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Text = "Dusa, wait, before you fly away... there must be something I can do, to help pitch in with everything? Doesn't matter how good you are at your job, what you're describing, that's more than one individual can handle!" },
				{ Cue = "/VO/Dusa_0122",
					AngleTowardHero = true,
					Text = "Your Highness, please! I know you mean well, but I have this! Wish everybody wouldn't doubt me all the time, like that House Contractor, just gets to stand around, waiting for work orders! Unless they come from me, then {#DialogueItalicFormat}nooo{#PreviousFormat}." },
				{ Cue = "/VO/ZagreusHome_1924", Portrait = "Portrait_Zag_Defiant_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusInteractionThoughtful", PreLineAnimTarget = "Hero",
					Text = "The House Contractor isn't cooperating with you, huh. No wonder so many lounge renovations are clogged up in the works. Though maybe I can grease the wheels a bit?" },
				{ Cue = "/VO/Dusa_0123", PreLineWait = 0.35, PostLineFunctionArgs = { Animation = "NPCMedusaHiding", }, PostLineFunctionName = "SetAnimationState",
					Emote = "PortraitEmoteSurprise",
					Text = "{#DialogueItalicFormat}AAH{#PreviousFormat}, I completely forgot!! I need to get more cooking grease for the Head Chef! I have to go!!" },
			},
			DusaLoungeRenovationQuest02_Alt =
			{
				Priority = true,
				PlayOnce = true,
				UseableOffSource = true,
				GiftableOffSource = true,
				-- [location: lounge northwest]
				TeleportToId = 421078,
				TeleportOffsetX = 30,
				TeleportOffsetY = 80,
				AngleTowardTargetId = 421078,
				RequiredTextLines = { "DusaLoungeRenovationQuest01" },
				RequiredAnyTextLines = { "DusaGift05", "DusaGift05_Alt" },
				RequiredFalseTextLines = { "DusaLoungeRenovationQuest02" },
				IgnoreStartTextLinesAngleTowardHero = true,
				RequiredMinAnyCosmetics =
				{
					Cosmetics = GameData.LoungeCosmetics,
					Count = 13,
				},
				EndVoiceLines =
				{
					PreLineWait = 0.45,
					UsePlayerSource = true,
					-- Just trying to help...?
					{ Cue = "/VO/ZagreusHome_1927" },
				},
				{ Cue = "/VO/Dusa_0121", PreLineWait = 0.35, StartSound = "/SFX/DusaRattle",
					Text = "...patch up the seating, update the menu, clean the stove, stock the honor bar, sweep the rug, dust the rafters, mop the flooring, pay the broker, oh!! Um, sorry, Prince, way behind on my assignment, so if you'd excuse me, please...?" },
				{ Cue = "/VO/ZagreusHome_1923", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Text = "Dusa, wait, before you fly away... there must be something I can do, to help pitch in with everything? Doesn't matter how good you are at your job, what you're describing, that's more than one individual can handle!" },
				{ Cue = "/VO/Dusa_0122",
					Text = "Your Highness, please! I know you mean well, but I have this! Wish everybody wouldn't doubt me all the time, like that House Contractor, just gets to stand around, waiting for work orders! Unless they come from me, then {#DialogueItalicFormat}nooo{#PreviousFormat}." },
				{ Cue = "/VO/ZagreusHome_1926", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusInteractionThoughtful", PreLineAnimTarget = "Hero",
					Text = "{#DialogueItalicFormat}Ooh{#PreviousFormat}, that might be my fault. I've been giving the Contractor lots of work. Some of those lounge orders must be coming from you, right? This place is really shaping up!" },
				{ Cue = "/VO/Dusa_0124", PreLineWait = 0.35, PostLineFunctionArgs = { Animation = "NPCMedusaHiding", }, PostLineFunctionName = "SetAnimationState",
					Emote = "PortraitEmoteSurprise",
					Text = "{#DialogueItalicFormat}Wha-wha{#PreviousFormat}, wait, then you're the one who's been authorizing all these improvements?! Oh my, oh my, {#DialogueItalicFormat}aaaah{#PreviousFormat}!" },
			},
			DusaLoungeRenovationQuest03 =
			{
				Priority = true,
				PlayOnce = true,
				UseableOffSource = true,
				GiftableOffSource = true,
				-- [location: lounge northwest]
				TeleportToId = 421078,
				TeleportOffsetX = 30,
				TeleportOffsetY = 80,
				AngleTowardTargetId = 421078,
				RequiredFalseFlags = { "DusaNotYetReHired" },
				RequiredTextLines = { "DusaLoungeRenovationQuest02" },
				RequiredFalseTextLines = { "DusaLoungeRenovationQuestComplete" },
				RequiredMinAnyCosmetics =
				{
					Cosmetics = GameData.LoungeCosmetics,
					Count = 5,
				},
				RequiredMaxAnyCosmetics =
				{
					Cosmetics = GameData.LoungeCosmetics,
					Count = 13,
				},
				EndVoiceLines =
				{
					PreLineWait = 0.4,
					UsePlayerSource = true,
					-- I'm looking forward!
					{ Cue = "/VO/ZagreusHome_1929" },
				},
				{ Cue = "/VO/ZagreusHome_1928", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "The lounge is really starting to look nice lately, Dusa! All your hard work is really paying off!" },
				{ Cue = "/VO/Dusa_0125", PreLineWait = 0.35, PostLineFunctionArgs = { Animation = "NPCMedusaHiding", }, PostLineFunctionName = "SetAnimationState",
					Emote = "PortraitEmoteAffection",
					Text = "{#DialogueItalicFormat}Hah{#PreviousFormat}! Why, thank you, Prince, it's just that, lately, the House Contractor's been slightly more agreeable I guess, working a little faster, maybe, but there's still a lot we have to do, so, you just wait!" },
				OnQueuedFunctionName = "CheckDistanceTriggerThread",
				OnQueuedFunctionArgs = PresetEventArgs.Cleaning,
				StatusAnimation = false,
			},
			DusaLoungeRenovationQuestComplete =
			{
				SuperPriority = true,
				PlayOnce = true,
				UseableOffSource = true,
				GiftableOffSource = true,
				-- [location: lounge entrance]
				TeleportToId = 421079,
				TeleportOffsetX = 250,
				TeleportOffsetY = -460,
				AngleTowardTargetId = 421995,				
				RequiredTextLines = { "DusaGift06" },
				RequiredFalseFlags = { "DusaNotYetReHired" },
				RequiredAnyTextLines = { "DusaLoungeRenovationQuest02", "DusaLoungeRenovationQuest02_Alt", "DusaLoungeRenovationQuest03" },
				RequiredCosmetics = { "Cosmetic_ClearFur", "Cosmetic_ClearScratches", },
				RequiredAnyCosmetics = { "Cosmetic_LoungeRug", "Cosmetic_LoungeRugA", "Cosmetic_LoungeRugB", "Cosmetic_LoungeRugC", "Cosmetic_LoungeBrokerRug" },
				RequiredMinAnyCosmetics =
				{
					Cosmetics = GameData.LoungeCosmetics,
					Count = 13,
				},
				EndVoiceLines =
				{
					PreLineWait = 0.45,
					UsePlayerSource = true,
					-- Take care now!
					{ Cue = "/VO/ZagreusHome_1932" },
				},
				{ Cue = "/VO/Dusa_0126", PreLineWait = 0.35, StartSound = "/SFX/DusaRattle",
					Text = "{#DialogueItalicFormat}Um{#PreviousFormat}, Prince Zagreus...? I just wanted to say something, if you have a little time? I know you're really busy... I can come back." },
				{ Cue = "/VO/ZagreusHome_1930", Portrait = "Portrait_Zag_Empathetic_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "Wait, no, now's fine, of course, what is it, Dusa? I'm not so busy, by the way, and always happy to check in. Wait, is something wrong?" },
				{ Cue = "/VO/Dusa_0127",
					Text = "No, {#DialogueItalicFormat}nononono{#PreviousFormat}, it is just the opposite, I... can't believe you helped me, with the lounge. I don't think {#DialogueItalicFormat}anyone {#PreviousFormat}has {#DialogueItalicFormat}ever {#PreviousFormat}helped me out like you did, cleaning up after Cerberus, and the new rug, and the kitchen, and, just, all of this!" },
				{ Cue = "/VO/ZagreusHome_1931", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Text = "It was my pleasure, really. All of us use the lounge, don't we? This House can really be a lot, but in the lounge here, things are easier a bit, I think. I'm so glad we have you watching over it." },
				{ Cue = "/VO/Dusa_0128", PreLineWait = 0.35, PostLineFunctionArgs = { Animation = "NPCMedusaHiding" }, PostLineFunctionName = "SetAnimationState",
					Emote = "PortraitEmoteAffection",
					Text = "Y, you really mean that, Prince...? But you don't know what this means to me. I'm finally caught up on work! W, what am I going to do? I, I don't know what I'm going to do with all this extra time! I have to go, and plan everything out!!" },
				OnQueuedFunctionName = "CheckDistanceTriggerThread",
				OnQueuedFunctionArgs = PresetEventArgs.Cleaning,
				StatusAnimation = false,
			},

			BecameCloseWithDusa01 =
			{
				Priority = true,
				PlayOnce = true,
				UseableOffSource = true,
				GiftableOffSource = true,
				-- checking to make sure both Meg and Thanatos are in DeathArea to prevent rare issues where they can appear in the bedroom during this event
				AreIdsAlive = { 390082, 423052 },
				-- [location: near bedroom]
				TeleportToId = 421082,
				TeleportOffsetX = -270,
				TeleportOffsetY = 30,
				AngleTowardTargetId = 422938,
				RequiredTextLines = { "DusaGift10" },
				RequiredFalseTextLinesLastRun = { "DusaGift10", "DusaAboutGorgons03", "DusaAboutGorgons03Aftermath01" },
				-- prevent a progstop
				RequiredFalseTextLinesThisRun = { "PersephoneMeeting08" },
				RequiredFalseFlags = { "DusaNotYetReHired" },
				EndVoiceLines =
				{
					PreLineWait = 0.6,
					UsePlayerSource = true,
					-- Bye, Dusa.
					{ Cue = "/VO/ZagreusHome_1961" },
				},
				{ Cue = "/VO/Dusa_0145", PreLineWait = 0.35, StartSound = "/SFX/DusaRattle",
					Emote = "PortraitEmoteNervous",
					Text = "{#DialogueItalicFormat}Um{#PreviousFormat}, Zagreus? I wanted to talk some more, if you're available? I mean, if you're free. I mean! If you have time to right now. Is that OK?" },
				{ Cue = "/VO/ZagreusHome_1956", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "Of course, Dusa! Where should we chat? The lounge, or maybe somewhere more private...?" },
				{ Cue = "/VO/Dusa_0146", Portrait = "Portrait_Dusa_Empathetic_01", PortraitExitAnimation = "Portrait_Dusa_Empathetic_01_Exit",
					Text = "Somewhere more private, please, if that's OK? Thank you." },
				{ Cue = "/VO/Dusa_0147", Portrait = "Portrait_Dusa_Empathetic_01",
					FadeOutTime = 0.5, FullFadeTime = 1.5, LoadMap = "DeathAreaBedroom",
					SpawnOnId = 422138, AngleTowardHero = true,
					TeleportHeroToId = 422254, AngleHeroTowardTargetId = 422138,
					FadeInTime = 1.5, FadeInSound = "/Leftovers/Menu Sounds/EmoteAffection",
					PreLineAnim = "ZagreusInteractionThoughtful", PreLineAnimTarget = "Hero",
					PreLineWait = 0.35,
					Text = "...Um, Zagreus... so, ever since you told me you had feelings for me, it's been, like... my whole mind is on fire. I haven't been able to think straight, or get anything done. It's all been so much!" },
				{ Cue = "/VO/ZagreusHome_1957", Portrait = "Portrait_Zag_Empathetic_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "I'm sorry, Dusa, I... put a lot on you, and... maybe I shouldn't have, since I'm the Prince and everything, as you point out, and... sometimes, I forget myself. I'd never pressure you, it's just... I thought you had these feelings for me, too." },
				{ Cue = "/VO/Dusa_0148",
					Emote = "PortraitEmoteFiredUp",
					Text = "I {#DialogueItalicFormat}do {#PreviousFormat}have feelings for you, silly! That's what I've been trying to tell you, it's just... it's just..." },
				{ Cue = "/VO/ZagreusHome_1958", Portrait = "Portrait_Zag_Empathetic_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Text = "It's just what...? Whatever it is, it's OK. You can be yourself with me, Dusa, I promise." },
				{ Cue = "/VO/Dusa_0149", Portrait = "Portrait_Dusa_Empathetic_01",
					Text = "I'm glad you put it that way. I... I'm not looking for an intimate relationship. You're the kindest soul in this whole House. And I have learned so much from you! But, when I really think about my feelings... I'm just, not attracted to you, in that way. I'm sorry. You're a really nice guy!" },
				{ Cue = "/VO/ZagreusHome_1959", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusInteractionThoughtful", PreLineAnimTarget = "Hero",
					Text = "Oh, {#DialogueItalicFormat}hahaha{#PreviousFormat}, look, there's nothing to apologize for, not at all. In fact... all this is a relief. I've learned a lot from you, too, and I cherish our friendship for what it is. Do you... do you think that we could still be friends? Or, is it better that you didn't see me anymore...?" },
				{ Cue = "/VO/Dusa_0150", Portrait = "Portrait_Dusa_Empathetic_01",
					Emote = "PortraitEmoteSurprise",
					Text = "Don't say {#DialogueItalicFormat}that{#PreviousFormat}! Though, I was going to ask you the same thing. I'd really like it if you'd be my friend! Though, my career comes first, so... I don't want you doing any special favors, promise me? I need to see how far I can get on my own." },
				{ Cue = "/VO/ZagreusHome_1960", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "I respect that so much, and yes, I promise, Dusa. So, well, then... friends? For all eternity?" },
				{ Cue = "/VO/Dusa_0151", Portrait = "Portrait_Dusa_Empathetic_01",
					Emote = "PortraitEmoteSparkly",
					PreLineWait = 0.35, PostLineFunctionArgs = { Animation = "NPCMedusaHiding", Text = "NPC_Dusa_01", Icon = "Keepsake_DusaSticker_Max" }, PostLineFunctionName = "SetAnimationState",
					PostLineThreadedFunctionName = "MaxedRelationshipPresentation",
					Text = "Yes. Friends. For all eternity. Now... I had better go. See you, OK?" },
				OnQueuedFunctionName = "CheckDistanceTriggerThread",
				OnQueuedFunctionArgs = PresetEventArgs.Cleaning,
				StatusAnimation = false,
			},

			BecameCloseWithDusaAftermath01 =
			{
				Priority = true,
				PlayOnce = true,
				UseableOffSource = true,
				GiftableOffSource = true,
				RequiredTextLines = { "BecameCloseWithDusa01" },
				-- [location: lounge bar]
				TeleportToId = 427182,
				TeleportOffsetX = -10,
				TeleportOffsetY = -40,
				AngleTowardTargetId = 427182,
				RequiredFalseFlags = { "DusaNotYetReHired" },
				EndGlobalVoiceLines = "PostBecameCloseWithDusaAftermathVoiceLines",
				{ Cue = "/VO/Dusa_0152", PreLineWait = 0.35, StartSound = "/SFX/DusaRattle",
					AngleTowardHero = true,
					Portrait = "Portrait_Dusa_Empathetic_01", PortraitExitAnimation = "Portrait_Dusa_Empathetic_01_Exit",
					Emote = "PortraitEmoteSparkly",
					Text = "Hey, Zagreus, I... wanted to talk to you again, I... what I mean is, I just, hope I didn't hurt your feelings, because, if I did, you'd let me know, wouldn't you?" },
				{ Cue = "/VO/ZagreusHome_1962", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Text = "Dusa, please! Remember {#DialogueItalicFormat}friends for all eternity{#PreviousFormat}? That's a long time, so, there's no need for second guessing. I'm glad we had a chance to talk." },
				{ Cue = "/VO/Dusa_0153",
					Emote = "PortraitEmoteNervous",
					Text = "Oh, well, good! That's a relief to hear, but... one last thing, so I can clear my conscience of it, I... I've saved all the Ambrosia that you gave me, and I figured since... I didn't realize, you know, the way you felt? I can be a little dense! I have it here. And I would like to give it back. Is that OK?" },
				{ Text = "Dusa_ChoiceText01",
					Choices =
					{
						{
							ChoiceText = "Dusa_Accept",
						{ Cue = "/VO/ZagreusHome_1963", Portrait = "Portrait_Zag_Empathetic_01", Speaker = "CharProtag",
							PreLineAnim = "ZagreusInteractionThoughtful", PreLineAnimTarget = "Hero",
							Text = "I... look, if you don't want the Ambrosia anymore, and receiving it made you feel uncomfortable in any way, I'll take it back. It was my gift to you, to do with as you please. Are you sure you don't want it?" },
						{ Cue = "/VO/Dusa_0154", PreLineWait = 0.35, PostLineFunctionArgs = { Animation = "NPCMedusaHiding", }, PostLineFunctionName = "SetAnimationState",
							Emote = "PortraitEmoteCheerful",
							PostLineThreadedFunctionName = "DusaSuperGiftPointRefundPresentation",
							Text = "Yes, I'm sure, and thank you for your understanding, Prince. It really was quite sweet of you, but it's important that I learn to stock the lounge myself. So, here you go, and I will see you soon, OK?" },
						},
						{
							ChoiceText = "Dusa_Decline",
						{ Cue = "/VO/ZagreusHome_1964", Portrait = "Portrait_Zag_Empathetic_01", Speaker = "CharProtag",
							PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
							PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
							Text = "Hey, now, there's no need to do anything rash like that! That Ambrosia was my gift to you, and it was unconditional. Just try the stuff and see! They say the purer the act of giving, the better it is. Won't you keep it? If not for yourself, then for the lounge?" },
						{ Cue = "/VO/Dusa_0155", PreLineWait = 0.35, PostLineFunctionArgs = { Animation = "NPCMedusaHiding", }, PostLineFunctionName = "SetAnimationState",
							Emote = "PortraitEmoteCheerful",
							Text = "{#DialogueItalicFormat}Ah{#PreviousFormat}, all right, I... had to ask, so, thanks again. Just wanted to make sure! I hope we can enjoy it at the lounge sometime!! I'd better go put it away. Not drink it! Store it for safe keeping, now, OK?" },
						}
					}
				},
				OnQueuedFunctionName = "CheckDistanceTriggerThread",
				OnQueuedFunctionArgs = PresetEventArgs.CleaningConfident,
				StatusAnimation = false,
			},

			BecameCloseWithDusaAftermath02 =
			{
				PlayOnce = true,
				UseableOffSource = true,
				GiftableOffSource = true,
				RequiredTextLines = { "BecameCloseWithDusaAftermath01Dusa_Decline" },
				-- [location: lounge northwest]
				TeleportToId = 421078,
				TeleportOffsetX = 30,
				TeleportOffsetY = 80,
				AngleTowardTargetId = 421078,
				RequiredFalseFlags = { "DusaNotYetReHired" },
				-- I wouldn't mind at all.
				EndCue = "/VO/ZagreusHome_2615",
				EndWait = 0.4,
				{ Cue = "/VO/Dusa_0514", PreLineWait = 0.35, StartSound = "/SFX/DusaRattle",
					AngleTowardHero = true,
					Portrait = "Portrait_Dusa_Empathetic_01", PortraitExitAnimation = "Portrait_Dusa_Empathetic_01_Exit",
					Emote = "PortraitEmoteCheerful",
					Text = "Hey, {#DialogueItalicFormat}um{#PreviousFormat}, Zagreus? I wanted to say, that was very sweet of you, letting me hang onto all that Ambrosia you gave me before? It's in safe keeping now! Except one bottle... that kind of slipped, and broke. Though that did mean I got to try the stuff!" },
				{ Cue = "/VO/ZagreusHome_2614", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "Oh, you got to try it, after all? What did you think, all it's cracked up to be?" },
				{ Cue = "/VO/Dusa_0515",
					Emote = "PortraitEmoteAffection",
					Text = "It was a{#DialogueItalicFormat}maz{#PreviousFormat}ing. I've never had anything like it! It's so good to have a forked tongue, meant I could taste it twice at the same time! So, um... if one of the other bottles slipped and broke... you wouldn't mind, would you?" },
				OnQueuedFunctionName = "CheckDistanceTriggerThread",
				OnQueuedFunctionArgs = PresetEventArgs.CleaningConfidentAlt,
				StatusAnimation = false,
			},

			BecameCloseWithDusaAftermath03 =
			{
				PlayOnce = true,
				UseableOffSource = true,
				GiftableOffSource = true,
				RequiredTextLines = { "BecameCloseWithDusaAftermath01" },
				-- [location: lounge bar]
				TeleportToId = 427182,
				TeleportOffsetX = -10,
				TeleportOffsetY = -40,
				RequiredFalseFlags = { "DusaNotYetReHired" },
				AngleTowardTargetId = 427182,
				EndVoiceLines =
				{
					PreLineWait = 0.5,
					UsePlayerSource = true,
					-- Ah, thank you, Dusa.
					{ Cue = "/VO/ZagreusHome_2613" },
				},
				{ Cue = "/VO/ZagreusHome_2611", Portrait = "Portrait_Zag_Empathetic_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "Dusa, I can't shake this feeling, so I wanted to apologize to you, because... I think when we first met, I just... I don't think I took it the right way. I never had a fan before! I wanted to live up to your idea of me." },
				{ Cue = "/VO/Dusa_0512",
					Emote = "PortraitEmoteCheerful", Portrait = "Portrait_Dusa_Empathetic_01", PortraitExitAnimation = "Portrait_Dusa_Empathetic_01_Exit",
					Text = "Oh, Prince, Zagreus, don't worry about that! Even I didn't know what I was going through. When I first started working here, I was just {#DialogueItalicFormat}so {#PreviousFormat}impressed with you, I mean... you were the only one who would stand up to your father! I thought about you all the time!" },
				{ Cue = "/VO/ZagreusHome_2612", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Text = "But you didn't even know me, yet. We didn't get to talking for what seemed like ages. You can be difficult to find! Since I can't fly up to the rafters like you can." },
				{ Cue = "/VO/Dusa_0513",
					Emote = "PortraitEmoteAffection", Portrait = "Portrait_Dusa_Empathetic_01", PortraitExitAnimation = "Portrait_Dusa_Empathetic_01_Exit",
					Text = "Well I'm so glad you kept trying to talk to me! If you hadn't... I'd probably be up in the rafters still, building up this idea of you that's not even as good as the real thing!" },
				OnQueuedFunctionName = "CheckDistanceTriggerThread",
				OnQueuedFunctionArgs = PresetEventArgs.CleaningConfident,
				StatusAnimation = false,
			},

			DusaAboutLove01 =
			{
				PlayOnce = true,
				UseableOffSource = true,
				GiftableOffSource = true,
				RequiredTextLines = { "BecameCloseWithDusaAftermath03", "AphroditeAboutLove01" },
				-- [location: east lounge]
				TeleportToId = 421080,
				TeleportOffsetX = -10,
				TeleportOffsetY = 40,
				AngleTowardTargetId = 421080,
				RequiredFalseFlags = { "DusaNotYetReHired" },
				-- I love you, too, Dusa!
				EndCue = "/VO/ZagreusHome_1969",
				EndWait = 0.45,
				{ Cue = "/VO/ZagreusHome_1967", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Text = "Hey Dusa, something I was wondering, and it's just curiosity, mind you, but... is there somebody else...? I mean, somebody special, whom you're close with, or would like to be. Romantically, I mean. If it's all right to ask." },
				{ Cue = "/VO/Dusa_0156", PreLineWait = 0.35, StartSound = "/SFX/DusaRattle",
					AngleTowardHero = true,
					Portrait = "Portrait_Dusa_Empathetic_01", PortraitExitAnimation = "Portrait_Dusa_Empathetic_01_Exit",
					Emote = "PortraitEmoteCheerful",
					Text = "Oh, you can ask me anything! But, {#DialogueItalicFormat}um{#PreviousFormat}, somebody else romantically, not really, no? It's not really my thing? It doesn't mean I don't have feelings for you, though! You know that, right? I wouldn't drop what I'm doing and throw myself at danger for just anyone, you know!" },
				{ Cue = "/VO/ZagreusHome_1968", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "I definitely know. There's seven kinds of love, or so, isn't that right? It isn't something that's taught extensively down here, though, so... there's still a lot for me to learn, I guess." },
				{ Cue = "/VO/Dusa_0157", PostLineFunctionArgs = { Animation = "NPCMedusaHiding", }, PostLineFunctionName = "SetAnimationState",
					PreLineThreadedFunctionName = "PlayEmoteAnimFromSource", PreLineThreadedFunctionArgs = { Emote = "PortraitEmoteAffection", DoShake = true, WaitTime = 4.9 },
					Text = "Wow, I never thought of it as {#DialogueItalicFormat}love {#PreviousFormat}until you said it there just now, Prince, but you're right! I love you, Zagreus! {#DialogueItalicFormat}Hah, haha{#PreviousFormat}, that feels good to come right out and say, you know? OK, well, bye!" },
				OnQueuedFunctionName = "CheckDistanceTriggerThread",
				OnQueuedFunctionArgs = PresetEventArgs.CleaningConfidentAlt,
				StatusAnimation = false,
			},

			DusaAboutCerberus01 =
			{
				PlayOnce = true,
				UseableOffSource = true,
				GiftableOffSource = true,
				-- [location: west hall north corner]
				TeleportToId = 421080,
				TeleportOffsetX = 30,
				TeleportOffsetY = 80,
				AngleTowardTargetId = 421080,
				RequiredTextLines = { "CerberusGift01", "DusaDate01" },
				RequiredFalseFlags = { "DusaNotYetReHired" },
				-- Fates be merciful...
				EndCue = "/VO/ZagreusHome_2000",
				EndWait = 0.65,
				{ Cue = "/VO/ZagreusHome_1998", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "I hope it's not been too much, having to care for Cerberus, is it, Dusa? He isn't quite as fearsome as he seems. At least, that goes for two heads out of three!" },
				{ Cue = "/VO/Dusa_0460", Portrait = "Portrait_Dusa_Empathetic_01", PortraitExitAnimation = "Portrait_Dusa_Empathetic_01_Exit",
					StartSound = "/SFX/DusaRattle",
					Text = "Oh, Cerberus is a good boy, he's fine! One of the highlights of the job, for sure. We really hit it off after he finally realized I'm not a ball! So, I've been feeding him, grooming him, changing his water dish..." },
				{ Cue = "/VO/ZagreusHome_1999", PreLineWait = 0.35, Portrait = "Portrait_Zag_Defiant_01", Speaker = "CharProtag",
					Text = "...Cleaning up after him...?" },
				{ Cue = "/VO/Dusa_0461",
					PreLineThreadedFunctionName = "PlayEmoteAnimFromSource", PreLineThreadedFunctionArgs = { Emote = "PortraitEmoteSurprise", DoShake = true, WaitTime = 4.4 },
					PostLineFunctionArgs = { Animation = "NPCMedusaHiding", }, PostLineFunctionName = "SetAnimationState", StartSound = "/SFX/DusaRattle",
					Text = "Most definitely, yes. If you'll excuse me, by the way? He's got to go. I mean! I've got to go! I mean!! You know what I mean, bye!" },
				OnQueuedFunctions =
				{
					{
						Name = "CheckDistanceTriggerThread",
						Args = PresetEventArgs.CleaningConfident,
						GameStateRequirements =
						{
							RequiredTextLines = { "BecameCloseWithDusa01" },
						},
					},
					{
						Name = "CheckDistanceTriggerThread",
						Args = PresetEventArgs.Cleaning,
						GameStateRequirements =
						{
							RequiredFalseTextLines = { "BecameCloseWithDusa01" },
						},
					}
				},
				StatusAnimation = false,
			},

			DusaAboutPressure01 =
			{
				PlayOnce = true,
				UseableOffSource = true,
				GiftableOffSource = true,
				-- [location: west hall north corner]
				TeleportToId = 421080,
				TeleportOffsetX = 30,
				TeleportOffsetY = 80,
				AngleTowardTargetId = 421080,
				RequiredTextLines = { "BecameCloseWithDusa01" },
				RequiredFalseFlags = { "DusaNotYetReHired" },
				RequiresRunNotCleared = true,
				ConsecutiveDeathsInRoom =
				{
				  Name = "D_Boss01",
				  Count = 2,
				},
				-- I... they do, huh.
				EndCue = "/VO/ZagreusHome_3109",
				EndWait = 0.4,
				{ Cue = "/VO/ZagreusHome_3107", Portrait = "Portrait_Zag_Empathetic_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusInteractionThoughtful", PreLineAnimTarget = "Hero",
					Text = "{#DialogueItalicFormat}Ungh{#PreviousFormat}, Dusa... it's a little much out there sometimes, I have to say. Different stresses than in here, but still." },
				{ Cue = "/VO/Dusa_0485", Portrait = "Portrait_Dusa_Empathetic_01", PortraitExitAnimation = "Portrait_Dusa_Empathetic_01_Exit",
					StartSound = "/SFX/DusaRattle",
					Text = "Oh don't you lose hope now, Prince Zagreus! There isn't anything you couldn't handle, I just know! You're every bit as tough as your father, except, you're nice! And you never back down from anything. You can do this!" },
				{ Cue = "/VO/ZagreusHome_3108", Portrait = "Portrait_Zag_Empathetic_01", Speaker = "CharProtag", PreLineWait = 0.35,
					Text = "...I don't know that anyone can do this. No escape from the Underworld and all that. Though, thanks for cheering me up." },
				{ Cue = "/VO/Dusa_0486",
					StartSound = "/SFX/DusaRattle",
					Emote = "PortraitEmoteCheerful",
					Text = "I'm not just saying it, I mean it, Prince! Look at how far you've come! Practically everybody in this House believes in you! Even the Wretched Broker and the Head Chef! Oh, I've asked them! Everybody thinks you're going to succeed!" },
				OnQueuedFunctionName = "CheckDistanceTriggerThread",
				OnQueuedFunctionArgs = PresetEventArgs.CleaningConfident,
				StatusAnimation = false,
			},
			DusaAboutPressure02 =
			{
				PlayOnce = true,
				UseableOffSource = true,
				GiftableOffSource = true,
				-- [location: west hall north corner]
				TeleportToId = 421080,
				TeleportOffsetX = 30,
				TeleportOffsetY = 80,
				AngleTowardTargetId = 421080,
				RequiredTextLines = { "BecameCloseWithDusa01" },
				RequiredFalseTextLines = { "Ending01" },
				RequiredFalseFlags = { "DusaNotYetReHired" },
				RequiresRunNotCleared = true,
				ConsecutiveDeathsInRoom =
				{
				  Name = "D_Boss01",
				  Count = 2,
				},
				-- You've no idea how much I appreciate it.
				EndCue = "/VO/ZagreusHome_3111",
				EndWait = 0.5,
				{ Cue = "/VO/ZagreusHome_3110", Portrait = "Portrait_Zag_Empathetic_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "Can I ask you a couple of purely figurative questions here, Dusa? First, what have I gotten myself into...? And second, what am I going to do...?" },
				{ Cue = "/VO/Dusa_0479", Portrait = "Portrait_Dusa_Empathetic_01", PortraitExitAnimation = "Portrait_Dusa_Empathetic_01_Exit",
					StartSound = "/SFX/DusaRattle",
					Emote = "PortraitEmoteCheerful",
					Text = "Believe me, I know the feeling, being in over my head! Though, if I can find my way out of my messes, then I know, I {#DialogueItalicFormat}know{#PreviousFormat}, you definitely can! I don't know what you ought to do, but I am always here to listen." },
				OnQueuedFunctionName = "CheckDistanceTriggerThread",
				OnQueuedFunctionArgs = PresetEventArgs.CleaningConfidentAlt,
				StatusAnimation = false,
			},

			DusaAboutAchilles01 =
			{
				PlayOnce = true,
				UseableOffSource = true,
				GiftableOffSource = true,
				-- [location: west hall north corner]
				TeleportToId = 421080,
				TeleportOffsetX = 30,
				TeleportOffsetY = 80,
				AngleTowardTargetId = 421080,
				RequiredTextLines = { "DusaDate01", "AchillesGift03", "AchillesGrantsCodex" },
				RequiredFalseTextLines = { "AchillesAboutMyrmidonReunionQuest01" },
				RequiredFalseFlags = { "DusaNotYetReHired" },
				AreIdsAlive = { 370009 },
				-- Maybe he does. Maybe you're right, Dusa..
				EndCue = "/VO/ZagreusHome_2604",
				EndWait = 0.45,
				{ Cue = "/VO/ZagreusHome_2603", Portrait = "Portrait_Zag_Empathetic_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "You know Achilles reasonably well, don't you, Dusa? Seems like he's always stuck there in that hall. At least you get to fly around. Sometimes I think he wishes he was someplace else." },
				{ Cue = "/VO/Dusa_0480", Portrait = "Portrait_Dusa_Empathetic_01", PortraitExitAnimation = "Portrait_Dusa_Empathetic_01_Exit",
					StartSound = "/SFX/DusaRattle",
					Text = "There has to be a way to help him, Prince! He's been so loyal to this House, and he has to be your truest friend of everybody here. But he's the pity-party type. He needs someone to get him out of this!" },
				OnQueuedFunctions =
				{
					{
						Name = "CheckDistanceTriggerThread",
						Args = PresetEventArgs.CleaningConfident,
						GameStateRequirements =
						{
							RequiredTextLines = { "BecameCloseWithDusa01" },
						},
					},
					{
						Name = "CheckDistanceTriggerThread",
						Args = PresetEventArgs.Cleaning,
						GameStateRequirements =
						{
							RequiredFalseTextLines = { "BecameCloseWithDusa01" },
						},
					}
				},
				StatusAnimation = false,
			},
			DusaAboutMyrmidonReunionQuest01 =
			{
				PlayOnce = true,
				UseableOffSource = true,
				GiftableOffSource = true,
				-- [location: west hall north corner]
				TeleportToId = 421080,
				TeleportOffsetX = 30,
				TeleportOffsetY = 80,
				AngleTowardTargetId = 421080,
				RequiredTextLines = { "MyrmidonReunionQuestComplete", "DusaDate01" },
				RequiredFalseFlags = { "DusaNotYetReHired" },
				AreIdsNotAlive = { 370009 },
				EndVoiceLines =
				{
					{
						PreLineWait = 0.35,
						ObjectType = "NPC_Dusa_01",
						PreLineAnim = "NPCMedusaEmbarrassed",
						-- Woo-hoo!
						{ Cue = "/VO/Dusa_0341",  },
					},
				},
				{ Cue = "/VO/Dusa_0481", Portrait = "Portrait_Dusa_Empathetic_01", PortraitExitAnimation = "Portrait_Dusa_Empathetic_01_Exit",
					StartSound = "/SFX/DusaRattle",
					Emote = "PortraitEmoteCheerful",
					Text = "Oh, Prince, I heard the news about Master Achilles, that's so great! I'm going to miss him being here as often as he used to, but I'm just so happy for him, and for you!" },
				{ Cue = "/VO/ZagreusHome_2605", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "Me too, Dusa. The happy-for-him part, at any rate! He's a good friend, and he deserved much better than to be stuck here." },
				OnQueuedFunctions =
				{
					{
						Name = "CheckDistanceTriggerThread",
