				{ Cue = "/VO/ZagreusHome_1973", Portrait = "Portrait_Zag_Serious_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "I had another question about your relationship to other gorgons, Dusa. So... I notice you're not much for hissing, snakelike noises when you speak, and so on? Is that some sort of special gorgon language or something?" },
				{ Cue = "/VO/Dusa_0165", StartSound = "/SFX/DusaRattle", AngleTowardHero = true,
					PreLineThreadedFunctionName = "PlayEmoteAnimFromSource", PreLineThreadedFunctionArgs = { Emote = "PortraitEmoteAnger", DoShake = true, WaitTime = 3.45 },
					Text = "No, {#DialogueItalicFormat}um{#PreviousFormat}, not exactly, Prince! It's what it sounds like, {#DialogueItalicFormat}hrrrsssss{#PreviousFormat}!! Just, scary hissing, really, takes some practice not to sound like that with a forked tongue! I really wanted to fit in. Hey, how about you? Always wondered how come you have a surface accent. Weren't you born here?" },
				{ Cue = "/VO/ZagreusHome_1974", Portrait = "Portrait_Zag_Empathetic_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusInteractionThoughtful", PreLineAnimTarget = "Hero",
					Text = "Oh, wait, me? I never gave it any thought. I... guess it's from my father. He insisted that he teach me how to speak, himself. Maybe it's in the blood." },
