				{ Cue = "/VO/Dusa_0471",
					StartSound = "/SFX/DusaRattle", PostLineFunctionArgs = { Animation = "NPCMedusaHiding", }, PostLineFunctionName = "SetAnimationState",
					Emote = "PortraitEmoteNervous",
