				MinRunsSinceAnyTextLines = { TextLines = { "PersephoneFirstMeeting", "PersephoneMeeting02", "PersephoneMeeting03", "PersephoneMeeting04", "PersephoneMeeting05", "PersephoneMeeting06", "PersephoneMeeting07", "PersephoneMeeting08", "PersephoneMeeting09", "Ending01", "PersephoneAboutOlympianReunionQuest01", "OlympianReunionQuestComplete" }, Count = 4 },
				-- Wait! Nyx. Damn it.
				EndCue = "/VO/ZagreusHome_2290",
				EndWait = 0.37,
				{ Cue = "/VO/Dusa_0187",
					StartSound = "/SFX/DusaRattle",
					Emote = "PortraitEmoteCheerful",
					Text = "I, {#DialogueItalicFormat}um{#PreviousFormat}, Prince, I... wanted to say good-bye, because... it's my last day. Or night? {#DialogueItalicFormat}Um{#PreviousFormat}, anyway, it's really been a pleasure, knowing you, and working here, and so...!" },
				{ Cue = "/VO/ZagreusHome_2289", Portrait = "Portrait_Zag_Empathetic_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Emote = "PortraitEmoteSurprise",
					Text = "{#DialogueItalicFormat}Wait, wait, wait{#PreviousFormat}, what exactly happened, your last day or night, what is all this? You're really leaving? Why?" },
				{ Cue = "/VO/Dusa_0188",
					StartSound = "/SFX/DusaRattle", PostLineFunctionArgs = { Animation = "NPCMedusaHiding", }, PostLineFunctionName = "SetAnimationState",
					Emote = "PortraitEmoteDepressed",
					SetFlagTrue = "DusaFiredFromJob",
					Text = "I lost my job! Miss Nyx, she... it's her final word, and so... I need to go, before I make things any worse. Farewell, Highness! Farewell...!" },
