				TeleportOffsetX = 30,
				TeleportOffsetY = 80,
				AngleTowardTargetId = 421078,
				RequiredTextLines = { "NyxAboutDusaRelationship01", "DusaWithNyx04" },
				MinRunsSinceAnyTextLines = { TextLines = { "DusaWithNyx04" }, Count = 3 },
				-- Great to hear it, hope you keep it up!
				EndCue = "/VO/ZagreusHome_2298",
				EndWait = 0.35,
				{ Cue = "/VO/ZagreusHome_2297", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "How's work lately, Dusa? You seem to have a handle on your new responsibilities, or so I hear from Nyx." },
				{ Cue = "/VO/Dusa_0490",
					StartSound = "/SFX/DusaRattle",
					Portrait = "Portrait_Dusa_Confident_01",
					Emote = "PortraitEmoteCheerful",
					Text = "Oh yes, it's going pretty well, so far, I think! I have some new accounting duties, though I've parceled everything, and got a budget for a little extra help. It's funny, I-I should be busier than ever, but... I have more time to spare, lately, and things are good, I guess?" },
