					StartSound = "/SFX/DusaRattle",
					Portrait = "Portrait_Dusa_Empathetic_01", PortraitExitAnimation = "Portrait_Dusa_Empathetic_01_Exit",
					Text = "{#DialogueItalicFormat}Um{#PreviousFormat}, Prince, so, is it... really true, then, that you're staying, and... that you'll be working here, officially, I guess?" },
				{ Cue = "/VO/ZagreusHome_3225", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusInteractionThoughtful", PreLineAnimTarget = "Hero",
					Text = "I... well, when you put it that way, yes, I guess it is! We're going to try and make it work like this, for now." },
