				{ Cue = "/VO/Dusa_0462", StartSound = "/SFX/DusaRattle",
					Text = "Mister Hypnos, I, {#DialogueItalicFormat}uh{#PreviousFormat}, wanted to check in with you again, regarding the procession line that's... right in front of you, right over there, it's... moving pretty slow." },
				{ Cue = "/VO/Hypnos_0184", Portrait = "Portrait_ChildGhost_Default_01", Speaker = "NPC_Hypnos_01",
					Emote = "PortraitEmoteSurprise",
					Text = "I {#DialogueItalicFormat}know{#PreviousFormat}! Those shades, they just go on and on, and {#DialogueItalicFormat}on and on{#PreviousFormat}, forever, blathering about this or that! I mean, Lord Hades, it's a wonder he can {#DialogueItalicFormat}stand {#PreviousFormat}it, listening to them every day or night!" },
				{ Cue = "/VO/Dusa_0463",
					Emote = "PortraitEmoteNervous",
					Text = "{#DialogueItalicFormat}Um{#PreviousFormat}, well, it's just, I think you were supposed to keep things moving, so, if they're taking too long, I guess that maybe you could say something, or... any bright ideas?" },
				{ Cue = "/VO/Hypnos_0185", Portrait = "Portrait_ChildGhost_Default_01", Speaker = "NPC_Hypnos_01",
					Text = "It's a toughie, Dusa, because I think it's important that the shades who wait their turn, why, that they get a chance to say their piece! But it's a real tricky situation you bring up, so, let me tell you what I'm going to do: I am going to sleep on it! And then, get back to you!" },
				{ Cue = "/VO/Dusa_0464", PostLineFunctionArgs = { Animation = "NPCMedusaHiding", }, PostLineFunctionName = "SetAnimationState",
					Emote = "PortraitEmoteNervous",
					Text = "OK, well... if you don't, though, I'll check in with you again, later, I guess? Because, I'm under orders, so... {#DialogueItalicFormat}um{#PreviousFormat}... bye..." },
			},
			DusaWithHypnos03 =
			{
				Partner = "NPC_Hypnos_01",
				UseText = "UseListenNPC",
				BlockDistanceTriggers = true,
				PlayOnce = true,
				UseableOffSource = true,
				GiftableOffSource = true,
				TeleportToId = 370024,
				TeleportOffsetX = 100,
				TeleportOffsetY = 60,
				AngleTowardTargetId = 370024,
				RequiredTextLines = { "DusaWithHypnos02" },
				RequiredFalseTextLines = { "ThanatosWithHypnos06", "DusaWithHypnos04" },
				MinRunsSinceAnyTextLines = { TextLines = { "DusaWithHypnos02" }, Count = 9 },
				IgnoreStartTextLinesAngleTowardHero = true,
				StatusAnimation = false,
				EndVoiceLines =
				{
					PreLineWait = 0.5,
					ObjectType = "NPC_Hypnos_01",
					-- You bet!
					{ Cue = "/VO/Hypnos_0189", PreLineAnim = "HypnosIdleGreeting" },
				},
				{ Cue = "/VO/Hypnos_0187", Portrait = "Portrait_ChildGhost_Default_01", Speaker = "NPC_Hypnos_01",
					Emote = "PortraitEmoteCheerful",
					Text = "Heya Dusa, let me guess! You're here regarding the procession of these shades, who have been standing here for quite some time, clogging the hall a bit... they've barely moved at all!" },
				{ Cue = "/VO/Dusa_0465",
					Emote = "PortraitEmoteNervous",
					Text = "Y, you guessed it, Mister Hypnos, so... have you, um, given any thought to maybe focusing the traffic flow through here a little bit, to keep the hallway clearer, and, oh, I don't know, put some sort of time limit in place?" },
				{ Cue = "/VO/Hypnos_0188", Portrait = "Portrait_ChildGhost_Default_01", Speaker = "NPC_Hypnos_01",
					Text = "You know, can't say I have! I've got my registration list, and I have had to keep a careful eye on it, with all the shades that have been pouring in? It's really gotten slow around here!" },
				{ Cue = "/VO/Dusa_0466", PostLineFunctionArgs = { Animation = "NPCMedusaHiding", }, PostLineFunctionName = "SetAnimationState",
					Emote = "PortraitEmoteNervous",
					Text = "It definitely has, I... have to go, um, hope you get yourself some rest, OK?" },
			},
			DusaWithHypnos04 =
			{
				Partner = "NPC_Hypnos_01",
				UseText = "UseListenNPC",
				BlockDistanceTriggers = true,
				PlayOnce = true,
				UseableOffSource = true,
				GiftableOffSource = true,
				TeleportToId = 370024,
				TeleportOffsetX = 100,
				TeleportOffsetY = 60,
				AngleTowardTargetId = 370024,
				RequiredTextLines = { "DusaWithHypnos01", "ThanatosWithHypnos05" },
				MinRunsSinceAnyTextLines = { TextLines = { "DusaWithHypnos01" }, Count = 9 },
				RequiredCosmetics = { "Cosmetic_MainHallSarcophagi" },
				IgnoreStartTextLinesAngleTowardHero = true,
				EndVoiceLines =
				{
					PreLineWait = 0.5,
					ObjectType = "NPC_Hypnos_01",
					-- Mark it away!
					{ Cue = "/VO/Hypnos_0192", PreLineAnim = "HypnosIdleGreeting" },
				},
				{ Cue = "/VO/Dusa_0467",
					Emote = "PortraitEmoteSurprise", Portrait = "Portrait_Dusa_Empathetic_01",
					Text = "I can't believe it, Mister Hypnos, how... {#DialogueItalicFormat}how did you do it{#PreviousFormat}? All the shades that have been lining up, they... they've been moving right along!" },
				{ Cue = "/VO/Hypnos_0190", Portrait = "Portrait_ChildGhost_Default_01", Speaker = "NPC_Hypnos_01",
					Emote = "PortraitEmoteCheerful",
					Text = "You know, you're right, {#DialogueItalicFormat}they have{#PreviousFormat}! They definitely have. It's pretty hard to notice, since they're slow, but I have a keen eye. And so do you! You noticed before I did in this case. What did you say your question was back there?" },
				{ Cue = "/VO/Dusa_0468",
					Text = "I asked you how you did it, got the traffic flow moving a little faster, and... it's the sarcophagus things, isn't it? Right down the hall! The shades, they're scared to stand around! That's brilliant, Mister Hypnos, I'm impressed!" },
				{ Cue = "/VO/Hypnos_0191", Portrait = "Portrait_ChildGhost_Default_01", Speaker = "NPC_Hypnos_01",
					Text = "Oh, {#DialogueItalicFormat}hahaha{#PreviousFormat}, that, well, really, it was nothing, Dusa! Though, I'll tell you something! It is good to know that you're impressed with me!" },
				{ Cue = "/VO/Dusa_0469", PostLineFunctionArgs = { Animation = "NPCMedusaHiding", }, PostLineFunctionName = "SetAnimationState",
					Emote = "PortraitEmoteNervous",
					Text = "I, {#DialogueItalicFormat}haha{#PreviousFormat}, don't know if I'd go that far, um, anyway! I'll mark this as resolved!" },
			},
			DusaWithNyx01 =
			{
				Partner = "NPC_Nyx_01",
				PlayOnce = true,
				RequiredMinCompletedRuns = 8,
				UseText = "UseListenNPC",
				BlockDistanceTriggers = true,
				StatusAnimation = false,
				UseableOffSource = true,
				GiftableOffSource = true,
				TeleportToId = 370010,
				TeleportOffsetX = -90,
				TeleportOffsetY = 120,
				AngleTowardTargetId = 370010,
				InteractDistance = 375,
				RequiredAnyTextLines = { "DusaFirstMeeting", "DusaFirstMeeting_Alt", },
				RequiredTextLines = { "NyxFirstMeeting", "DusaGift01", "NyxGrantsRespec" },
				RequiredFalseTextLinesThisRun = { "PersephoneFirstMeeting" },
				IgnoreStartTextLinesAngleTowardHero = true,
				-- Poor Dusa....
				EndCue = "/VO/ZagreusHome_0488",
				EndWait = 1.0,
				{ Cue = "/VO/Nyx_0080", PreLineWait = 0.35, Portrait = "Portrait_Nyx_Default_01", Speaker = "NPC_Nyx_01",
					Text = "...And after that, Dusa, you are to clean the rafters once again, and prune the gardens thoroughly. By then it should be time to tend the kitchens, and sweep the lounging area a second time..." },
				{ Cue = "/VO/Dusa_0030", PostLineFunctionArgs = { Animation = "NPCMedusaHiding", }, PostLineFunctionName = "SetAnimationState", StartSound = "/SFX/DusaRattle",
					Emote = "PortraitEmoteNervous",
					Text = "Y, yes, right away!" },
			},
			DusaWithNyx02 =
			{
				Partner = "NPC_Nyx_01",
				PlayOnce = true,
				RequiredMinCompletedRuns = 12,
				UseText = "UseListenNPC",
				BlockDistanceTriggers = true,
				StatusAnimation = false,
				UseableOffSource = true,
				GiftableOffSource = true,
				TeleportToId = 370010,
				TeleportOffsetX = -90,
				TeleportOffsetY = 120,
				AngleTowardTargetId = 370010,
				InteractDistance = 375,
				RequiredTextLines = { "DusaWithNyx01", "DusaGift04", "DusaVsNyx01" },
				RequiredFalseTextLinesThisRun = { "PersephoneFirstMeeting" },
				IgnoreStartTextLinesAngleTowardHero = true,
				-- Oh no...
				EndCue = "/VO/ZagreusHome_0489",
				EndWait = 0.6,
				{ Cue = "/VO/Nyx_0087", PreLineWait = 0.35, Portrait = "Portrait_Nyx_Default_01", Speaker = "NPC_Nyx_01",
					Text = "...Lastly, Dusa, you are not permitted to address the Prince, unless first spoken to. You are invisible to him. Now, are we understood?" },
				{ Cue = "/VO/Dusa_0074", PostLineFunctionArgs = { Animation = "NPCMedusaHiding", }, PostLineFunctionName = "SetAnimationState", StartSound = "/SFX/DusaRattle",
					Emote = "PortraitEmoteDepressed",
					Text = "Yes, Miss Nyx, we're understood, I mean... I understand. I, I'll be going, then." },
			},
			-- Dusa Fired Quest (ReHired)
			DusaWithNyx03 =
			{
				SuperPriority = true,
				PlayOnce = true,
				Partner = "NPC_Nyx_01",
				UseText = "UseListenNPC",
				-- BlockDistanceTriggers = true,
				UseableOffSource = true,
				GiftableOffSource = true,
				TeleportToId = 370010,
				TeleportOffsetX = -90,
				TeleportOffsetY = 120,
				AngleTowardTargetId = 370010,
				InteractDistance = 375,
				RequiredTextLines = { "NyxAboutDusa04" },
				RequiredFalseTextLinesThisRun = { "PersephoneFirstMeeting" },
				IgnoreStartTextLinesAngleTowardHero = true,
				EndVoiceLines =
				{
					{
						PreLineWait = 0.35,
						ObjectType = "NPC_Nyx_01",
						-- Do your best work.
						{ Cue = "/VO/Nyx_0201" },
					},
					{
						PreLineWait = 0.15,
						UsePlayerSource = true,
						-- Hooray...!
						{ Cue = "/VO/ZagreusHome_2260" },
					},
				},
				{ Cue = "/VO/Dusa_0434", StartSound = "/SFX/DusaRattle",
					Emote = "PortraitEmoteNervous", FontSize = 24,
					Text = "Miss Nyx, you're...? Do I want my job again, I... {#DialogueItalicFormat}wha{#PreviousFormat}, I, more than anything! This job, it, it means everything to me, I mean, working with {#DialogueItalicFormat}you{#PreviousFormat}? You are the Night Itself! This place, and all of you, all of it, it's so amazing to me, and I just... I, just I want to help! Just, give me one more chance!" },
				{ Cue = "/VO/Nyx_0200", Portrait = "Portrait_Nyx_Default_01", Speaker = "NPC_Nyx_01", FontSize = 24,
					PreLineAnim = "NyxIdleGreeting", PreLineAnimTarget = 370010,
					Text = "Your one more chance has certain stipulations, Dusa. If you think it is necessary to exceed the allotted time for your responsibilities, I expect you to ask. You are not the sole bearer of this House's fate. Your temperament toward your work is as important as the end result. Now then... I would ask that you return to your post." },
				{ Cue = "/VO/Dusa_0435", PostLineFunctionArgs = { Animation = "NPCMedusaHiding", }, PostLineFunctionName = "SetAnimationState", StartSound = "/SFX/DusaRattle",
					Emote = "PortraitEmoteAffection",
					SetFlagFalse = "DusaNotYetReHired",
					Text = "What, I, Miss Nyx, you mean... like, start right now? {#DialogueItalicFormat}Now {#PreviousFormat}now? I mean, I, I don't have a lot of pressing obligations, so, I'll do it, {#DialogueItalicFormat}yes{#PreviousFormat}!! I accept! I'm so excited to be back! Where do I even start, I can survey the lounge, sweep the cobwebs, walk Cerberus, check the pantry, ah, OK, I'm off!!" },
			},
			-- Dusa Fired / Dusa Promoted
			DusaWithNyx04 =
			{
				PlayOnce = true,
				Partner = "NPC_Nyx_01",
				UseText = "UseListenNPC",
				BlockDistanceTriggers = true,
				UseableOffSource = true,
				StatusAnimation = false,
				GiftableOffSource = true,
				TeleportToId = 370010,
				TeleportOffsetX = -90,
				TeleportOffsetY = 120,
				AngleTowardTargetId = 370010,
				InteractDistance = 375,
				RequiredTextLines = { "DusaWithNyx03", "NyxGift09", "BecameCloseWithDusaAftermath01", "DusaAboutWorkLifeBalance01" },
				RequiredFalseTextLinesThisRun = { "PersephoneFirstMeeting" },
				RequiredFalseTextLinesLastRun = { "BecameCloseWithDusaAftermath01" },
				MinRunsSinceAnyTextLines = { TextLines = { "DusaWithNyx03" }, Count = 15 },
				IgnoreStartTextLinesAngleTowardHero = true,
				EndVoiceLines =
				{
					{
						PreLineWait = 0.35,
						ObjectType = "NPC_Dusa_01",
						-- Thank you!
						{ Cue = "/VO/Dusa_0437" },
					},
					{
						PreLineWait = 0.15,
						UsePlayerSource = true,
						-- Well deserved.
						{ Cue = "/VO/ZagreusHome_2261" },
					},
				},
				{ Cue = "/VO/Nyx_0202", Portrait = "Portrait_Nyx_Default_01", Speaker = "NPC_Nyx_01",
					Text = "...and, therefore, for your diligence and service, and for having demonstrated such improvement... by my authority as Night Itself, I hereby name you Chief Custodian of the House of Hades, and appoint to you the privileges implied. Bear the responsibility with honor, Dusa." },
				{ Cue = "/VO/Dusa_0436", StartSound = "/SFX/DusaRattle",
					Emote = "PortraitEmoteCheerful",
					Text = "Miss Nyx, I am truly honored, and, I'm very grateful for this opportunity, and for the trust you've placed in me, in this expanded role. I promise you, I will not let you down. Not you, or anybody here! Thank you so much!!" },
				{ Cue = "/VO/Nyx_0203", Portrait = "Portrait_Nyx_Default_01", Speaker = "NPC_Nyx_01",
					PostLineFunctionArgs = { Animation = "NPCMedusaHiding", WaitTime = 2.5 }, PostLineFunctionName = "SetAnimationState", StartSound = "/SFX/DusaRattle",
					Text = "You are most welcome. The orderliness of this House is a reflection of the realm as a whole. You have internalized this, and understood that a disciplined approach toward work is far more valuable than mere exertion of effort. Although, you understand the need for exertion, as well." },
			},
			DusaWithMegaera01 =
			{
				Partner = "NPC_FurySister_01",
				PlayOnce = true,
				UseText = "UseListenNPC",
				BlockDistanceTriggers = true,
				StatusAnimation = false,
				UseableOffSource = true,
				GiftableOffSource = true,
				TeleportToId = 390082,
				TeleportOffsetX = -150,
				TeleportOffsetY = -30,
				AngleTowardTargetId = 390082,
				InteractDistance = 350,
				RequiredAnyTextLines = { "DusaFirstMeeting", "DusaFirstMeeting_Alt", },
				RequiredTextLines = { "FuryEncounter04", "MegaeraMeeting02" },
				RequiredFalseFlags = { "DusaNotYetReHired" },
				IgnoreStartTextLinesAngleTowardHero = true,
				BeginGossipingOnUseableOff = true,
				-- Ladies night I guess. Or day? I don't know.
				EndCue = "/VO/ZagreusHome_0490",
				EndWait = 0.6,
				{ Cue = "/VO/MegaeraHome_0014", PreLineWait = 0.35, Portrait = "Portrait_FurySister01_Standoffish_01", Speaker = "NPC_FurySister_01",
					Text = "...And then, all seven of those wretches tried cutting to the front of the procession line, can you believe the nerve? As though they'd get an audience that way." },
				{ Cue = "/VO/Dusa_0072", StartSound = "/SFX/DusaRattle",
					Text = "{#DialogueItalicFormat}Ha, hahah{#PreviousFormat}, what a bunch of dummies! What were they even thinking, cutting the line, I mean, in front of {#DialogueItalicFormat}you{#PreviousFormat}?" },
				{ Cue = "/VO/MegaeraHome_0015", PreLineWait = 0.35, Portrait = "Portrait_FurySister01_Standoffish_01", Speaker = "NPC_FurySister_01", SetFlagTrue = "MegDusaGossipLegal",
					Text = "Let's just say, if they still have any complaints for our Lord Hades, they'll be waiting for a long, long time before they get the chance to bring them up." },
			},
			DusaWithMegaera02 =
			{
				Partner = "NPC_FurySister_01",
				PlayOnce = true,
				UseText = "UseListenNPC",
				BlockDistanceTriggers = true,
				StatusAnimation = false,
				UseableOffSource = true,
				GiftableOffSource = true,
				TeleportToId = 390082,
				TeleportOffsetX = -150,
				TeleportOffsetY = -30,
				AngleTowardTargetId = 390082,
				InteractDistance = 350,
				RequiredTextLines = { "DusaWithMegaera01", },
				RequiredFalseFlags = { "DusaNotYetReHired" },
				IgnoreStartTextLinesAngleTowardHero = true,
				-- They get along at least.
				EndCue = "/VO/ZagreusHome_0491",
				EndWait = 0.6,
				{ Cue = "/VO/MegaeraHome_0016", PreLineWait = 0.35, Portrait = "Portrait_FurySister01_Standoffish_01", Speaker = "NPC_FurySister_01",
					Text = "...I swear you're right that one is such an idiot...! Though anyway, I'd best get back to work. Next round's on me, Dusa, and you take care." },
				{ Cue = "/VO/Dusa_0073", PostLineFunctionArgs = { Animation = "NPCMedusaHiding", }, PostLineFunctionName = "SetAnimationState", StartSound = "/SFX/DusaRattle", SetFlagTrue = "MegDusaGossipLegal",
					Text = "You take care, too, Miss Meg, thanks for the drink! Bye now...!" },
			},
			DusaWithMegaera03 =
			{
				Partner = "NPC_FurySister_01",
				PlayOnce = true,
				UseText = "UseListenNPC",
				UseableOffSource = true,
				BlockDistanceTriggers = true,
				StatusAnimation = false,
				GiftableOffSource = true,
				TeleportToId = 390082,
				TeleportOffsetX = -150,
				TeleportOffsetY = -30,
				StatusAnimation = false,
				AngleTowardTargetId = 390082,
				InteractDistance = 350,
				RequiredTextLines = { "DusaWithMegaera02", "MegaeraWithHades01" },
				IgnoreStartTextLinesAngleTowardHero = true,
				BeginGossipingOnUseableOff = true,
				RequiredFalseTextLinesThisRun = { "Ending01" },
				RequiredFalseFlags = { "DusaNotYetReHired" },
				EndVoiceLines =
				{
					PreLineWait = 0.35,
					ObjectType = "NPC_Dusa_01",
					-- What are you, kidding me? Of course!
					{ Cue = "/VO/Dusa_0179" },
				},
				{ Cue = "/VO/MegaeraHome_0077", PreLineWait = 0.35, Portrait = "Portrait_FurySister01_Standoffish_01", Speaker = "NPC_FurySister_01",
					Text = "...Follow your heart? That's odd advice, especially from you. Though, no offense intended." },
				{ Cue = "/VO/Dusa_0178", StartSound = "/SFX/DusaRattle",
					Portrait = "Portrait_Dusa_Empathetic_01", PortraitExitAnimation = "Portrait_Dusa_Empathetic_01_Exit",
					Text = "I just mean, if you don't believe in the work you're doing, why keep on doing it, just out of obligation? You have to take care of yourself." },
				{ Cue = "/VO/MegaeraHome_0078", PreLineWait = 0.35, Portrait = "Portrait_FurySister01_Standoffish_01", Speaker = "NPC_FurySister_01", SetFlagTrue = "MegDusaGossipLegal",
					Text = "I don't think there's any rush for that. We won't just shrivel up and die in forty years like mortals do. But, I know what you're saying. I'll think about it, Dusa. Thanks." },
			},
			DusaWithMegaera04 =
			{
				Partner = "NPC_FurySister_01",
				PlayOnce = true,
				UseText = "UseListenNPC",
				BlockDistanceTriggers = true,
				StatusAnimation = false,
				UseableOffSource = true,
				GiftableOffSource = true,
				TeleportToId = 390082,
				TeleportOffsetX = -150,
				TeleportOffsetY = -30,
				AngleTowardTargetId = 390082,
				InteractDistance = 350,
				RequiredTextLines = { "FuryMetSisters01", "Fury2FirstAppearance", "Fury3FirstAppearance" },
				RequiredFalseTextLinesThisRun = { "Ending01" },
				RequiredFalseFlags = { "DusaNotYetReHired" },
				IgnoreStartTextLinesAngleTowardHero = true,
				BeginGossipingOnUseableOff = true,
				EndVoiceLines =
				{
					PreLineWait = 0.45,
					ObjectType = "NPC_Dusa_01",
					-- Hey, look, I understand. I really do.
					{ Cue = "/VO/Dusa_0181" },
				},
				{ Cue = "/VO/MegaeraHome_0079", PreLineWait = 0.35, Portrait = "Portrait_FurySister01_Standoffish_01", Speaker = "NPC_FurySister_01",
					Text = "...We got off on the wrong foot from the start. Just what happens when you're born of Titan blood, I guess. It's for the best we haven't crossed paths much since." },
				{ Cue = "/VO/Dusa_0180", StartSound = "/SFX/DusaRattle",
					Portrait = "Portrait_Dusa_Empathetic_01", PortraitExitAnimation = "Portrait_Dusa_Empathetic_01_Exit",
					Text = "But they're your sisters, I mean... they're family! You must have something in common, some way of, I don't know, seeing eye to eye?" },
				{ Cue = "/VO/MegaeraHome_0080", PreLineWait = 0.35, Portrait = "Portrait_FurySister01_Standoffish_01", Speaker = "NPC_FurySister_01", SetFlagTrue = "MegDusaGossipLegal",
					Text = "You're more my sister than they are to me, Dusa. I don't always agree with Lord Hades but I am thankful that they're not permitted in this House." },
			},
			DusaWithMegaeraHighAffinity01 =
			{
				Partner = "NPC_FurySister_01",
				PlayOnce = true,
				UseText = "UseListenNPC",
				BlockDistanceTriggers = true,
				StatusAnimation = false,
				UseableOffSource = true,
				GiftableOffSource = true,
				TeleportToId = 390082,
				TeleportOffsetX = -150,
				TeleportOffsetY = -30,
				AngleTowardTargetId = 390082,
				InteractDistance = 350,
				RequiredAnyTextLines = { "BecameCloseWithMegaera01", "BecameCloseWithMegaera01_B", },
				RequiredFalseFlags = { "DusaNotYetReHired" },
				RequiredFalseTextLinesThisRun = { "Ending01" },
				IgnoreStartTextLinesAngleTowardHero = true,
				EndVoiceLines =
				{
					PreLineWait = 0.45,
					ObjectType = "NPC_FurySister_01",
					-- You're right. And same to you.
					{ Cue = "/VO/MegaeraHome_0360" },
				},
				{ Cue = "/VO/MegaeraHome_0358", PreLineWait = 0.35, Portrait = "Portrait_FurySister01_Standoffish_01", Speaker = "NPC_FurySister_01",
					Text = "...You sure that you're all right, with everything? I just... I wouldn't want you to be caught up in the middle of all this. If you so much as said to call it off..." },
				{ Cue = "/VO/Dusa_0158", StartSound = "/SFX/DusaRattle",
					Emote = "PortraitEmoteFiredUp", Portrait = "Portrait_Dusa_Empathetic_01", PortraitExitAnimation = "Portrait_Dusa_Empathetic_01_Exit",
					Text = "No, {#DialogueItalicFormat}nononono{#PreviousFormat}, no, Meg, no! No, Meg! Come on, now, are you kidding me? I mean, I couldn't be happier about it, really! You deserve this! As long as you're happy, yourself, right?" },
				{ Cue = "/VO/MegaeraHome_0359", PreLineWait = 0.35, Portrait = "Portrait_FurySister01_Pleased_01", Speaker = "NPC_FurySister_01",
					Text = "Well, thank you, Dusa. You've always been so kind. I don't quite know what to say." },
				{ Cue = "/VO/Dusa_0159", StartSound = "/SFX/DusaRattle",
					Emote = "PortraitEmoteCheerful", PostLineFunctionArgs = { Animation = "NPCMedusaHiding", }, PostLineFunctionName = "SetAnimationState", StartSound = "/SFX/DusaRattle",
					Text = "Hey, now, that's my line! Look, though, I better get going, but, I just want to say this is great news, all right? Thank you for telling me!" },
