		GiftTextLineSets =
		{
			-- grants a gift
			DusaGift01 =
			{
				PlayOnce = true,
				{ Cue = "/VO/ZagreusHome_0111", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "{#DialogueItalicFormat}Erm{#PreviousFormat}, Ms. Dusa? I wanted you to have this. It's nothing really, just... for your hard work." },
				{ Cue = "/VO/Dusa_0046", PreLineAnim = "NPCMedusaEmbarrassed", PreLineWait = 0.35, StartSound = "/SFX/DusaRattle",
					Emote = "PortraitEmoteNervous",
					Text = "P-P-Prince, I'm really grateful for this, really, and I thought, I, kind of got something for you...?" },
			},
			DusaGift02 =
			{
				PlayOnce = true,
				RequiredTextLines = { "DusaGift01", },
				{ Cue = "/VO/Dusa_0039", PreLineAnim = "NPCMedusaEmbarrassed", PreLineWait = 0.35, StartSound = "/SFX/DusaRattle",
					Emote = "PortraitEmoteNervous",
					Text = "{#DialogueItalicFormat}Wh-whaaaa{#PreviousFormat}, b-but, there must be some mistake, I, Prince, you're giving this to {#DialogueItalicFormat}me{#PreviousFormat}...?" },
				{ Cue = "/VO/ZagreusHome_0386", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					Text = "I want you to know we all appreciate you, Dusa. Don't work yourself too hard on my account, OK?" },
			},
			DusaGift03 =
			{
				PlayOnce = true,
				RequiredTextLines = { "DusaGift02", },
				{ Cue = "/VO/Dusa_0040", PreLineAnim = "NPCMedusaEmbarrassed", PreLineWait = 0.35, StartSound = "/SFX/DusaRattle",
					Emote = "PortraitEmoteSurprise",
					Text = "{#DialogueItalicFormat}Ohh{#PreviousFormat}, oh, oh, oh my, Your Highness, are you... are you really sure you want me to have {#DialogueItalicFormat}this{#PreviousFormat}?" },
				{ Cue = "/VO/ZagreusHome_0387", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					Text = "I really am, Dusa. I hope you enjoy it." },
			},
			DusaGift04 =
			{
				PlayOnce = true,
				RequiredTextLines = { "DusaGift03", },
				{ Cue = "/VO/Dusa_0041", PreLineAnim = "NPCMedusaEmbarrassed", PreLineWait = 0.35, StartSound = "/SFX/DusaRattle",
					Emote = "PortraitEmoteNervous",
					Text = "B-b-but, Prince, I, don't know what to say, I... are you joking around with me, maybe...?" },
				{ Cue = "/VO/ZagreusHome_0388", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					Text = "Oh, I am deadly serious, Dusa, honest. It's for you." },
			},

			DusaGift05_Alt =
			{
				PlayOnce = true,
				UseableOffSource = true,
				RequiredTextLines = { "DusaGift04", },
				RequiredFalseTextLines = { "DusaGift05" },
				EndVoiceLines =
				{
					PreLineWait = 0.45,
					UsePlayerSource = true,
					-- OK, bye...!
					{ Cue = "/VO/ZagreusHome_1935" },
				},
				{ Cue = "/VO/ZagreusHome_1933", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					Text = "Dusa, you must work harder than anyone in this House, keeping everything so neat and orderly. I don't think it should be thankless work, so, thank you! And I'd like for you to have this." },
				{ Cue = "/VO/Dusa_0129", PreLineAnim = "NPCMedusaEmbarrassed", PreLineWait = 0.35, StartSound = "/SFX/DusaRattle",
					Emote = "PortraitEmoteAffection",
					Text = "Oh, Prince, {#DialogueItalicFormat}wow{#PreviousFormat}, thank you! Thanks so much!! You've been so generous, I... I think I'm speechless! No, can't think of anything to say right now!" },
				{ Cue = "/VO/ZagreusHome_1934", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					Text = "Oh, you said it all! Don't mean to put you on the spot or anything. I'm just glad we've had a chance to get to know each other." },
				{ Cue = "/VO/Dusa_0130",
					PostLineFunctionArgs = { Animation = "NPCMedusaHiding", }, PostLineFunctionName = "DusaExit", StartSound = "/SFX/DusaRattle",
					Emote = "PortraitEmoteNervous",
					Text = "I... I'm glad we've had a chance to know each other, too. Thanks for the Nectar! I can tell it's really, really good! {#DialogueItalicFormat}Um{#PreviousFormat}, OK, bye!" },
			},

			DusaGift06 =
			{
				PlayOnce = true,
				UseableOffSource = true,
				RequiredAnyTextLines = { "DusaGift05", "DusaGift05_Alt" },
				EndVoiceLines =
				{
					PreLineWait = 0.45,
					UsePlayerSource = true,
					-- Well, you enjoy, all right?
					{ Cue = "/VO/ZagreusHome_1938" },
				},
				{ Cue = "/VO/ZagreusHome_1936", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					Text = "Hey Dusa? Here's some more of this really, really good Nectar for you! Please keep up the wonderful work, and more importantly, keep being your wonderful self." },
				{ Cue = "/VO/Dusa_0131", PreLineAnim = "NPCMedusaEmbarrassed", PreLineWait = 0.35, StartSound = "/SFX/DusaRattle",
					Emote = "PortraitEmoteNervous",
					Text = "I, I, I, I'm really very flattered, Prince, but, I've just been so busy, lately, that I haven't even had a chance to try the last bottle you gave me, so... you want this back, maybe for someone else?" },
				{ Cue = "/VO/ZagreusHome_1937", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					Text = "Please, it's yours! Besides, you must have some time off coming up when you can enjoy it, don't you?" },
				{ Cue = "/VO/Dusa_0132",
					PostLineFunctionArgs = { Animation = "NPCMedusaHiding", }, PostLineFunctionName = "DusaExit", StartSound = "/SFX/DusaRattle",
					Emote = "PortraitEmoteNervous",
					Text = "{#DialogueItalicFormat}Oh{#PreviousFormat}! I mean, maybe I do? I think maybe I do, except, nobody's told me anything about that, and besides, there's so much work to do around the House still, I just can't leave everything like this!!" },
			},

			-- high relationship / locked gifts
			DusaGift07 =
			{
				PlayOnce = true,
				UseableOffSource = true,
				RequiredTextLines = { "DusaGift06" },
				{ Cue = "/VO/ZagreusHome_1941", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					Text = "Hey, Dusa? Congratulations on completing your assignment with the lounge. Look at this place! It's better than ever. All thanks to your effort and dedication. Here, this is for you. Something special for the occasion." },
				{ Cue = "/VO/Dusa_0134", PreLineAnim = "NPCMedusaEmbarrassed", PreLineWait = 0.35,
					Emote = "PortraitEmoteSurprise",
					PostLineFunctionArgs = { Animation = "NPCMedusaHiding", }, PostLineFunctionName = "DusaExit", StartSound = "/SFX/DusaRattle",
					Text = "{#DialogueItalicFormat}Whoa{#PreviousFormat}, I, this, this is {#DialogueItalicFormat}Ambrosia{#PreviousFormat}, Prince! But, this is for {#DialogueItalicFormat}gods{#PreviousFormat}...! This isn't for... someone like me? But, wait, wait wait, no, here, I'm going to do it, I am going to do it, here, take {#DialogueItalicFormat}this{#PreviousFormat}!! I got it, just for you, so, take it, please! Farewell!!" },
			},
			DusaGift08 =
			{
				PlayOnce = true,
				UseableOffSource = true,
				RequiredTextLines = { "DusaGift07" },
				EndVoiceLines =
				{
					PreLineWait = 0.45,
					UsePlayerSource = true,
					-- Dusa, wait...!
					{ Cue = "/VO/ZagreusHome_1946" },
				},
				{ Cue = "/VO/ZagreusHome_1943", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Text = "Ambrosia isn't only for the gods, you know, Dusa. It's for anyone we really care about. So, here. Please, enjoy this, or... just add it to the lounge's stash. Whatever suits you." },
				{ Cue = "/VO/Dusa_0135", PreLineAnim = "NPCMedusaEmbarrassed", PreLineWait = 0.35, StartSound = "/SFX/DusaRattle",
					Emote = "PortraitEmoteNervous",
					Text = "{#DialogueItalicFormat}Oh{#PreviousFormat}, I, but, you... really mean that, huh...? But, you're the Prince himself, and I'm just... look, we both know I am just a floating monster head. And a custodian. I just don't understand. Why keep on being nice to me, like this? I thought you'd never even talk to me..." },
				{ Cue = "/VO/ZagreusHome_1944", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusInteractionThoughtful", PreLineAnimTarget = "Hero",
					Text = "You're so much more than what you said. I wish you could see that. If only everybody in this House had your spirit." },
				{ Cue = "/VO/Dusa_0510", PreLineAnim = "NPCMedusaEmbarrassed", PreLineWait = 0.35, StartSound = "/SFX/DusaRattle",
					Emote = "PortraitEmoteAffection",
					Text = "{#DialogueItalicFormat}Ah{#PreviousFormat}. If only everybody in this House had yours, Prince Zagreus. You've been so kind to me, and I'm glad we've had a chance to get to know each other more. It's really nice, sometimes... knowing somebody really cares about me here?" },
				{ Cue = "/VO/ZagreusHome_1945", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "It should be more than sometimes that you feel that way, as far as I'm concerned. I just... I've never met anybody like you before. My father always told me not to smile quite as much, but... you're like me." },
				{ Cue = "/VO/Dusa_0137", PreLineAnim = "NPCMedusaEmbarrassed", PreLineWait = 0.35,
					Emote = "PortraitEmoteCheerful",
					PostLineFunctionArgs = { Animation = "NPCMedusaHiding", }, PostLineFunctionName = "DusaExit", StartSound = "/SFX/DusaRattle",
					Text = "What, because I smile, {#DialogueItalicFormat}haha{#PreviousFormat}? Smiling's just a little something that I like to do to deal with the day! Though, oh, {#DialogueItalicFormat}that's {#PreviousFormat}what you meant, I... oh, I see. I, look, this is kind of a lot, I, I had better go...! I'll cherish the Ambrosia, though, OK?!" },
			},

			DusaGift09 =
			{
				PlayOnce = true,
				UseableOffSource = true,
				RequiredTextLines = { "DusaGift08" },
				EndVoiceLines =
				{
					PreLineWait = 0.45,
					UsePlayerSource = true,
					-- See you, Dusa.
					{ Cue = "/VO/ZagreusHome_1950" },
				},
				{ Cue = "/VO/ZagreusHome_1947", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Text = "I got you more Ambrosia, Dusa, and I hope this is OK. I figure maybe you could use this for the lounge? Special events, guests, that sort of thing?" },
				{ Cue = "/VO/Dusa_0138", PreLineAnim = "NPCMedusaEmbarrassed", PreLineWait = 0.35, StartSound = "/SFX/DusaRattle",
					Emote = "PortraitEmoteNervous",
					Portrait = "Portrait_Dusa_Empathetic_01", PortraitExitAnimation = "Portrait_Dusa_Empathetic_01_Exit",
					Text = "...I'm sorry that I flew away last time you brought me something like this, Prince... I mean, Zagreus. It's really strange for me to call you that, I... when I started here, they told me not to even speak with you, and now, {#DialogueItalicFormat}uh{#PreviousFormat}... here we are...!" },
				{ Cue = "/VO/ZagreusHome_1948", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Text = "Well, look, if I listened to everything I was told, my life would be a lot different and a lot worse off, I think! So... who cares what anybody says? I enjoy your company." },
				{ Cue = "/VO/Dusa_0139", PreLineAnim = "NPCMedusaEmbarrassed",
					Emote = "PortraitEmoteAffection",
					Text = "And I enjoy yours, too! Wasn't long ago that I was just a lonely gorgon head, all by myself in the big Underworld, looking for steady work. And then, I heard about the opening here, in your father's House? Well I got it! It seemed too good to be real. And then, there was you..." },
				{ Cue = "/VO/ZagreusHome_1949", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "I remember when we first met. You were carrying three bowls for Cerberus and bumped right into me. It all went flying, but you cleaned up half of it before it hit the ground. Then flew away before I got your name. I thought I'd never see you again." },
				{ Cue = "/VO/Dusa_0140", PreLineAnim = "NPCMedusaEmbarrassed", PreLineWait = 0.35,
					Emote = "PortraitEmoteNervous",
					PostLineFunctionArgs = { Animation = "NPCMedusaHiding", }, PostLineFunctionName = "DusaExit", StartSound = "/SFX/DusaRattle",
					Text = "I was {#DialogueItalicFormat}so {#PreviousFormat}embarrassed! Worse... I thought I was done for, here. But, I got another chance! That was your doing, wasn't it? You've been looking out for me, all this time! That's so sweet of you, I... I don't know how to thank you, I... I, I better put this Ambrosia away, for safe keeping, OK?" },
			},

			DusaGift10 =
			{
				PlayOnce = true,
				UseableOffSource = true,
				RequiredTextLines = { "DusaGift09" },
				EndVoiceLines =
				{
					PreLineWait = 0.55,
					UsePlayerSource = true,
					-- See you soon.
					{ Cue = "/VO/ZagreusHome_1955" },
				},
				{ Cue = "/VO/ZagreusHome_1951", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Text = "Dusa, I was thinking that this batch of Ambrosia ought to hold the lounge over for quite a while! Keep things livelier for everyone. And, please take a bottle for yourself, won't you? As a token of my affection." },
				{ Cue = "/VO/Dusa_0141", PreLineAnim = "NPCMedusaEmbarrassed", PreLineWait = 0.35, StartSound = "/SFX/DusaRattle",
					Emote = "PortraitEmoteNervous",
					AngleTowardHero = true,
					Portrait = "Portrait_Dusa_Empathetic_01", PortraitExitAnimation = "Portrait_Dusa_Empathetic_01_Exit",
					Text = "{#DialogueItalicFormat}Oh{#PreviousFormat}! Y-you said your {#DialogueItalicFormat}affection{#PreviousFormat}, huh, I... wow! I... really like you, also, Zagreus. I liked you from the start! You were so different from your father, and Miss Nyx, I could just tell, but... wait, what am I saying, I am such a dummy! You're just being friendly with me, right...?" },
				{ Cue = "/VO/ZagreusHome_2610", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "I would be more than that, if it pleased you. You're very wonderful, and... you light up this House. But I would never risk our friendship, so, if this is where we draw the boundary, please, just let me know." },
				{ Cue = "/VO/Dusa_0511", PreLineAnim = "NPCMedusaEmbarrassed",
					Emote = "PortraitEmoteSparkly",
					Portrait = "Portrait_Dusa_Empathetic_01", PortraitExitAnimation = "Portrait_Dusa_Empathetic_01_Exit",
					Text = "{#DialogueItalicFormat}Um{#PreviousFormat}... wait, wait. Wait, wait. Did you just say I'm... {#DialogueItalicFormat}wonderful{#PreviousFormat}?" },
				{ Cue = "/VO/ZagreusHome_1953", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "I did, indeed. And I am glad to get it off my chest, if you must know! Though... what about you? How do you feel?" },
				{ Cue = "/VO/Dusa_0143", PreLineAnim = "NPCMedusaEmbarrassed",
					Emote = "PortraitEmoteCheerful",
					Text = "I, {#DialogueItalicFormat}wow{#PreviousFormat}, I, I never thought you'd... have those kinds of feelings, for somebody like me. It's really nice to hear! It makes me feel complete. But, {#DialogueItalicFormat}um{#PreviousFormat}, Zagreus, I don't know if I can be what you need, I mean? Wait. You weren't, you weren't just being nice to me for all this time, because...?" },
				{ Cue = "/VO/ZagreusHome_1954", Portrait = "Portrait_Zag_Empathetic_01", Speaker = "CharProtag",
					Emote = "PortraitEmoteSurprise",
					PreLineAnim = "ZagreusInteractionThoughtful", PreLineAnimTarget = "Hero",
					Text = "{#DialogueItalicFormat}What{#PreviousFormat}? No, no, Dusa, of course not, I... {#DialogueItalicFormat}ungh{#PreviousFormat}, why is it so damn difficult to share one's feelings with somebody else? I don't want to ruin what we have. If only you could see what's in my heart." },
				{ Cue = "/VO/Dusa_0144", PreLineAnim = "NPCMedusaEmbarrassed",
					Emote = "PortraitEmoteNervous",
					PostLineFunctionArgs = { Animation = "NPCMedusaHiding", }, PostLineFunctionName = "DusaExit", StartSound = "/SFX/DusaRattle",
					Text = "Oh, {#DialogueItalicFormat}hahaha{#PreviousFormat}, I was just checking, don't feel bad, OK? You don't seem capable of hiding what's in your heart. And I mean that in the best possible way. I'm so happy, it's just... all this is a lot, and, I'm on duty right now, but... see you soon...?" },
			},
			-- below: old conversations from before the Welcome to Hell update
			DusaGift05 =
			{
				Skip = true,
				PlayOnce = true,
				RequiredTextLines = { "DusaGift04" },
				RequiredFalseTextLines = { "DusaGift05_Alt" },
